const { test, expect } = require('@playwright/test');

test.describe('Login Debug', () => {
  test('detailed login flow debugging', async ({ page }) => {
    // Enable console logging
    page.on('console', msg => console.log('BROWSER CONSOLE:', msg.text()));
    page.on('pageerror', error => console.log('PAGE ERROR:', error.message));

    // Navigate to the app
    console.log('1. Navigating to login page...');
    await page.goto('/');

    // Check initial state
    const initialUrl = page.url();
    console.log('2. Initial URL:', initialUrl);

    // Check if login form elements are present
    const usernameInput = page.locator('input[placeholder="admin"]');
    const passwordInput = page.locator('input[type="password"]');
    const loginButton = page.locator('button:has-text("Login"), button[type="submit"]');

    await expect(usernameInput).toBeVisible();
    await expect(passwordInput).toBeVisible();
    await expect(loginButton).toBeVisible();

    console.log('3. Login form elements are visible');

    // Fill credentials
    console.log('4. Filling credentials...');
    await usernameInput.fill('admin');
    await passwordInput.fill('admin123');

    // Check network requests
    const responsePromise = page.waitForResponse('**/api/auth/login');

    // Click login
    console.log('5. Clicking login button...');
    await loginButton.click();

    try {
      const response = await responsePromise;
      const status = response.status();
      const responseData = await response.json();
      console.log('6. Login API Response Status:', status);
      console.log('6. Login API Response Data:', JSON.stringify(responseData, null, 2));

      // Wait a bit for navigation
      await page.waitForTimeout(2000);

      const finalUrl = page.url();
      console.log('7. Final URL:', finalUrl);

      // Check localStorage
      const token = await page.evaluate(() => localStorage.getItem('token'));
      console.log('8. Token in localStorage:', token ? 'Present' : 'Not present');

      // Check if we're authenticated
      const isOnLoginPage = finalUrl.includes('/login');
      const isOnDashboard = finalUrl.includes('/dashboard');

      console.log('9. Status check:');
      console.log('   - On login page:', isOnLoginPage);
      console.log('   - On dashboard:', isOnDashboard);

      // Check for any error messages
      const errorAlert = page.locator('[role="alert"], .error, .MuiAlert-message');
      const hasError = await errorAlert.count() > 0;
      if (hasError) {
        const errorText = await errorAlert.first().textContent();
        console.log('10. Error message:', errorText);
      } else {
        console.log('10. No error messages found');
      }

    } catch (error) {
      console.log('6. Login API call failed:', error.message);

      // Check for network connectivity issues
      const finalUrl = page.url();
      console.log('7. Final URL after failed API:', finalUrl);

      // Check for error messages
      await page.waitForTimeout(1000);
      const errorAlert = page.locator('[role="alert"], .error, .MuiAlert-message');
      const hasError = await errorAlert.count() > 0;
      if (hasError) {
        const errorText = await errorAlert.first().textContent();
        console.log('8. Error message after failed API:', errorText);
      }
    }

    // Take a screenshot for debugging
    await page.screenshot({ path: 'test-results/login-debug.png', fullPage: true });
    console.log('Debug screenshot saved');
  });

  test('test server connectivity', async ({ page }) => {
    console.log('Testing server endpoints...');

    // Test if backend is accessible
    try {
      const healthResponse = await page.request.get('http://localhost:3005/health');
      console.log('Backend health check status:', healthResponse.status());
      if (healthResponse.ok()) {
        const healthData = await healthResponse.json();
        console.log('Backend health data:', healthData);
      }
    } catch (error) {
      console.log('Backend health check failed:', error.message);
    }

    // Test auth endpoint directly
    try {
      const loginResponse = await page.request.post('http://localhost:3005/api/auth/login', {
        data: {
          username: 'admin',
          password: 'admin123'
        }
      });
      console.log('Direct auth API status:', loginResponse.status());
      if (loginResponse.ok()) {
        const loginData = await loginResponse.json();
        console.log('Direct auth API response:', JSON.stringify(loginData, null, 2));
      } else {
        const errorData = await loginResponse.text();
        console.log('Direct auth API error:', errorData);
      }
    } catch (error) {
      console.log('Direct auth API failed:', error.message);
    }
  });
});

