const { app, BrowserWindow, Menu, ipcMain, dialog } = require('electron');
const path = require('path');
// Force development mode for now to ensure it loads from localhost:3000
const isDev = true; // process.env.NODE_ENV === 'development';

let mainWindow;

function createWindow() {
  // Create the browser window
  mainWindow = new BrowserWindow({
    width: 1400,
    height: 900,
    minWidth: 1200,
    minHeight: 800,
    webPreferences: {
      nodeIntegration: false,
      contextIsolation: true,
      enableRemoteModule: false,
      preload: path.join(__dirname, 'preload.js'),
      // Security best practices for 2024
      webSecurity: true,
      allowRunningInsecureContent: false,
      experimentalFeatures: false,
      // Additional security measures
      sandbox: false, // Required for preload script
      webviewTag: false,
      plugins: false
    },
    icon: path.join(__dirname, '../public/favicon.ico'),
    show: false,
    titleBarStyle: 'default',
    autoHideMenuBar: false
  });

  // Load the app
  const startUrl = isDev 
    ? 'http://localhost:3000' 
    : `file://${path.join(__dirname, '../build/index.html')}`;

  console.log('Loading URL:', startUrl);

  // Load the URL with error handling
  mainWindow.loadURL(startUrl).catch(err => {
    console.error('Failed to load URL:', err);
    // Try again after a delay
    setTimeout(() => {
      mainWindow.loadURL(startUrl).catch(err2 => {
        console.error('Failed to load URL again:', err2);
        mainWindow.loadURL('data:text/html,<h1>Loading CollisionOS...</h1><p>Please wait while the application starts.</p>');
      });
    }, 2000);
  });

  // Show window when ready to prevent visual flash
  mainWindow.once('ready-to-show', () => {
    console.log('Window ready to show');
    mainWindow.show();
    mainWindow.focus(); // Ensure window is focused

    // Open DevTools in development
    if (isDev) {
      mainWindow.webContents.openDevTools();
    }
  });

  // Handle page load errors
  mainWindow.webContents.on('did-fail-load', (event, errorCode, errorDescription, validatedURL) => {
    console.error('Page failed to load:', errorCode, errorDescription);
    if (isDev) {
      // In development, show a loading page and retry
      mainWindow.loadURL('data:text/html,<h1>Loading CollisionOS...</h1><p>Please wait while the React development server starts.</p><p>Error: ' + errorDescription + '</p>');
      setTimeout(() => {
        mainWindow.loadURL(startUrl);
      }, 3000);
    }
  });

  // Handle navigation and ensure clicks work
  mainWindow.webContents.on('dom-ready', () => {
    console.log('DOM is ready');
    mainWindow.webContents.executeJavaScript(`
      console.log('CollisionOS: DOM loaded, enabling interactions');

      // Force enable all click events
      document.addEventListener('click', function(e) {
        console.log('Click detected on:', e.target);
        console.log('Click target tag:', e.target.tagName);
        console.log('Click target class:', e.target.className);
        console.log('Click target id:', e.target.id);
      }, true);

      // Remove any blocking overlays
      const backdrops = document.querySelectorAll('.MuiBackdrop-root, .MuiModal-backdrop');
      backdrops.forEach(backdrop => {
        backdrop.style.display = 'none';
        backdrop.style.pointerEvents = 'none';
      });

      // Force all buttons to be clickable
      const buttons = document.querySelectorAll('button, [role="button"], a, .MuiButton-root, .MuiCard-root');
      buttons.forEach(button => {
        button.style.pointerEvents = 'auto';
        button.style.cursor = 'pointer';
        button.addEventListener('click', function(e) {
          console.log('Button clicked:', e.target);
        });
      });

      // Add click handlers to all cards
      const cards = document.querySelectorAll('.MuiCard-root');
      cards.forEach(card => {
        card.style.pointerEvents = 'auto';
        card.style.cursor = 'pointer';
        card.addEventListener('click', function(e) {
          console.log('Card clicked:', e.target);
        });
      });

      // Ensure all interactive elements are clickable
      const interactiveElements = document.querySelectorAll('[onclick], [data-testid], .clickable');
      interactiveElements.forEach(element => {
        element.style.pointerEvents = 'auto';
        element.style.cursor = 'pointer';
      });

      console.log('All interactions enabled');
    `);
  });

  // Handle window closed
  mainWindow.on('closed', () => {
    mainWindow = null;
  });

  // Handle window focus events
  mainWindow.on('focus', () => {
    console.log('Window focused');
  });

  mainWindow.on('blur', () => {
    console.log('Window blurred');
  });
}

// This method will be called when Electron has finished initialization
app.whenReady().then(() => {
  createWindow();

  app.on('activate', () => {
    if (BrowserWindow.getAllWindows().length === 0) {
      createWindow();
    }
  });
});

// Quit when all windows are closed
app.on('window-all-closed', () => {
  // Don't quit the app when window is closed - keep it running
  console.log('Window closed, but keeping app alive');
  // app.quit(); // Commented out to prevent immediate closure
});

// Handle IPC events
ipcMain.handle('select-file', async (event, options) => {
  const result = await dialog.showOpenDialog(mainWindow, options);
  return result;
});

ipcMain.handle('select-folder', async () => {
  const result = await dialog.showOpenDialog(mainWindow, {
    properties: ['openDirectory']
  });
  return result;
});

ipcMain.handle('save-file', async (event, options) => {
  const result = await dialog.showSaveDialog(mainWindow, options);
  return result;
});

ipcMain.handle('get-setting', async (event, key) => {
  // Implement settings retrieval
  return null;
});

ipcMain.handle('set-setting', async (event, key, value) => {
  // Implement settings storage
  return true;
});

ipcMain.handle('get-system-info', async () => {
  return {
    platform: process.platform,
    arch: process.arch,
    version: process.version,
    electronVersion: process.versions.electron
  };
});

ipcMain.handle('open-external', async (event, url) => {
  const { shell } = require('electron');
  await shell.openExternal(url);
});

// Create application menu
const template = [
  {
    label: 'File',
    submenu: [
      {
        label: 'New Job',
        accelerator: 'CmdOrCtrl+N',
        click: () => {
          mainWindow.webContents.send('menu-new-job');
        }
      },
      {
        label: 'New Customer',
        accelerator: 'CmdOrCtrl+Shift+N',
        click: () => {
          mainWindow.webContents.send('menu-new-customer');
        }
      },
      { type: 'separator' },
      {
        label: 'Import Estimates',
        click: () => {
          mainWindow.webContents.send('menu-import-estimates');
        }
      },
      {
        label: 'Import Customers',
        click: () => {
          mainWindow.webContents.send('menu-import-customers');
        }
      },
      {
        label: 'Import Parts',
        click: () => {
          mainWindow.webContents.send('menu-import-parts');
        }
      },
      { type: 'separator' },
      {
        label: 'Export Reports',
        click: () => {
          mainWindow.webContents.send('menu-export-reports');
        }
      },
      {
        label: 'Export Backup',
        click: () => {
          mainWindow.webContents.send('menu-export-backup');
        }
      },
      { type: 'separator' },
      {
        label: 'Exit',
        accelerator: process.platform === 'darwin' ? 'Cmd+Q' : 'Ctrl+Q',
        click: () => {
          app.quit();
        }
      }
    ]
  },
  {
    label: 'View',
    submenu: [
      {
        label: 'Production Board',
        accelerator: 'CmdOrCtrl+1',
        click: () => {
          mainWindow.webContents.send('menu-production-board');
        }
      },
      {
        label: 'Financial Dashboard',
        accelerator: 'CmdOrCtrl+2',
        click: () => {
          mainWindow.webContents.send('menu-financial-dashboard');
        }
      },
      {
        label: 'Customer Management',
        accelerator: 'CmdOrCtrl+3',
        click: () => {
          mainWindow.webContents.send('menu-customer-management');
        }
      },
      {
        label: 'Parts Management',
        accelerator: 'CmdOrCtrl+4',
