import { createTheme, alpha } from '@mui/material/styles';

// Modern color palette inspired by 2024 design trends
const colors = {
  primary: {
    50: '#e3f2fd',
    100: '#bbdefb',
    200: '#90caf9',
    300: '#64b5f6',
    400: '#42a5f5',
    500: '#2196f3', // Main blue
    600: '#1e88e5',
    700: '#1976d2',
    800: '#1565c0',
    900: '#0d47a1',
  },
  secondary: {
    50: '#fff3e0',
    100: '#ffe0b2',
    200: '#ffcc80',
    300: '#ffb74d',
    400: '#ffa726',
    500: '#ff9800', // Main orange
    600: '#fb8c00',
    700: '#f57c00',
    800: '#ef6c00',
    900: '#e65100',
  },
  success: {
    50: '#e8f5e8',
    100: '#c8e6c9',
    200: '#a5d6a7',
    300: '#81c784',
    400: '#66bb6a',
    500: '#4caf50', // Main green
    600: '#43a047',
    700: '#388e3c',
    800: '#2e7d32',
    900: '#1b5e20',
  },
  warning: {
    50: '#fff8e1',
    100: '#ffecb3',
    200: '#ffe082',
    300: '#ffd54f',
    400: '#ffca28',
    500: '#ffc107', // Main amber
    600: '#ffb300',
    700: '#ffa000',
    800: '#ff8f00',
    900: '#ff6f00',
  },
  error: {
    50: '#ffebee',
    100: '#ffcdd2',
    200: '#ef9a9a',
    300: '#e57373',
    400: '#ef5350',
    500: '#f44336', // Main red
    600: '#e53935',
    700: '#d32f2f',
    800: '#c62828',
    900: '#b71c1c',
  },
  neutral: {
    50: '#fafafa',
    100: '#f5f5f5',
    200: '#eeeeee',
    300: '#e0e0e0',
    400: '#bdbdbd',
    500: '#9e9e9e',
    600: '#757575',
    700: '#616161',
    800: '#424242',
    900: '#212121',
  }
};

// Glassmorphism utilities
const glassmorphism = {
  light: (alpha = 0.1) => ({
    background: `rgba(255, 255, 255, ${alpha})`,
    backdropFilter: 'blur(20px)',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    borderRadius: '16px',
  }),
  dark: (alpha = 0.1) => ({
    background: `rgba(16, 16, 16, ${alpha})`,
    backdropFilter: 'blur(20px)',
    border: '1px solid rgba(255, 255, 255, 0.1)',
    borderRadius: '16px',
  }),
};

// Modern shadows with subtle depth
const modernShadows = {
  light: {
    xs: '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
    sm: '0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06)',
    md: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)',
    lg: '0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)',
    xl: '0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)',
    glow: '0 0 20px rgba(33, 150, 243, 0.3)',
    glowHover: '0 0 30px rgba(33, 150, 243, 0.5)',
  },
  dark: {
    xs: '0 1px 2px 0 rgba(0, 0, 0, 0.3)',
    sm: '0 1px 3px 0 rgba(0, 0, 0, 0.4), 0 1px 2px 0 rgba(0, 0, 0, 0.3)',
    md: '0 4px 6px -1px rgba(0, 0, 0, 0.4), 0 2px 4px -1px rgba(0, 0, 0, 0.3)',
    lg: '0 10px 15px -3px rgba(0, 0, 0, 0.4), 0 4px 6px -2px rgba(0, 0, 0, 0.3)',
    xl: '0 20px 25px -5px rgba(0, 0, 0, 0.5), 0 10px 10px -5px rgba(0, 0, 0, 0.4)',
    glow: '0 0 20px rgba(33, 150, 243, 0.4)',
    glowHover: '0 0 30px rgba(33, 150, 243, 0.6)',
  }
};

// Create modern theme
export const createModernTheme = (mode = 'light') => {
  const isDark = mode === 'dark';

  return createTheme({
    palette: {
      mode,
      primary: {
        main: isDark ? colors.primary[400] : colors.primary[600],
        light: colors.primary[300],
        dark: colors.primary[700],
        contrastText: '#ffffff',
      },
      secondary: {
        main: isDark ? colors.secondary[400] : colors.secondary[600],
        light: colors.secondary[300],
        dark: colors.secondary[700],
        contrastText: '#ffffff',
      },
      success: {
        main: isDark ? colors.success[400] : colors.success[600],
        light: colors.success[300],
        dark: colors.success[700],
      },
      warning: {
        main: isDark ? colors.warning[400] : colors.warning[600],
        light: colors.warning[300],
        dark: colors.warning[700],
      },
      error: {
        main: isDark ? colors.error[400] : colors.error[600],
        light: colors.error[300],
        dark: colors.error[700],
      },
      background: {
        default: isDark ? '#0a0e13' : '#f8fafc',
        paper: isDark ? '#1a1f2e' : '#ffffff',
        glass: isDark ? 'rgba(26, 31, 46, 0.7)' : 'rgba(255, 255, 255, 0.7)',
        gradient: isDark 
          ? 'linear-gradient(135deg, rgba(26, 31, 46, 0.9) 0%, rgba(16, 20, 31, 0.9) 100%)'
          : 'linear-gradient(135deg, rgba(248, 250, 252, 0.9) 0%, rgba(241, 245, 249, 0.9) 100%)',
      },
      text: {
        primary: isDark ? '#f8fafc' : '#1e293b',
        secondary: isDark ? '#94a3b8' : '#64748b',
        disabled: isDark ? '#475569' : '#94a3b8',
      },
      divider: isDark ? 'rgba(255, 255, 255, 0.12)' : 'rgba(0, 0, 0, 0.12)',
      action: {
        hover: isDark ? 'rgba(255, 255, 255, 0.08)' : 'rgba(0, 0, 0, 0.04)',
        selected: isDark ? 'rgba(255, 255, 255, 0.16)' : 'rgba(0, 0, 0, 0.08)',
      },
    },

    typography: {
      fontFamily: [
        '"Inter"',
        '-apple-system',
        'BlinkMacSystemFont',
        '"Segoe UI"',
        'Roboto',
        '"Helvetica Neue"',
        'Arial',
        'sans-serif',
      ].join(','),

      h1: {
        fontSize: '3rem',
        fontWeight: 700,
        lineHeight: 1.2,
        letterSpacing: '-0.02em',
        background: isDark 
          ? 'linear-gradient(135deg, #60a5fa 0%, #34d399 100%)'
          : 'linear-gradient(135deg, #1e40af 0%, #059669 100%)',
        WebkitBackgroundClip: 'text',
        WebkitTextFillColor: 'transparent',
        backgroundClip: 'text',
      },

      h2: {
        fontSize: '2.25rem',
        fontWeight: 600,
        lineHeight: 1.3,
        letterSpacing: '-0.01em',
      },

      h3: {
        fontSize: '1.875rem',
        fontWeight: 600,
        lineHeight: 1.4,
      },

      h4: {
        fontSize: '1.5rem',
        fontWeight: 600,
        lineHeight: 1.4,
      },

      h5: {
        fontSize: '1.25rem',
        fontWeight: 600,
        lineHeight: 1.5,
      },

      h6: {
        fontSize: '1.125rem',
        fontWeight: 600,
        lineHeight: 1.5,
      },

      body1: {
        fontSize: '1rem',
        lineHeight: 1.6,
        fontWeight: 400,
      },

      body2: {
        fontSize: '0.875rem',
        lineHeight: 1.6,
        fontWeight: 400,
      },
    },

    shape: {
      borderRadius: 12,
    },

    components: {
      MuiCard: {
        styleOverrides: {
          root: {
            borderRadius: 16,
            border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.08)'}`,
            boxShadow: modernShadows[mode].md,
            background: isDark ? glassmorphism.dark(0.8).background : glassmorphism.light(0.9).background,
            backdropFilter: 'blur(20px)',
            transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
            position: 'relative',
            overflow: 'hidden',

            '&::before': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              height: '1px',
              background: isDark 
                ? 'linear-gradient(90deg, transparent 0%, rgba(255, 255, 255, 0.4) 50%, transparent 100%)'
                : 'linear-gradient(90deg, transparent 0%, rgba(0, 0, 0, 0.1) 50%, transparent 100%)',
            },

            '&:hover': {
              transform: 'translateY(-4px)',
              boxShadow: modernShadows[mode].lg,
              border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.2)' : 'rgba(0, 0, 0, 0.12)'}`,

              '&::before': {
                background: isDark 
                  ? 'linear-gradient(90deg, transparent 0%, rgba(96, 165, 250, 0.6) 50%, transparent 100%)'
                  : 'linear-gradient(90deg, transparent 0%, rgba(59, 130, 246, 0.3) 50%, transparent 100%)',
              }
            },
          },
        },
      },

      MuiPaper: {
        styleOverrides: {
          root: {
            borderRadius: 16,
            border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.08)'}`,
            background: isDark ? glassmorphism.dark(0.8).background : glassmorphism.light(0.9).background,
            backdropFilter: 'blur(20px)',
          },
          elevation1: {
            boxShadow: modernShadows[mode].sm,
          },
          elevation4: {
            boxShadow: modernShadows[mode].md,
          },
          elevation8: {
            boxShadow: modernShadows[mode].lg,
          },
        },
      },

      MuiButton: {
        styleOverrides: {
          root: {
            borderRadius: 12,
            textTransform: 'none',
            fontWeight: 600,
            fontSize: '0.875rem',
            padding: '10px 24px',
            position: 'relative',
            overflow: 'hidden',

            '&::before': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: '-100%',
              width: '100%',
              height: '100%',
              background: 'linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent)',
              transition: 'left 0.5s',
            },

            '&:hover::before': {
              left: '100%',
            },
          },

          contained: {
            boxShadow: modernShadows[mode].sm,
            background: isDark 
              ? 'linear-gradient(135deg, rgba(59, 130, 246, 0.8) 0%, rgba(147, 51, 234, 0.8) 100%)'
              : 'linear-gradient(135deg, #3b82f6 0%, #9333ea 100%)',

            '&:hover': {
              boxShadow: modernShadows[mode].glow,
              transform: 'translateY(-2px)',
            },
          },

          outlined: {
            border: `2px solid ${isDark ? 'rgba(255, 255, 255, 0.2)' : 'rgba(0, 0, 0, 0.12)'}`,
            backdropFilter: 'blur(10px)',

            '&:hover': {
              border: `2px solid ${isDark ? colors.primary[400] : colors.primary[600]}`,
              background: alpha(colors.primary[isDark ? 400 : 600], 0.08),
            },
          },
        },
      },

      MuiTextField: {
        styleOverrides: {
          root: {
            '& .MuiOutlinedInput-root': {
              borderRadius: 12,
              backgroundColor: isDark ? 'rgba(255, 255, 255, 0.05)' : 'rgba(255, 255, 255, 0.8)',
              backdropFilter: 'blur(10px)',

              '&:hover .MuiOutlinedInput-notchedOutline': {
                borderColor: isDark ? colors.primary[400] : colors.primary[600],
                borderWidth: '2px',
              },

              '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                borderColor: isDark ? colors.primary[400] : colors.primary[600],
                borderWidth: '2px',
                boxShadow: `0 0 0 4px ${alpha(colors.primary[isDark ? 400 : 600], 0.1)}`,
              },
            },
          },
        },
      },

      MuiAppBar: {
        styleOverrides: {
          root: {
            backgroundColor: 'transparent',
            background: isDark ? glassmorphism.dark(0.9).background : glassmorphism.light(0.9).background,
            backdropFilter: 'blur(20px)',
            border: 'none',
            borderBottom: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.08)'}`,
            boxShadow: 'none',
          },
        },
      },

      MuiDrawer: {
        styleOverrides: {
          paper: {
            backgroundColor: 'transparent',
            background: isDark ? glassmorphism.dark(0.95).background : glassmorphism.light(0.95).background,
            backdropFilter: 'blur(20px)',
            border: 'none',
            borderRight: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.08)'}`,
          },
        },
      },

      MuiListItemButton: {
        styleOverrides: {
          root: {
            borderRadius: 12,
            margin: '2px 8px',

            '&:hover': {
              backgroundColor: isDark ? 'rgba(255, 255, 255, 0.08)' : 'rgba(0, 0, 0, 0.04)',
              transform: 'translateX(4px)',
            },

            '&.Mui-selected': {
