        dark: theme === 'dark' ? '#4caf50' : '#1b5e20',
      },
      warning: {
        main: theme === 'dark' ? '#ffb74d' : '#f57c00', // Attention orange
        light: theme === 'dark' ? '#ffc947' : '#ff9800',
        dark: theme === 'dark' ? '#ff9800' : '#e65100',
      },
      error: {
        main: theme === 'dark' ? '#e57373' : '#c62828', // Alert red
        light: theme === 'dark' ? '#ef5350' : '#f44336',
        dark: theme === 'dark' ? '#f44336' : '#b71c1c',
      },
      info: {
        main: theme === 'dark' ? '#64b5f6' : '#1976d2', // Information blue
        light: theme === 'dark' ? '#90caf9' : '#2196f3',
        dark: theme === 'dark' ? '#2196f3' : '#0d47a1',
      },
      background: {
        default: theme === 'dark' ? '#0a0e13' : '#f8fafc',
        paper: theme === 'dark' ? '#1a202c' : '#ffffff',
        secondary: theme === 'dark' ? '#2d3748' : '#f1f5f9',
      },
      text: {
        primary: theme === 'dark' ? '#f7fafc' : '#1a202c',
        secondary: theme === 'dark' ? '#a0aec0' : '#4a5568',
        disabled: theme === 'dark' ? '#718096' : '#a0aec0',
      },
      divider: theme === 'dark' ? 'rgba(255, 255, 255, 0.12)' : 'rgba(0, 0, 0, 0.12)',
    },
    typography: {
      fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
      fontSize: 14,
      h1: {
        fontSize: '2.75rem',
        fontWeight: 700,
        lineHeight: 1.2,
        letterSpacing: '-0.02em',
      },
      h2: {
        fontSize: '2.25rem',
        fontWeight: 600,
        lineHeight: 1.3,
        letterSpacing: '-0.01em',
      },
      h3: {
        fontSize: '1.875rem',
        fontWeight: 600,
        lineHeight: 1.4,
        letterSpacing: '-0.01em',
      },
      h4: {
        fontSize: '1.5rem',
        fontWeight: 600,
        lineHeight: 1.4,
        letterSpacing: '0em',
      },
      h5: {
        fontSize: '1.25rem',
        fontWeight: 600,
        lineHeight: 1.5,
        letterSpacing: '0em',
      },
      h6: {
        fontSize: '1.125rem',
        fontWeight: 600,
        lineHeight: 1.5,
        letterSpacing: '0em',
      },
      subtitle1: {
        fontSize: '1rem',
        fontWeight: 500,
        lineHeight: 1.6,
      },
      subtitle2: {
        fontSize: '0.875rem',
        fontWeight: 500,
        lineHeight: 1.6,
      },
      body1: {
        fontSize: '1rem',
        fontWeight: 400,
        lineHeight: 1.6,
      },
      body2: {
        fontSize: '0.875rem',
        fontWeight: 400,
        lineHeight: 1.6,
      },
      caption: {
        fontSize: '0.75rem',
        fontWeight: 400,
        lineHeight: 1.5,
        letterSpacing: '0.01em',
      },
      overline: {
        fontSize: '0.75rem',
        fontWeight: 600,
        lineHeight: 1.5,
        letterSpacing: '0.08em',
        textTransform: 'uppercase',
      },
    },
    shape: {
      borderRadius: 12,
    },
    // Remove custom shadows to use Material-UI defaults and avoid array access errors
    components: {
      MuiCssBaseline: {
        styleOverrides: {
          body: {
            scrollbarWidth: 'thin',
            scrollbarColor: theme === 'dark' ? '#4a5568 #2d3748' : '#cbd5e0 #f7fafc',
            '&::-webkit-scrollbar': {
              width: 8,
            },
            '&::-webkit-scrollbar-track': {
              background: theme === 'dark' ? '#2d3748' : '#f7fafc',
            },
            '&::-webkit-scrollbar-thumb': {
              background: theme === 'dark' ? '#4a5568' : '#cbd5e0',
              borderRadius: 4,
              '&:hover': {
                background: theme === 'dark' ? '#718096' : '#a0aec0',
              },
            },
          },
        },
      },
      MuiButton: {
        styleOverrides: {
          root: {
            textTransform: 'none',
            fontWeight: 600,
            borderRadius: 8,
            padding: '10px 20px',
            fontSize: '0.875rem',
            boxShadow: 'none',
            '&:hover': {
              boxShadow: theme === 'dark' 
                ? '0 4px 12px rgba(0, 0, 0, 0.3)' 
                : '0 4px 12px rgba(0, 0, 0, 0.1)',
            },
          },
          contained: {
            fontWeight: 600,
            '&:hover': {
              boxShadow: theme === 'dark' 
                ? '0 6px 16px rgba(0, 0, 0, 0.4)' 
                : '0 6px 16px rgba(0, 0, 0, 0.15)',
            },
          },
          outlined: {
            borderWidth: 2,
            '&:hover': {
              borderWidth: 2,
            },
          },
        },
      },
      MuiCard: {
        styleOverrides: {
          root: {
            borderRadius: 12,
            border: `1px solid ${theme === 'dark' ? 'rgba(255, 255, 255, 0.08)' : 'rgba(0, 0, 0, 0.06)'}`,
            boxShadow: theme === 'dark' 
              ? '0 4px 12px rgba(0, 0, 0, 0.3)' 
              : '0 2px 8px rgba(0, 0, 0, 0.08)',
            transition: 'all 0.2s ease-in-out',
            '&:hover': {
              transform: 'translateY(-1px)',
              boxShadow: theme === 'dark' 
                ? '0 8px 24px rgba(0, 0, 0, 0.4)' 
                : '0 4px 16px rgba(0, 0, 0, 0.12)',
            },
          },
        },
      },
      MuiChip: {
        styleOverrides: {
          root: {
