import React, { useState } from 'react';
import { Button, Box, TextField, Typography, Alert, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { authService } from '../../services/authService';

export default function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    try {
      setError('');
      setLoading(true);

      if (!username || !password) {
        setError('Please enter both username and password');
        return;
      }

      const response = await authService.login(username, password);

      if (response.success && response.token && response.user) {
        // Store token
        localStorage.setItem('token', response.token);

        // Update auth context
        login(response.user, response.token);

        console.log('Login successful, redirecting to dashboard...');
        navigate('/dashboard');
      } else {
        setError('Invalid username or password');
      }
    } catch (err) {
      console.error('Login error:', err);
      if (err.response?.data?.error) {
        setError(err.response.data.error);
      } else if (err.message === 'Network Error') {
        setError('Cannot connect to server. Please check if the server is running.');
      } else {
        setError('Login failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

  return (
    <Box sx={{p:4, display:'flex', flexDirection:'column', gap:2, maxWidth:360, m:'80px auto'}}>
      <Typography variant="h4" sx={{ textAlign: 'center', mb: 2, color: 'primary.main' }}>
        ðŸš— CollisionOS
      </Typography>
      <Typography variant="h6" sx={{ textAlign: 'center', mb: 3 }}>
        Auto Body Shop Management
      </Typography>

      {error && <Alert severity="error">{error}</Alert>}

      <TextField 
        label="Username" 
        size="small" 
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="admin"
      />
      <TextField 
        label="Password" 
        type="password" 
        size="small"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="admin123"
      />
      <Button 
        variant="contained" 
        onClick={handleLogin}
        disabled={loading}
        size="large"
        sx={{ mt: 2 }}
        startIcon={loading ? <CircularProgress size={20} /> : null}
      >
        {loading ? 'Signing In...' : 'Login'}
      </Button>

      <Box sx={{ mt: 2, p: 2, bgcolor: 'grey.100', borderRadius: 1 }}>
        <Typography variant="caption" display="block">
          <strong>Demo Accounts:</strong>
        </Typography>
        <Typography variant="caption" display="block">
          admin / admin123 (Owner)
        </Typography>
        <Typography variant="caption" display="block">
          manager / manager123 (Manager)
        </Typography>
        <Typography variant="caption" display="block">
          estimator / estimator123 (Estimator)
        </Typography>
      </Box>
    </Box>
  );
}

