toolUseResult.originalFile:
import { createTheme } from '@mui/material/styles';

// Theme module extensions for custom properties
const themeExtensions = (theme) => ({
  ...theme,
  custom: {
    gradients: {
      primary: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      background: 'linear-gradient(135deg, #0F172A 0%, #1E293B 100%)'
    },
    glass: {
      surface: 'rgba(255,255,255,0.08)',
      border: 'rgba(255,255,255,0.18)',
      shadow: '0 8px 32px rgba(0,0,0,0.35)'
    }
  }
});

// Glassmorphism utilities
const glassmorphism = {
  light: (alpha = 0.1) => ({
    background: `rgba(255, 255, 255, ${alpha})`,
    backdropFilter: 'blur(20px)',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    borderRadius: '16px',
  }),
  dark: (alpha = 0.1) => ({
    background: `rgba(16, 16, 16, ${alpha})`,
    backdropFilter: 'blur(20px)',
    border: '1px solid rgba(255, 255, 255, 0.1)',
    borderRadius: '16px',
  }),
};

// Modern shadows with subtle depth
const modernShadows = {
  light: {
    xs: '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
    sm: '0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06)',
    md: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)',
    lg: '0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)',
    xl: '0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)',
    glow: '0 0 20px rgba(33, 150, 243, 0.3)',
    glowHover: '0 0 30px rgba(33, 150, 243, 0.5)',
  },
  dark: {
    xs: '0 1px 2px 0 rgba(0, 0, 0, 0.3)',
    sm: '0 1px 3px 0 rgba(0, 0, 0, 0.4), 0 1px 2px 0 rgba(0, 0, 0, 0.3)',
    md: '0 4px 6px -1px rgba(0, 0, 0, 0.4), 0 2px 4px -1px rgba(0, 0, 0, 0.3)',
    lg: '0 10px 15px -3px rgba(0, 0, 0, 0.4), 0 4px 6px -2px rgba(0, 0, 0, 0.3)',
    xl: '0 20px 25px -5px rgba(0, 0, 0, 0.5), 0 10px 10px -5px rgba(0, 0, 0, 0.4)',
    glow: '0 0 20px rgba(33, 150, 243, 0.4)',
    glowHover: '0 0 30px rgba(33, 150, 243, 0.6)',
  }
};

export const modernTheme = themeExtensions(createTheme({
  palette: {
    mode: 'dark',
    background: { default: '#0F172A', paper: 'rgba(30,41,59,0.50)' },
    primary: { main: '#6366F1' }, // Indigo
    secondary: { main: '#8B5CF6' }, // Purple
    success: { main: '#10B981' },
    error: { main: '#EF4444' },
    warning: { main: '#F59E0B' },
    info: { main: '#0EA5E9' }
  },
  typography: {
    fontFamily: [
      'Inter',
      'SF Pro Text',
      'system-ui',
      '-apple-system',
      'Segoe UI',
      'Roboto',
      'Helvetica Neue',
      'Arial',
      'Noto Sans',
      'sans-serif',
    ].join(','),
    h1: { fontWeight: 800, letterSpacing: '-0.02em' },
    h2: { fontWeight: 700, letterSpacing: '-0.02em' },
    h3: { fontWeight: 700 },
    subtitle1: { opacity: 0.9 }
  },
  shape: { borderRadius: 20 },
  shadows: [
    'none',
    '0 8px 24px rgba(0,0,0,0.12)',
    '0 12px 32px rgba(0,0,0,0.16)',
    '0 16px 48px rgba(0,0,0,0.18)',
    ...Array(21).fill('0 16px 48px rgba(0,0,0,0.18)')
  ],

    components: {
      MuiCard: {
        styleOverrides: {
          root: {
            borderRadius: 16,
            border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.08)'}`,
            boxShadow: modernShadows[mode].md,
            background: isDark ? glassmorphism.dark(0.8).background : glassmorphism.light(0.9).background,
            backdropFilter: 'blur(20px)',
            transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
            position: 'relative',
            overflow: 'hidden',

            '&::before': {
