import React from 'react';
import { Box, useTheme } from '@mui/material';
import { keyframes } from '@emotion/react';

// Animated gradient keyframes
const gradientShift = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`;

const floatingElements = keyframes`
  0% {
    transform: translateY(0px) rotate(0deg);
    opacity: 0.7;
  }
  50% {
    transform: translateY(-20px) rotate(180deg);
    opacity: 1;
  }
  100% {
    transform: translateY(0px) rotate(360deg);
    opacity: 0.7;
  }
`;

const pulseGlow = keyframes`
  0% {
    opacity: 0.4;
    transform: scale(1);
  }
  50% {
    opacity: 0.8;
    transform: scale(1.1);
  }
  100% {
    opacity: 0.4;
    transform: scale(1);
  }
`;

const ModernBackground = ({ children, variant = 'default' }) => {
  const theme = useTheme();
  const isDark = theme.palette.mode === 'dark';

  // Dynamic background variants
  const backgroundVariants = {
    default: {
      background: isDark
        ? `
          linear-gradient(-45deg, 
            rgba(10, 14, 19, 0.95), 
            rgba(26, 31, 46, 0.95), 
            rgba(16, 20, 31, 0.95), 
            rgba(30, 41, 59, 0.95)
          )
        `
        : `
          linear-gradient(-45deg, 
            rgba(248, 250, 252, 0.95), 
            rgba(241, 245, 249, 0.95), 
            rgba(226, 232, 240, 0.95), 
            rgba(203, 213, 225, 0.95)
          )
        `,
      backgroundSize: '400% 400%',
      animation: `${gradientShift} 15s ease infinite`,
    },

    dashboard: {
      background: isDark
        ? `
          radial-gradient(ellipse at top left, rgba(59, 130, 246, 0.1) 0%, transparent 50%),
          radial-gradient(ellipse at top right, rgba(147, 51, 234, 0.1) 0%, transparent 50%),
          radial-gradient(ellipse at bottom left, rgba(16, 185, 129, 0.1) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(10, 14, 19, 0.98) 0%, 
            rgba(26, 31, 46, 0.95) 50%, 
            rgba(16, 20, 31, 0.98) 100%
          )
        `
        : `
          radial-gradient(ellipse at top left, rgba(59, 130, 246, 0.08) 0%, transparent 50%),
          radial-gradient(ellipse at top right, rgba(147, 51, 234, 0.08) 0%, transparent 50%),
          radial-gradient(ellipse at bottom left, rgba(16, 185, 129, 0.08) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(248, 250, 252, 0.98) 0%, 
            rgba(241, 245, 249, 0.95) 50%, 
            rgba(226, 232, 240, 0.98) 100%
          )
        `,
    },

    auth: {
      background: isDark
        ? `
          radial-gradient(circle at 20% 80%, rgba(30, 64, 175, 0.25) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(16, 185, 129, 0.25) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(15, 23, 42, 0.95) 0%, 
            rgba(30, 41, 59, 0.9) 100%
          )
        `
        : `
          radial-gradient(circle at 20% 80%, rgba(30, 64, 175, 0.12) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(16, 185, 129, 0.12) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(248, 250, 252, 0.95) 0%, 
            rgba(226, 232, 240, 0.9) 100%
          )
        `,
      backgroundSize: '400% 400%',
      animation: `${gradientShift} 20s ease infinite`,
    }
  };

  return (
    <Box
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        // Modern viewport units for better mobile browser support
        height: 'max(100vh, 100svh)',
        minHeight: '100svh',
        // Proper z-index layering (MUI v6 standard)
        zIndex: -10,
        overflow: 'hidden',
        // Strict pointer events none for background only - force override
        pointerEvents: 'none !important',
        userSelect: 'none',
        // CSS containment for performance
        contain: 'layout style paint',
        ...backgroundVariants[variant],

        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundImage: isDark
            ? `
              radial-gradient(circle at 25% 25%, rgba(59, 130, 246, 0.1) 0%, transparent 25%),
              radial-gradient(circle at 75% 25%, rgba(147, 51, 234, 0.1) 0%, transparent 25%),
              radial-gradient(circle at 25% 75%, rgba(16, 185, 129, 0.1) 0%, transparent 25%),
              radial-gradient(circle at 75% 75%, rgba(245, 101, 101, 0.1) 0%, transparent 25%)
            `
            : `
              radial-gradient(circle at 25% 25%, rgba(59, 130, 246, 0.05) 0%, transparent 25%),
              radial-gradient(circle at 75% 25%, rgba(147, 51, 234, 0.05) 0%, transparent 25%),
              radial-gradient(circle at 25% 75%, rgba(16, 185, 129, 0.05) 0%, transparent 25%),
              radial-gradient(circle at 75% 75%, rgba(245, 101, 101, 0.05) 0%, transparent 25%)
            `,
          animation: `${pulseGlow} 8s ease-in-out infinite`,
          pointerEvents: 'none',
          zIndex: -1
        },

        '&::after': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundImage: `
            url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='${
              isDark ? '%234f46e5' : '%236366f1'
            }' fill-opacity='${isDark ? '0.05' : '0.03'}'%3E%3Ccircle cx='30' cy='30' r='1'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")
          `,
          opacity: isDark ? 0.3 : 0.2,
          pointerEvents: 'none',
          zIndex: -1
        }
      }}
    >
      {/* Floating geometric elements - responsive positioning */}
      <Box
        sx={{
          position: 'absolute',
          top: { xs: '5%', sm: '10%' },
          left: { xs: '5%', sm: '10%' },
          width: { xs: 60, sm: 80, md: 100 },
          height: { xs: 60, sm: 80, md: 100 },
          borderRadius: '20px',
          background: isDark 
            ? 'linear-gradient(135deg, rgba(30, 64, 175, 0.1) 0%, rgba(16, 185, 129, 0.1) 100%)'
            : 'linear-gradient(135deg, rgba(30, 64, 175, 0.05) 0%, rgba(16, 185, 129, 0.05) 100%)',
          backdropFilter: 'blur(10px)',
          border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.08)' : 'rgba(0, 0, 0, 0.03)'}`,
          animation: `${floatingElements} 20s ease-in-out infinite`,
          pointerEvents: 'none',
          zIndex: -1,
          // Hide on mobile and tablet screens
          display: { xs: 'none', md: 'block' },
        }}
      />

      <Box
        sx={{
          position: 'absolute',
          top: { xs: '75%', sm: '60%' },
          right: { xs: '8%', sm: '15%' },
          width: { xs: 50, sm: 65, md: 80 },
          height: { xs: 50, sm: 65, md: 80 },
          borderRadius: '50%',
          background: isDark 
            ? 'linear-gradient(135deg, rgba(16, 185, 129, 0.08) 0%, rgba(5, 150, 105, 0.08) 100%)'
            : 'linear-gradient(135deg, rgba(16, 185, 129, 0.04) 0%, rgba(5, 150, 105, 0.04) 100%)',
          backdropFilter: 'blur(10px)',
          border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.08)' : 'rgba(0, 0, 0, 0.03)'}`,
          animation: `${floatingElements} 25s ease-in-out infinite reverse`,
          pointerEvents: 'none',
          zIndex: -1,
          // Hide on mobile and tablet
          display: { xs: 'none', md: 'block' },
        }}
      />

      <Box
        sx={{
          position: 'absolute',
          bottom: { xs: '10%', sm: '20%' },
          left: { xs: '15%', sm: '20%' },
          width: { xs: 40, sm: 50, md: 60 },
          height: { xs: 40, sm: 50, md: 60 },
          borderRadius: '15px',
          background: isDark 
            ? 'linear-gradient(135deg, rgba(245, 101, 101, 0.08) 0%, rgba(239, 68, 68, 0.08) 100%)'
            : 'linear-gradient(135deg, rgba(245, 101, 101, 0.04) 0%, rgba(239, 68, 68, 0.04) 100%)',
          backdropFilter: 'blur(10px)',
          border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.08)' : 'rgba(0, 0, 0, 0.03)'}`,
          animation: `${floatingElements} 18s ease-in-out infinite`,
          animationDelay: '5s',
          pointerEvents: 'none',
          zIndex: -1,
          // Hide on mobile and tablet
          display: { xs: 'none', md: 'block' },
        }}
      />

      {children}
    </Box>
  );
};

export default ModernBackground;

