import React from 'react';
import { Card, CardContent, Box, useTheme } from '@mui/material';
import { motion } from 'framer-motion';

const MotionCard = motion(Card);

const GlassCard = ({ 
  children, 
  className = '', 
  elevation = 0,
  variant = 'default',
  onClick,
  sx = {},
  ...props 
}) => {
  const theme = useTheme();
  const isDark = theme.palette.mode === 'dark';

  const glassStyles = {
    position: 'relative',
    background: isDark 
      ? 'rgba(30, 41, 59, 0.6)'
      : 'rgba(255, 255, 255, 0.8)',
    backdropFilter: 'saturate(180%) blur(20px)',
    WebkitBackdropFilter: 'saturate(180%) blur(20px)',
    border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.15)' : 'rgba(255, 255, 255, 0.25)'}`,
    borderRadius: theme.shape.borderRadius * 2,
    overflow: 'hidden',
    cursor: onClick ? 'pointer' : 'default',
    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',

    '&::before': {
      content: '""',
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      height: '1px',
      background: isDark 
        ? 'linear-gradient(90deg, transparent 0%, rgba(255, 255, 255, 0.4) 50%, transparent 100%)'
        : 'linear-gradient(90deg, transparent 0%, rgba(255, 255, 255, 0.8) 50%, transparent 100%)',
    },

    '&:hover': onClick ? {
      transform: 'translateY(-2px)',
      border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.25)' : 'rgba(255, 255, 255, 0.35)'}`,
      boxShadow: isDark 
        ? '0 20px 40px rgba(0, 0, 0, 0.3), 0 0 40px rgba(99, 102, 241, 0.2)'
        : '0 20px 40px rgba(0, 0, 0, 0.1), 0 0 40px rgba(99, 102, 241, 0.1)',
    } : {},
  };

  const motionProps = {
    initial: { opacity: 0, y: 20 },
    animate: { opacity: 1, y: 0 },
    whileHover: onClick ? { 
      scale: 1.02,
      transition: { type: 'spring', stiffness: 400, damping: 30 }
    } : {},
    transition: { 
      duration: 0.3,
      ease: [0.4, 0, 0.2, 1]
    },
  };

  return (
    <MotionCard
      {...motionProps}
      elevation={elevation}
      className={`glass-card ${className}`}
      onClick={onClick}
      sx={{
        ...glassStyles,
        ...sx,
      }}
      {...props}
    >
      {children}
    </MotionCard>
  );
};

export default GlassCard;

