import React from 'react';
import { Box, styled } from '@mui/material';
import { motion } from 'framer-motion';

const BentoGridContainer = styled(motion.div)(({ theme }) => ({
  display: 'grid',
  gap: theme.spacing(3),
  width: '100%',

  // Default mobile layout (stacked)
  gridTemplateColumns: '1fr',
  gridTemplateRows: 'auto',

  // Tablet layout
  [theme.breakpoints.up('md')]: {
    gridTemplateColumns: 'repeat(4, 1fr)',
    gridTemplateRows: 'repeat(6, minmax(120px, auto))',
    gridTemplateAreas: `
      "kpi1 kpi2 kpi3 kpi4"
      "production production production sidebar1"
      "production production production sidebar2"
      "recent recent analytics analytics"
      "recent recent analytics analytics"
      "advanced advanced advanced advanced"
    `,
  },

  // Desktop layout
  [theme.breakpoints.up('lg')]: {
    gridTemplateColumns: 'repeat(6, 1fr)',
    gridTemplateRows: 'repeat(5, minmax(140px, auto))',
    gridTemplateAreas: `
      "kpi1 kpi2 kpi3 kpi4 sidebar1 sidebar2"
      "production production production production sidebar3 sidebar4"
      "production production production production weather calendar"
      "recent recent analytics analytics ai-insights ai-insights"
      "advanced advanced advanced advanced advanced advanced"
    `,
  },
}));

const BentoGridItem = styled(motion.div)(({ theme, area, variant = 'default' }) => {
  const variants = {
    default: {
      borderRadius: theme.spacing(2),
    },
    kpi: {
      borderRadius: theme.spacing(2),
      minHeight: '120px',
    },
    large: {
      borderRadius: theme.spacing(2),
      minHeight: '240px',
    },
    tall: {
      borderRadius: theme.spacing(2),
      minHeight: '320px',
    },
    wide: {
      borderRadius: theme.spacing(2),
      minHeight: '160px',
    },
    compact: {
      borderRadius: theme.spacing(2),
      minHeight: '100px',
    },
  };

  return {
    gridArea: area,
    display: 'flex',
    flexDirection: 'column',
    position: 'relative',
    overflow: 'hidden',
    ...variants[variant],

    // Mobile responsive
    [theme.breakpoints.down('md')]: {
      gridArea: 'unset',
      minHeight: variants[variant === 'large' || variant === 'tall' ? 'default' : variant].minHeight,
    },
  };
});

const BentoGrid = ({ 
  children, 
  spacing = 3,
  animate = true,
  staggerDelay = 0.1,
  ...props 
}) => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: animate ? staggerDelay : 0,
        delayChildren: 0.2,
      },
    },
  };

  return (
    <BentoGridContainer
      initial={animate ? "hidden" : false}
      animate={animate ? "visible" : false}
      variants={animate ? containerVariants : undefined}
      sx={{ gap: spacing }}
      {...props}
    >
      {children}
    </BentoGridContainer>
  );
};

const BentoItem = ({ 
  area, 
  variant = 'default', 
  children, 
  delay = 0,
  animate = true,
  ...props 
}) => {
  const itemVariants = {
    hidden: { 
      opacity: 0, 
      y: 20, 
      scale: 0.95 
    },
    visible: { 
      opacity: 1, 
      y: 0, 
      scale: 1,
      transition: {
        type: 'spring',
        stiffness: 100,
        damping: 15,
        delay,
      },
    },
  };

  return (
    <BentoGridItem
      area={area}
      variant={variant}
      initial={animate ? "hidden" : false}
      animate={animate ? "visible" : false}
      variants={animate ? itemVariants : undefined}
      {...props}
    >
      {children}
    </BentoGridItem>
  );
};

// Predefined layouts
export const BentoLayouts = {
  dashboard: {
    areas: {
      kpi1: 'kpi1',
      kpi2: 'kpi2', 
      kpi3: 'kpi3',
      kpi4: 'kpi4',
      production: 'production',
      recent: 'recent',
      analytics: 'analytics',
      sidebar1: 'sidebar1',
      sidebar2: 'sidebar2',
      sidebar3: 'sidebar3',
      sidebar4: 'sidebar4',
      weather: 'weather',
      calendar: 'calendar',
      aiInsights: 'ai-insights',
      advanced: 'advanced',
    },
    responsive: {
      mobile: 'single-column',
      tablet: '4-column',
      desktop: '6-column',
    },
  },
};

export { BentoGrid, BentoItem };
export default BentoGrid;

