import React, { ReactNode } from 'react';
import { Box } from '@mui/material';
import { motion } from 'framer-motion';

export const BentoGrid = ({ children, cols = 12, gap = 2 }) => (
  <Box component={motion.div}
       initial={{ opacity: 0 }}
       animate={{ opacity: 1 }}
       transition={{ duration: 0.3 }}
       sx={{
         display: 'grid',
         gridTemplateColumns: `repeat(${cols}, minmax(0, 1fr))`,
         gap
       }}>
    {children}
  </Box>
);

export const BentoItem = ({ children, span }) => (
  <Box
    component={motion.div}
    variants={{ hidden: { y: 20, opacity: 0 }, visible: { y: 0, opacity: 1, transition: { type: 'spring', stiffness: 120, damping: 16 } } }}
    initial="hidden"
    animate="visible"
    sx={{
      gridColumn: {
        xs: `span ${span?.xs ?? 12}`,
        sm: `span ${span?.sm ?? span?.xs ?? 12}`,
        md: `span ${span?.md ?? span?.sm ?? 6}`,
        lg: `span ${span?.lg ?? span?.md ?? 4}`,
        xl: `span ${span?.xl ?? span?.lg ?? 3}`,
      }
    }}
  >
    {children}
  </Box>
);

export default BentoGrid;

