import { BentoGrid, BentoItem } from '../../components/Layout/BentoGrid';

// Hooks
import { useAuth } from '../../contexts/AuthContext';
import { useTheme as useAppTheme } from '../../contexts/ThemeContext';

// Utils
import { getGlassStyles, glassHoverEffects, coloredGlassVariants } from '../../utils/glassTheme';
import { microAnimations, containerAnimations } from '../../utils/animations';

const Dashboard = () => {
  const theme = useTheme();
  const { mode } = useAppTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const isTablet = useMediaQuery(theme.breakpoints.down('lg'));
  const { user } = useAuth();

  const [selectedTimeframe, setSelectedTimeframe] = useState('today');
  const [isLoading, setIsLoading] = useState(false);
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);
  }, []);

  // Mock data
  const dashboardData = {
    kpis: { 
      revenue: 123456, 
      jobsInProgress: 12, 
      avgCycleTime: 6.2, 
      customerSatisfaction: 95 
    },
    productionStats: { 
      total: 24, 
      estimate: 3, 
      inProgress: 12, 
      qualityCheck: 2, 
      readyPickup: 1 
    },
    efficiencyStats: { 
      laborEfficiency: 88, 
      partsEfficiency: 92, 
      bayUtilization: 73 
    }
  };

  const recentJobs = [
    { id: 1, jobNumber: 'JOB-001', customerName: 'John Smith', status: 'estimate', priority: 'high' },
    { id: 2, jobNumber: 'JOB-002', customerName: 'Jane Doe', status: 'in_progress', priority: 'medium' },
    { id: 3, jobNumber: 'JOB-003', customerName: 'Bob Johnson', status: 'ready_pickup', priority: 'low' }
  ];

  const alerts = [
    { id: 1, title: 'Parts Overdue', message: '2 parts overdue', type: 'warning' },
    { id: 2, title: 'Job Overdue', message: 'One job overdue', type: 'error' }
  ];

  const handleRefresh = () => {
    setIsLoading(true);
    setTimeout(() => setIsLoading(false), 1000);
  };

  const handleTimeframeChange = (timeframe) => {
    setSelectedTimeframe(timeframe);
  };

  const handleViewFullBoard = () => {
    // Navigation logic here
  };

  const handleKPIWidgetClick = (title) => {
    // KPI widget click logic
  };

  const handleStatusWidgetClick = (title) => {
    // Status widget click logic
  };

  // Enhanced KPI Card Component with glassmorphism
  const ModernKPICard = ({ title, value, change, icon, color, onClick }) => (
    <GlassCard
      variant="elevated"
      depth={2}
      initial={{ opacity: 0, y: 20, scale: 0.95 }}
      animate={{ opacity: 1, y: 0, scale: 1 }}
      transition={{ duration: 0.6, ease: [0.4, 0, 0.2, 1] }}
      sx={{ 
        height: '100%',
        cursor: 'pointer',
        position: 'relative',
        overflow: 'hidden',
        ...getGlassStyles('elevated', mode),
        ...glassHoverEffects(mode, 2),

        // Color-specific glass tinting
        background: color ? `linear-gradient(135deg, ${color}08 0%, ${color}03 100%), ${getGlassStyles('elevated', mode).background}` : undefined,
        borderColor: color ? `${color}30` : undefined,

        '&::after': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: `radial-gradient(circle at 20% 20%, ${color}15 0%, transparent 50%)`,
          pointerEvents: 'none',
        },

        '&:hover': {
          ...glassHoverEffects(mode, 2)['&:hover'],
          borderColor: color,
          '&::after': {
            background: `radial-gradient(circle at 20% 20%, ${color}20 0%, transparent 60%)`,
          }
        }
      }}
      onClick={onClick}
    >
      <CardContent sx={{ p: 3, position: 'relative', zIndex: 1 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
          <Avatar 
            sx={{ 
              bgcolor: color,
              width: 52,
              height: 52,
              boxShadow: `0 8px 24px ${color}40`,
              backdropFilter: 'blur(10px)',
              border: `1px solid ${color}30`
            }}
          >
            {icon}
          </Avatar>
          <IconButton 
            size="small" 
            sx={{ 
              color: 'text.secondary',
              backdropFilter: 'blur(10px)',
              '&:hover': { 
                bgcolor: 'rgba(255, 255, 255, 0.1)',
                color: color
              }
            }}
          >
            <MoreVert />
          </IconButton>
        </Box>

        <Typography variant="h4" component="div" sx={{ fontWeight: 700, mb: 1 }}>
          {value}
        </Typography>

        <Typography variant="body2" color="text.secondary" sx={{ mb: 2, fontWeight: 500 }}>
          {title}
        </Typography>

        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          {change > 0 ? (
            <TrendingUp sx={{ color: 'success.main', fontSize: 18 }} />
          ) : (
            <TrendingDown sx={{ color: 'error.main', fontSize: 18 }} />
          )}
          <Typography 
            variant="caption" 
            sx={{ 
              color: change > 0 ? 'success.main' : 'error.main',
              fontWeight: 600,
              fontSize: '0.8rem'
            }}
          >
            {Math.abs(change)}% from last period
          </Typography>
        </Box>
      </CardContent>
    </GlassCard>
  );

  // Status Widget Component
  const StatusWidget = ({ title, count, total, icon, color, onClick }) => (
    <Slide direction="up" in={mounted} timeout={600}>
      <Card 
        sx={{ 
          height: '100%',
          background: `linear-gradient(135deg, ${color}10 0%, ${color}05 100%)`,
          border: `1px solid ${color}20`,
          borderRadius: 3,
          cursor: 'pointer',
          transition: 'all 0.3s ease',
          '&:hover': {
            transform: 'translateY(-2px)',
            boxShadow: `0 6px 20px ${color}20`,
          }
        }}
        onClick={onClick}
      >
        <CardContent sx={{ p: 2.5 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
            <Avatar 
              sx={{ 
                bgcolor: color,
                width: 40,
                height: 40,
                boxShadow: `0 3px 8px ${color}30`
              }}
            >
              {icon}
            </Avatar>
            <Box sx={{ flex: 1 }}>
              <Typography variant="h6" component="div" sx={{ fontWeight: 600 }}>
                {count}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {title}
              </Typography>
            </Box>
          </Box>

          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <LinearProgress 
              variant="determinate" 
              value={(count / total) * 100} 
              sx={{ 
                flex: 1,
                height: 6,
                borderRadius: 3,
                bgcolor: `${color}20`,
                '& .MuiLinearProgress-bar': {
                  bgcolor: color,
                  borderRadius: 3,
                }
              }} 
            />
            <Typography variant="caption" color="text.secondary">
              {Math.round((count / total) * 100)}%
            </Typography>
          </Box>
        </CardContent>
      </Card>
    </Slide>
  );

  return (
    <Fade in={mounted} timeout={500}>
      <Box sx={{ 
        p: { xs: 2, md: 3 }, 
        pb: 10,
        minHeight: '100vh',
        background: 'transparent',
        position: 'relative',
        zIndex: 1
      }}>
        {/* Header Section */}
        <GlassCard
          variant="frosted"
          depth={1}
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, ease: [0.4, 0, 0.2, 1] }}
          sx={{ 
            p: { xs: 2, md: 3 },
            mb: 3,
          }}
        >
            <Box sx={{ 
              display: 'flex', 
              flexDirection: { xs: 'column', md: 'row' },
              justifyContent: 'space-between', 
              alignItems: { xs: 'flex-start', md: 'center' }, 
              gap: 2
            }}>
              <Box>
                <Typography 
                  variant="h4" 
                  component="h1" 
                  sx={{ 
                    fontWeight: 700,
                    color: mode === 'dark' ? '#ffffff' : '#1e293b',
                    textShadow: mode === 'dark' ? '0 2px 4px rgba(0,0,0,0.3)' : '0 1px 2px rgba(255,255,255,0.8)',
                    mb: 1,
                    fontSize: { xs: '1.75rem', sm: '2.125rem', md: '2.5rem' }
                  }}
                >
                  Dashboard
                </Typography>
                <Typography 
                  variant="body1" 
                  sx={{
                    color: mode === 'dark' ? 'rgba(255, 255, 255, 0.9)' : 'rgba(0, 0, 0, 0.8)',
                    fontWeight: 500,
                    fontSize: { xs: '1rem', sm: '1.1rem' },
                    textShadow: mode === 'dark' ? '0 1px 2px rgba(0,0,0,0.2)' : '0 1px 2px rgba(255,255,255,0.5)'
                  }}
                >
                  Welcome back, {user?.firstName || 'User'}! Here's what's happening today.
                </Typography>
              </Box>

              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <ThemeToggle />

                <Tooltip title="Refresh">
                  <IconButton 
                    onClick={handleRefresh} 
                    disabled={isLoading}
                    sx={{
                      bgcolor: 'rgba(30, 64, 175, 0.1)',
                      backdropFilter: 'blur(10px)',
                      border: '1px solid rgba(30, 64, 175, 0.2)',
                      '&:hover': { 
                        bgcolor: 'rgba(30, 64, 175, 0.2)',
                        transform: 'scale(1.05)'
                      },
                      transition: 'all 0.2s ease-in-out'
                    }}
                  >
                    <Refresh sx={{ 
                      animation: isLoading ? 'spin 1s linear infinite' : 'none',
                      '@keyframes spin': {
                        '0%': { transform: 'rotate(0deg)' },
                        '100%': { transform: 'rotate(360deg)' }
                      }
                    }} />
                  </IconButton>
                </Tooltip>

                <Chip
                  icon={<CheckCircle />}
                  label="Connected"
                  color="success"
                  size="small"
                  sx={{ 
                    cursor: 'pointer',
                    bgcolor: 'rgba(16, 185, 129, 0.1)',
                    border: '1px solid rgba(16, 185, 129, 0.3)',
                    backdropFilter: 'blur(10px)',
                    '&:hover': {
                      bgcolor: 'rgba(16, 185, 129, 0.15)',
                      transform: 'scale(1.02)'
                    },
                    transition: 'all 0.2s ease-in-out'
                  }}
                />
              </Box>
            </Box>
        </GlassCard>

        {/* Timeframe Selector */}
        <GlassCard
          variant="subtle"
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          sx={{ 
            p: 2,
            mb: 3,
          }}
        >
            <Stack direction="row" spacing={1} sx={{ flexWrap: 'wrap', gap: 1 }}>
              {['today', 'week', 'month', 'quarter'].map((timeframe) => (
                <Button
                  key={timeframe}
                  variant={selectedTimeframe === timeframe ? 'contained' : 'outlined'}
                  size="small"
                  onClick={() => handleTimeframeChange(timeframe)}
                  sx={{
                    borderRadius: 2,
                    textTransform: 'capitalize',
                    fontWeight: 600,
                    ...(selectedTimeframe === timeframe && {
                      background: 'linear-gradient(135deg, #1e40af 0%, #10b981 100%)',
                      '&:hover': {
                        background: 'linear-gradient(135deg, #1e3a8a 0%, #059669 100%)',
                      }
                    })
                  }}
                >
                  {timeframe.charAt(0).toUpperCase() + timeframe.slice(1)}
                </Button>
              ))}
            </Stack>
        </GlassCard>

        {/* Bento Grid Dashboard Layout */}
        <BentoGrid animate={true} staggerDelay={0.1}>
          {/* KPI Cards Row */}
          <BentoItem area="kpi1" variant="kpi">
            <ModernKPICard
              title="Total Revenue"
              value={`$${dashboardData.kpis.revenue.toLocaleString()}`}
              change={12.5}
              icon={<AttachMoney />}
              color={theme.palette.primary.main}
              onClick={() => handleKPIWidgetClick('Revenue')}
            />
          </BentoItem>

          <BentoItem area="kpi2" variant="kpi">
            <ModernKPICard
              title="Jobs in Progress"
              value={dashboardData.kpis.jobsInProgress}
              change={-2.3}
              icon={<Build />}
              color={theme.palette.warning.main}
              onClick={() => handleKPIWidgetClick('Jobs')}
            />
          </BentoItem>

          <BentoItem area="kpi3" variant="kpi">
            <ModernKPICard
              title="Avg Cycle Time"
              value={`${dashboardData.kpis.avgCycleTime} days`}
              change={8.1}
              icon={<Speed />}
              color={theme.palette.info.main}
              onClick={() => handleKPIWidgetClick('Cycle Time')}
            />
          </BentoItem>

          <BentoItem area="kpi4" variant="kpi">
            <ModernKPICard
              title="Customer Satisfaction"
              value={`${dashboardData.kpis.customerSatisfaction}%`}
              change={5.2}
              icon={<VerifiedUser />}
              color={theme.palette.success.main}
              onClick={() => handleKPIWidgetClick('Satisfaction')}
            />
          </BentoItem>

          {/* Production Status - Large Widget */}
          <BentoItem area="production" variant="large">
            <GlassCard
              variant="elevated"
              sx={{ 
                p: 3,
                height: '100%',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h6" sx={{ fontWeight: 600 }}>
                  Production Status
                </Typography>
                <Button
                  endIcon={<ArrowForward />}
                  onClick={handleViewFullBoard}
                  sx={{ 
                    textTransform: 'none', 
                    fontWeight: 600,
                    backdropFilter: 'blur(10px)',
                    '&:hover': { transform: 'scale(1.02)' }
                  }}
                >
                  View Full Board
                </Button>
              </Box>

              <Grid container spacing={2} sx={{ flex: 1 }}>
                <Grid item xs={6} sm={3}>
                  <StatusWidget
                    title="Estimate"
                    count={dashboardData.productionStats.estimate}
                    total={dashboardData.productionStats.total}
                    icon={<Assignment />}
                    color={theme.palette.info.main}
                    onClick={() => handleStatusWidgetClick('Estimate')}
                  />
                </Grid>
                <Grid item xs={6} sm={3}>
                  <StatusWidget
                    title="In Progress"
                    count={dashboardData.productionStats.inProgress}
                    total={dashboardData.productionStats.total}
                    icon={<Engineering />}
                    color={theme.palette.warning.main}
                    onClick={() => handleStatusWidgetClick('In Progress')}
                  />
                </Grid>
                <Grid item xs={6} sm={3}>
                  <StatusWidget
                    title="Quality Check"
                    count={dashboardData.productionStats.qualityCheck}
                    total={dashboardData.productionStats.total}
                    icon={<VerifiedUser />}
                    color={theme.palette.primary.main}
                    onClick={() => handleStatusWidgetClick('Quality Check')}
                  />
                </Grid>
                <Grid item xs={6} sm={3}>
                  <StatusWidget
                    title="Ready Pickup"
                    count={dashboardData.productionStats.readyPickup}
                    total={dashboardData.productionStats.total}
                    icon={<LocalShipping />}
                    color={theme.palette.success.main}
                    onClick={() => handleStatusWidgetClick('Ready Pickup')}
                  />
                </Grid>
              </Grid>
            </GlassCard>
          </BentoItem>

          {/* Recent Jobs */}
          <BentoItem area="recent" variant="tall">
            <GlassCard
              variant="default"
              sx={{ 
                p: 3,
                height: '100%',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                Recent Jobs
              </Typography>
              <Box sx={{ flex: 1, overflow: 'auto' }}>
                <RecentJobs jobs={recentJobs} />
              </Box>
            </GlassCard>
          </BentoItem>

          {/* Analytics Widget */}
          <BentoItem area="analytics" variant="large">
            <GlassCard
              variant="elevated"
              sx={{ 
                p: 3,
                height: '100%',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                Analytics Overview
              </Typography>
              <Box sx={{ flex: 1 }}>
                <KPIChart />
              </Box>
            </GlassCard>
          </BentoItem>

          {/* Quick Actions */}
          <BentoItem area="sidebar1" variant="compact">
            <GlassCard
              variant="subtle"
              sx={{ 
                p: 3,
                height: '100%',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                Quick Actions
              </Typography>
              <QuickActions />
            </GlassCard>
          </BentoItem>

          {/* Alerts */}
          <BentoItem area="sidebar2" variant="compact">
            <GlassCard
              variant="subtle"
              sx={{ 
                p: 3,
                height: '100%',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                Alerts
              </Typography>
              <AlertsPanel alerts={alerts} />
            </GlassCard>
          </BentoItem>

          {/* Weather Widget */}
          <BentoItem area="weather" variant="compact">
            <GlassCard
              variant="default"
              sx={{ 
                p: 3,
                height: '100%'
              }}
            >
              <WeatherWidget />
            </GlassCard>
          </BentoItem>

          {/* Calendar Widget */}
          <BentoItem area="calendar" variant="compact">
            <GlassCard
              variant="default"
              sx={{ 
                p: 3,
                height: '100%'
              }}
            >
              <CalendarWidget />
            </GlassCard>
          </BentoItem>

          {/* AI Insights Placeholder */}
          <BentoItem area="ai-insights" variant="wide">
            <GlassCard
              variant="frosted"
              sx={{ 
                p: 3,
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.1) 0%, rgba(16, 185, 129, 0.1) 100%)'
              }}
            >
              <Box sx={{ textAlign: 'center' }}>
                <Assessment sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                <Typography variant="h6" sx={{ fontWeight: 600, mb: 1 }}>
                  AI Insights
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Coming Soon - Smart recommendations based on your data
                </Typography>
              </Box>
            </GlassCard>
          </BentoItem>

          {/* Advanced Analytics Section */}
          <BentoItem area="advanced" variant="wide">
            <GlassCard
              variant="elevated"
              sx={{ 
                p: 3,
                height: '100%'
              }}
            >
              <AdvancedAnalytics />
            </GlassCard>
          </BentoItem>
        </BentoGrid>
      </Box>
    </Fade>
  );
};

export default Dashboard;

