              <Typography>Quick Actions: New RO â€¢ Book Calibration â€¢ Order Parts</Typography>
            </GlassCard>
          </BentoItem>

          {/* Alerts (1x3 vertical stack on md+) */}
          <BentoItem span={{ xs: 12, md: 4 }}>
