import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Container, 
  Typography, 
  Grid, 
  Card, 
  CardContent, 
  Chip, 
  Stack, 
  LinearProgress, 
  Avatar,
  IconButton,
  Button,
  Badge,
  Paper,
  Divider
} from '@mui/material';
import { 
  TrendingUp, 
  TrendingDown, 
  Speed,
  AttachMoney,
  Build,
  Schedule,
  Engineering,
  Assessment,
  DirectionsCar,
  Person,
  Inventory,
  Warning,
  CheckCircle,
  Refresh,
  Notifications,
  ArrowUpward,
  ArrowDownward,
  LocalShipping,
  Timer,
  PrecisionManufacturing,
  MonetizationOn,
  Group,
  MoreVert
} from '@mui/icons-material';
import { ModernBackground } from '../../components/Common/ModernBackground';
import { BentoGrid, BentoItem } from '../../components/Layout/BentoGrid';
import { GlassCard } from '../../components/Common/GlassCard';
import { AnimatedCounter } from '../../utils/AnimatedCounter';
import { ResponsiveContainer, LineChart, Line, AreaChart, Area, BarChart, Bar, XAxis, YAxis, Tooltip, PieChart, Pie, Cell } from 'recharts';
import { motion } from 'framer-motion';

// Real auto body shop data
const realTimeData = {
  cycleTime: {
    current: 6.8,
    target: 7.0,
    lastWeek: 7.2,
    trend: -5.6, // Improved by 5.6%
    series: [
      { day: 'Mon', value: 7.2 },
      { day: 'Tue', value: 7.0 },
      { day: 'Wed', value: 6.9 },
      { day: 'Thu', value: 6.8 },
      { day: 'Fri', value: 6.5 },
      { day: 'Sat', value: 6.8 },
      { day: 'Sun', value: 6.8 }
    ]
  },
  laborEfficiency: {
    current: 142.5, // 142.5% efficiency (12 sold hrs / 8.4 worked hrs)
    target: 125.0,
    series: [
      { name: 'Mike Johnson', efficiency: 156, revenue: 12500 },
      { name: 'Sarah Davis', efficiency: 145, revenue: 11800 },
      { name: 'Tom Wilson', efficiency: 138, revenue: 10900 },
      { name: 'Lisa Brown', efficiency: 132, revenue: 9800 }
    ]
  },
  revenuePerTech: 11025, // $11,025 per technician
  averageRO: 1850, // Average Repair Order value
  utilizationRate: 87.5, // 87.5% utilization
  partsEfficiency: {
    inventoryTurns: 8.2, // 8.2 turns per year
    onTime: 94.2, // 94.2% on-time delivery
    markup: 28.5 // 28.5% markup
  },
  touchTime: {
    current: 4.2, // 4.2 days touch time
    target: 4.0,
    breakdown: [
      { stage: 'Teardown', hours: 8, target: 6 },
      { stage: 'Body Work', hours: 24, target: 22 },
      { stage: 'Paint Prep', hours: 12, target: 10 },
      { stage: 'Paint', hours: 16, target: 16 },
      { stage: 'Assembly', hours: 18, target: 16 }
    ]
  }
};

const productionData = [
  { status: 'Estimate', count: 8, color: '#3B82F6' },
  { status: 'Parts Ordered', count: 12, color: '#F59E0B' },
  { status: 'Body Work', count: 15, color: '#8B5CF6' },
  { status: 'Paint Prep', count: 6, color: '#06B6D4' },
  { status: 'Paint Booth', count: 4, color: '#EF4444' },
  { status: 'Assembly', count: 9, color: '#10B981' },
  { status: 'QC', count: 3, color: '#6366F1' },
  { status: 'Ready', count: 5, color: '#22C55E' }
];

const revenueData = [
  { month: 'Jan', labor: 85000, parts: 45000, total: 130000 },
  { month: 'Feb', labor: 92000, parts: 48000, total: 140000 },
  { month: 'Mar', labor: 88000, parts: 52000, total: 140000 },
  { month: 'Apr', labor: 95000, parts: 55000, total: 150000 },
  { month: 'May', labor: 102000, parts: 58000, total: 160000 },
  { month: 'Jun', labor: 108000, parts: 62000, total: 170000 }
];

const alerts = [
  { id: 1, type: 'error', title: 'Parts Overdue', message: 'BMW 3-Series front bumper 3 days overdue', priority: 'high' },
  { id: 2, type: 'warning', title: 'Cycle Time Alert', message: 'Job #2457 exceeding target by 2 days', priority: 'medium' },
  { id: 3, type: 'info', title: 'Insurance Approval', message: 'Supplement approved for $1,245', priority: 'low' }
];

// Enhanced KPI Card with glassmorphism
const ProfessionalKPICard = ({ title, value, prefix = '', suffix = '', trend, icon, color, subtext, isLoading = false }) => {
  const isPositive = trend > 0;
  const trendColor = title.includes('Cycle Time') || title.includes('Touch Time') 
    ? (trend < 0 ? '#10B981' : '#EF4444') // For time metrics, negative is good
    : (trend > 0 ? '#10B981' : '#EF4444'); // For other metrics, positive is good

  return (
    <motion.div
      whileHover={{ y: -8, scale: 1.02 }}
      transition={{ type: 'spring', stiffness: 400, damping: 30 }}
    >
      <GlassCard sx={{ 
        height: '100%',
        background: 'linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%)',
        backdropFilter: 'blur(20px)',
        border: '1px solid rgba(255,255,255,0.2)',
        position: 'relative',
        overflow: 'hidden',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          height: '2px',
          background: `linear-gradient(90deg, ${color}, transparent)`,
        }
      }}>
        <CardContent sx={{ p: 3 }}>
          <Stack direction="row" justifyContent="space-between" alignItems="flex-start" mb={2}>
            <Avatar sx={{ 
              bgcolor: color + '20',
              color: color,
              width: 56,
              height: 56,
              backdropFilter: 'blur(10px)',
              border: `1px solid ${color}40`
            }}>
              {icon}
            </Avatar>
            <IconButton size="small" sx={{ color: 'rgba(255,255,255,0.7)' }}>
              <MoreVert />
            </IconButton>
          </Stack>

          <Typography variant="h3" sx={{ 
            fontWeight: 800, 
            mb: 0.5,
            background: `linear-gradient(135deg, ${color}, #ffffff)`,
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>
            <AnimatedCounter value={value} prefix={prefix} suffix={suffix} />
          </Typography>

          <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.8)', mb: 2, fontWeight: 500 }}>
            {title}
          </Typography>

          <Stack direction="row" alignItems="center" spacing={1}>
            <Chip
              size="small"
              icon={trend >= 0 ? <TrendingUp /> : <TrendingDown />}
              label={`${Math.abs(trend).toFixed(1)}%`}
              sx={{
                bgcolor: trendColor + '20',
                color: trendColor,
                border: `1px solid ${trendColor}40`,
                fontWeight: 600
              }}
            />
            <Typography variant="caption" sx={{ color: 'rgba(255,255,255,0.6)' }}>
              {subtext}
            </Typography>
          </Stack>
        </CardContent>
      </GlassCard>
    </motion.div>
  );
};

// Production Status Widget
const ProductionStatusCard = () => (
  <GlassCard sx={{ height: '100%', p: 3 }}>
    <Typography variant="h6" sx={{ fontWeight: 700, mb: 3, color: 'white' }}>
      Production Pipeline
    </Typography>
    <Grid container spacing={2}>
      {productionData.map((item, index) => (
        <Grid item xs={6} sm={4} md={3} key={index}>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            <Paper sx={{ 
              p: 2, 
              textAlign: 'center',
              background: `linear-gradient(135deg, ${item.color}20, ${item.color}10)`,
              backdropFilter: 'blur(10px)',
              border: `1px solid ${item.color}40`
            }}>
              <Typography variant="h4" sx={{ 
                color: item.color, 
                fontWeight: 800,
                mb: 1
              }}>
                {item.count}
              </Typography>
              <Typography variant="caption" sx={{ 
                color: 'rgba(255,255,255,0.8)',
                fontWeight: 600
              }}>
                {item.status}
              </Typography>
            </Paper>
          </motion.div>
        </Grid>
      ))}
    </Grid>
  </GlassCard>
);

// Revenue Chart Component
const RevenueChart = () => (
  <GlassCard sx={{ height: 400, p: 3 }}>
    <Typography variant="h6" sx={{ fontWeight: 700, mb: 3, color: 'white' }}>
      Revenue Trend - Last 6 Months
    </Typography>
    <ResponsiveContainer width="100%" height={300}>
      <AreaChart data={revenueData}>
        <defs>
          <linearGradient id="laborGradient" x1="0" y1="0" x2="0" y2="1">
            <stop offset="5%" stopColor="#3B82F6" stopOpacity={0.3}/>
            <stop offset="95%" stopColor="#3B82F6" stopOpacity={0}/>
          </linearGradient>
          <linearGradient id="partsGradient" x1="0" y1="0" x2="0" y2="1">
            <stop offset="5%" stopColor="#10B981" stopOpacity={0.3}/>
            <stop offset="95%" stopColor="#10B981" stopOpacity={0}/>
          </linearGradient>
        </defs>
        <XAxis dataKey="month" axisLine={false} tickLine={false} tick={{ fill: 'rgba(255,255,255,0.6)' }} />
        <YAxis axisLine={false} tickLine={false} tick={{ fill: 'rgba(255,255,255,0.6)' }} />
        <Tooltip 
          contentStyle={{ 
            backgroundColor: 'rgba(15,23,42,0.9)', 
            border: '1px solid rgba(255,255,255,0.2)',
            borderRadius: '12px'
          }} 
        />
        <Area type="monotone" dataKey="labor" stackId="1" stroke="#3B82F6" fill="url(#laborGradient)" />
        <Area type="monotone" dataKey="parts" stackId="1" stroke="#10B981" fill="url(#partsGradient)" />
      </AreaChart>
    </ResponsiveContainer>
  </GlassCard>
);

// Technician Performance Component
const TechnicianPerformance = () => (
  <GlassCard sx={{ height: '100%', p: 3 }}>
    <Typography variant="h6" sx={{ fontWeight: 700, mb: 3, color: 'white' }}>
      Technician Performance
    </Typography>
    <Stack spacing={3}>
      {realTimeData.laborEfficiency.series.map((tech, index) => (
        <Box key={index}>
          <Stack direction="row" justifyContent="space-between" alignItems="center" mb={1}>
            <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.9)', fontWeight: 600 }}>
              {tech.name}
            </Typography>
            <Stack direction="row" spacing={2} alignItems="center">
              <Typography variant="caption" sx={{ color: '#10B981' }}>
                ${tech.revenue.toLocaleString()}
              </Typography>
              <Chip 
                label={`${tech.efficiency}%`}
                size="small"
                sx={{ 
                  bgcolor: tech.efficiency >= 140 ? '#10B98120' : '#F59E0B20',
                  color: tech.efficiency >= 140 ? '#10B981' : '#F59E0B',
                  fontWeight: 600
                }}
              />
            </Stack>
          </Stack>
          <LinearProgress 
            variant="determinate" 
            value={Math.min(tech.efficiency, 200)} 
            sx={{ 
              height: 8,
              borderRadius: 4,
              bgcolor: 'rgba(255,255,255,0.1)',
              '& .MuiLinearProgress-bar': {
                bgcolor: tech.efficiency >= 140 ? '#10B981' : '#F59E0B',
                borderRadius: 4
              }
            }} 
          />
        </Box>
      ))}
    </Stack>
  </GlassCard>
);

// Alerts Panel
const AlertsPanel = () => (
  <GlassCard sx={{ height: '100%', p: 3 }}>
    <Stack direction="row" justifyContent="space-between" alignItems="center" mb={3}>
      <Typography variant="h6" sx={{ fontWeight: 700, color: 'white' }}>
        Live Alerts
      </Typography>
      <Badge badgeContent={alerts.length} color="error">
        <Notifications sx={{ color: 'rgba(255,255,255,0.7)' }} />
      </Badge>
    </Stack>
    <Stack spacing={2}>
      {alerts.map((alert) => (
        <motion.div key={alert.id} initial={{ x: -20, opacity: 0 }} animate={{ x: 0, opacity: 1 }}>
          <Paper sx={{ 
            p: 2,
            background: alert.type === 'error' ? 'rgba(239,68,68,0.1)' : 
                        alert.type === 'warning' ? 'rgba(245,158,11,0.1)' : 'rgba(59,130,246,0.1)',
            border: `1px solid ${
              alert.type === 'error' ? '#EF4444' : 
              alert.type === 'warning' ? '#F59E0B' : '#3B82F6'
            }40`
          }}>
            <Typography variant="body2" sx={{ fontWeight: 600, color: 'white', mb: 0.5 }}>
              {alert.title}
            </Typography>
            <Typography variant="caption" sx={{ color: 'rgba(255,255,255,0.7)' }}>
              {alert.message}
            </Typography>
          </Paper>
        </motion.div>
      ))}
    </Stack>
  </GlassCard>
);

export const DashboardPage = () => {
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <Box sx={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0F172A 0%, #1E293B 50%, #0F172A 100%)',
      position: 'relative'
    }}>
      <ModernBackground />

      <Container maxWidth="xl" sx={{ py: 4, position: 'relative', zIndex: 1 }}>
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
