import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Container, 
  Typography, 
  Grid, 
  Card, 
  CardContent, 
  Chip, 
  Stack, 
  LinearProgress, 
  Avatar,
  IconButton,
  Button,
  Badge,
  Paper,
  Divider
} from '@mui/material';
import { 
  TrendingUp, 
  TrendingDown, 
  Speed,
  AttachMoney,
  Build,
  Schedule,
  Engineering,
  Assessment,
  DirectionsCar,
  Person,
  Inventory,
  Warning,
  CheckCircle,
  Refresh,
  Notifications,
  ArrowUpward,
  ArrowDownward,
  LocalShipping,
  Timer,
  PrecisionManufacturing,
  MonetizationOn,
  Group,
  MoreVert
} from '@mui/icons-material';
import { ModernBackground } from '../../components/Common/ModernBackground';
import { BentoGrid, BentoItem } from '../../components/Layout/BentoGrid';
import { GlassCard } from '../../components/Common/GlassCard';
import { AnimatedCounter } from '../../utils/AnimatedCounter';
import { ResponsiveContainer, LineChart, Line, AreaChart, Area, BarChart, Bar, XAxis, YAxis, Tooltip, PieChart, Pie, Cell } from 'recharts';
import { motion } from 'framer-motion';

// Real auto body shop data
const realTimeData = {
  cycleTime: {
    current: 6.8,
    target: 7.0,
    lastWeek: 7.2,
    trend: -5.6, // Improved by 5.6%
    series: [
      { day: 'Mon', value: 7.2 },
      { day: 'Tue', value: 7.0 },
      { day: 'Wed', value: 6.9 },
      { day: 'Thu', value: 6.8 },
      { day: 'Fri', value: 6.5 },
      { day: 'Sat', value: 6.8 },
      { day: 'Sun', value: 6.8 }
    ]
  },
  laborEfficiency: {
    current: 142.5, // 142.5% efficiency (12 sold hrs / 8.4 worked hrs)
    target: 125.0,
    series: [
      { name: 'Mike Johnson', efficiency: 156, revenue: 12500 },
      { name: 'Sarah Davis', efficiency: 145, revenue: 11800 },
      { name: 'Tom Wilson', efficiency: 138, revenue: 10900 },
      { name: 'Lisa Brown', efficiency: 132, revenue: 9800 }
    ]
  },
  revenuePerTech: 11025, // $11,025 per technician
  averageRO: 1850, // Average Repair Order value
  utilizationRate: 87.5, // 87.5% utilization
  partsEfficiency: {
    inventoryTurns: 8.2, // 8.2 turns per year
    onTime: 94.2, // 94.2% on-time delivery
    markup: 28.5 // 28.5% markup
  },
  touchTime: {
    current: 4.2, // 4.2 days touch time
    target: 4.0,
    breakdown: [
      { stage: 'Teardown', hours: 8, target: 6 },
      { stage: 'Body Work', hours: 24, target: 22 },
      { stage: 'Paint Prep', hours: 12, target: 10 },
      { stage: 'Paint', hours: 16, target: 16 },
      { stage: 'Assembly', hours: 18, target: 16 }
    ]
  }
};

const productionData = [
  { status: 'Estimate', count: 8, color: '#3B82F6' },
  { status: 'Parts Ordered', count: 12, color: '#F59E0B' },
  { status: 'Body Work', count: 15, color: '#8B5CF6' },
  { status: 'Paint Prep', count: 6, color: '#06B6D4' },
  { status: 'Paint Booth', count: 4, color: '#EF4444' },
  { status: 'Assembly', count: 9, color: '#10B981' },
  { status: 'QC', count: 3, color: '#6366F1' },
  { status: 'Ready', count: 5, color: '#22C55E' }
];

const revenueData = [
  { month: 'Jan', labor: 85000, parts: 45000, total: 130000 },
  { month: 'Feb', labor: 92000, parts: 48000, total: 140000 },
  { month: 'Mar', labor: 88000, parts: 52000, total: 140000 },
  { month: 'Apr', labor: 95000, parts: 55000, total: 150000 },
