const { test, expect } = require('@playwright/test');

test.describe('Parts Management System', () => {
  test.beforeEach(async ({ page }) => {
    // Login and navigate to parts management
    await page.goto('/');

    // Login if needed
    const needsLogin = await page.locator('input[type="password"]').count() > 0;
    if (needsLogin) {
      await page.fill('input[type="email"], input[placeholder*="admin"], input[name="username"]', 'admin');
      await page.fill('input[type="password"]', 'admin123');
      await page.click('button:has-text("Login"), button[type="submit"]');
      await page.waitForTimeout(3000);
    }

    // Navigate to parts management
    await page.goto('/parts');
    await page.waitForTimeout(2000);

    // Alternative navigation via menu
    const partsLink = await page.locator('a:has-text("Parts"), button:has-text("Parts")').count();
    if (partsLink > 0) {
      await page.click('a:has-text("Parts"), button:has-text("Parts")');
      await page.waitForTimeout(2000);
    }
  });

  test('should display parts management header', async ({ page }) => {
    // Check for parts management title
    await expect(page.locator('h1, h2, h3, h4').filter({ hasText: /parts/i })).toBeVisible();

    // Check for subtitle
    const subtitle = await page.locator('text*="Manage parts inventory"').count() > 0;
    console.log(`âœ… Parts management header displayed, subtitle: ${subtitle}`);
  });

  test('should display stats cards', async ({ page }) => {
    // Wait for stats to load
    await page.waitForTimeout(2000);

    // Look for KPI cards
    const statsCards = await page.locator('.MuiCard-root').count();
    const totalParts = await page.locator('text*="Total Parts"').count() > 0;
    const partsOrdered = await page.locator('text*="Parts Ordered"').count() > 0;
    const lowStock = await page.locator('text*="Low Stock"').count() > 0;

    console.log(`ğŸ“Š Parts Stats:
    - Total cards: ${statsCards}
    - Total Parts card: ${totalParts}
    - Parts Ordered card: ${partsOrdered}
    - Low Stock card: ${lowStock}`);

    expect(statsCards).toBeGreaterThan(0);
  });

  test('should display alert notifications', async ({ page }) => {
    // Look for alert messages
    const alerts = await page.locator('[role="alert"], .MuiAlert-root').count();
    const lowStockAlert = await page.locator('text*="running low on stock"').count() > 0;
    const deliveryAlert = await page.locator('text*="arriving today"').count() > 0;

    console.log(`ğŸš¨ Parts Alerts: ${alerts}, Low stock: ${lowStockAlert}, Delivery: ${deliveryAlert}`);

    if (alerts > 0) {
      console.log('âœ… Alert system is working');
    }
  });

  test('should have quick action buttons', async ({ page }) => {
    // Look for quick action buttons
    const createPOButton = await page.locator('button:has-text("Create Purchase Order")').count() > 0;
    const receiveShipment = await page.locator('button:has-text("Receive Shipment")').count() > 0;
    const stockCheck = await page.locator('button:has-text("Stock Check")').count() > 0;
    const partsReport = await page.locator('button:has-text("Parts Report")').count() > 0;

    console.log(`âš¡ Quick Actions:
    - Create PO: ${createPOButton}
    - Receive Shipment: ${receiveShipment}
    - Stock Check: ${stockCheck}
    - Parts Report: ${partsReport}`);

    expect(createPOButton || receiveShipment).toBeTruthy();
  });

  test('should display tabs navigation', async ({ page }) => {
    // Check for tab navigation
    const tabsContainer = await page.locator('[role="tablist"], .MuiTabs-root').count();
    const partsBoard = await page.locator('[role="tab"]:has-text("Parts Board")').count() > 0;
    const inventory = await page.locator('[role="tab"]:has-text("Inventory")').count() > 0;
    const purchaseOrders = await page.locator('[role="tab"]:has-text("Purchase Orders")').count() > 0;
    const suppliers = await page.locator('[role="tab"]:has-text("Suppliers")').count() > 0;

    console.log(`ğŸ“‘ Tabs Navigation:
    - Tab container: ${tabsContainer > 0}
    - Parts Board: ${partsBoard}
    - Inventory: ${inventory}
    - Purchase Orders: ${purchaseOrders}
    - Suppliers: ${suppliers}`);

    expect(tabsContainer).toBeGreaterThan(0);
  });

  test('should display parts board with status columns', async ({ page }) => {
    // Wait for parts board to load
    await page.waitForTimeout(2000);

    // Look for parts status columns
    const statusColumns = [
      'Needed', 'Ordered', 'Shipped', 'Received', 'Installed', 'Returned'
    ];

    let foundColumns = 0;
    for (const status of statusColumns) {
      const columnExists = await page.locator(`text="${status}"`).count() > 0;
      if (columnExists) foundColumns++;
    }

    console.log(`ğŸ“¦ Parts status columns found: ${foundColumns}/${statusColumns.length}`);
    expect(foundColumns).toBeGreaterThan(2);
  });

  test('should switch between tabs', async ({ page }) => {
    // Test tab switching
    const inventoryTab = page.locator('[role="tab"]:has-text("Inventory")');
    const inventoryTabExists = await inventoryTab.count() > 0;

    if (inventoryTabExists) {
      await inventoryTab.click();
      await page.waitForTimeout(1000);

      // Check if inventory content is displayed
      const inventoryTable = await page.locator('table, [role="table"]').count();
      console.log(`ğŸ“‹ Inventory tab - Table found: ${inventoryTable > 0}`);

      // Switch to Purchase Orders tab
      const poTab = page.locator('[role="tab"]:has-text("Purchase Orders")');
      const poTabExists = await poTab.count() > 0;

      if (poTabExists) {
        await poTab.click();
        await page.waitForTimeout(1000);

        const poCards = await page.locator('.MuiCard-root').count();
        console.log(`ğŸ“„ Purchase Orders tab - Cards found: ${poCards}`);
      }

      // Switch to Suppliers tab
      const suppliersTab = page.locator('[role="tab"]:has-text("Suppliers")');
      const suppliersTabExists = await suppliersTab.count() > 0;

      if (suppliersTabExists) {
        await suppliersTab.click();
        await page.waitForTimeout(1000);

        const supplierCards = await page.locator('.MuiCard-root').count();
        console.log(`ğŸª Suppliers tab - Cards found: ${supplierCards}`);
      }

      console.log('âœ… Tab navigation tested');
    }
  });

  test('should display inventory with low stock alerts', async ({ page }) => {
    // Navigate to inventory tab
    const inventoryTab = page.locator('[role="tab"]:has-text("Inventory")');
    const inventoryTabExists = await inventoryTab.count() > 0;

    if (inventoryTabExists) {
      await inventoryTab.click();
      await page.waitForTimeout(1000);

      // Look for low stock warnings
      const warningIcons = await page.locator('[data-testid="WarningIcon"]').count();
      const lowStockRows = await page.locator('tr').filter({ has: page.locator('[data-testid="WarningIcon"]') }).count();

      console.log(`âš ï¸ Inventory low stock indicators: ${warningIcons}, Rows with warnings: ${lowStockRows}`);

      // Check for progress bars
      const progressBars = await page.locator('.MuiLinearProgress-root').count();
      console.log(`ğŸ“Š Stock level progress bars: ${progressBars}`);

      console.log('âœ… Inventory low stock system tested');
    }
  });

  test('should have working search functionality', async ({ page }) => {
    // Look for search button in header
    const searchButton = await page.locator('button:has-text("Search Parts")').count();

    if (searchButton > 0) {
      await page.click('button:has-text("Search Parts")');
      await page.waitForTimeout(1000);

      // Check if search dialog opened
      const searchDialog = await page.locator('[role="dialog"]').count();
      console.log(`ğŸ” Search dialog opened: ${searchDialog > 0}`);

      if (searchDialog > 0) {
        // Test search input
        const searchInput = await page.locator('input[placeholder*="part number" i]').count();
        if (searchInput > 0) {
          await page.fill('input[placeholder*="part number" i]', 'TOY-52119-06903');
          await page.waitForTimeout(500);
          console.log('âœ… Search input tested');
        }

        // Close dialog
        const closeButton = await page.locator('button:has-text("Close")').count();
        if (closeButton > 0) {
          await page.click('button:has-text("Close")');
          await page.waitForTimeout(500);
        }
      }
    }

    console.log('âœ… Parts search functionality tested');
  });

  test('should have barcode scanning capability', async ({ page }) => {
    // Look for barcode scan button
    const scanButton = await page.locator('button:has-text("Scan Barcode"), [data-testid="QrCodeIcon"]').count();

    if (scanButton > 0) {
      await page.click('button:has-text("Scan Barcode"), [data-testid="QrCodeIcon"]');
      await page.waitForTimeout(1000);

      console.log('âœ… Barcode scan button clicked - Scanner would activate in real implementation');
    } else {
      console.log('âš ï¸ Barcode scan button not found');
    }
  });

  test('should display supplier information', async ({ page }) => {
    // Navigate to suppliers tab
    const suppliersTab = page.locator('[role="tab"]:has-text("Suppliers")');
    const suppliersTabExists = await suppliersTab.count() > 0;

    if (suppliersTabExists) {
      await suppliersTab.click();
      await page.waitForTimeout(1000);

      // Check for supplier cards with expected suppliers from implementation
      const oemDirect = await page.locator('text*="OEM Direct"').count() > 0;
      const oeConnection = await page.locator('text*="OE Connection"').count() > 0;
      const partsTrader = await page.locator('text*="PartsTrader"').count() > 0;
      const lkq = await page.locator('text*="LKQ"').count() > 0;

      console.log(`ğŸª Suppliers found:
      - OEM Direct: ${oemDirect}
      - OE Connection: ${oeConnection}
      - PartsTrader: ${partsTrader}
      - LKQ/Recycled: ${lkq}`);

      // Check for star ratings
      const starRatings = await page.locator('[data-testid="StarIcon"]').count();
      console.log(`â­ Star ratings found: ${starRatings}`);

      expect(oemDirect || oeConnection || partsTrader).toBeTruthy();
    }
  });

  test('should handle add part dialog', async ({ page }) => {
    // Look for add part button
    const addButton = await page.locator('button:has-text("Add Part")').count();

    if (addButton > 0) {
      await page.click('button:has-text("Add Part")');
      await page.waitForTimeout(1000);

      // Check if add part dialog opened
      const dialog = await page.locator('[role="dialog"]').count();
      console.log(`â• Add part dialog opened: ${dialog > 0}`);

      if (dialog > 0) {
        // Close dialog
        const cancelButton = await page.locator('button:has-text("Cancel"), button:has-text("Close")').count();
        if (cancelButton > 0) {
          await page.click('button:has-text("Cancel"), button:has-text("Close")');
          await page.waitForTimeout(500);
        }
      }

      console.log('âœ… Add part dialog tested');
    }
  });

  test('should be responsive on mobile', async ({ page }) => {
    // Set mobile viewport
    await page.setViewportSize({ width: 375, height: 667 });
    await page.waitForTimeout(1000);

    // Check that key elements are still visible
    const title = await page.locator('h1, h2, h3, h4').filter({ hasText: /parts/i }).isVisible();
    const tabs = await page.locator('[role="tablist"]').isVisible();
    const cards = await page.locator('.MuiCard-root').count();

    console.log(`ğŸ“± Mobile view - Title: ${title}, Tabs: ${tabs}, Cards: ${cards}`);
    expect(title || cards > 0).toBeTruthy();
  });

  test('should take parts management screenshots', async ({ page }) => {
    // Take desktop screenshots of different tabs
    await page.screenshot({ 
      path: 'test-results/parts-board-screenshot.png',
      fullPage: true 
    });

    // Inventory tab screenshot
    const inventoryTab = page.locator('[role="tab"]:has-text("Inventory")');
    if (await inventoryTab.count() > 0) {
      await inventoryTab.click();
      await page.waitForTimeout(1000);

      await page.screenshot({ 
        path: 'test-results/parts-inventory-screenshot.png',
        fullPage: true 
      });
    }

    // Mobile screenshot
    await page.setViewportSize({ width: 375, height: 667 });
    await page.waitForTimeout(1000);

    await page.screenshot({ 
      path: 'test-results/parts-mobile-screenshot.png',
      fullPage: true 
    });

    console.log('ğŸ“¸ Parts management screenshots saved');
  });

  test('should measure parts management performance', async ({ page }) => {
    const startTime = Date.now();

    await page.goto('/parts');
    await page.waitForLoadState('networkidle');

    // Wait for parts board to load
    await page.waitForSelector('[role="tablist"]', { timeout: 10000 }).catch(() => {
      console.log('âš ï¸ Parts tabs not found within timeout');
    });

    const loadTime = Date.now() - startTime;

    console.log(`âš¡ Parts Management Performance: ${loadTime}ms`);
    expect(loadTime).toBeLessThan(6000); // Should load within 6 seconds
  });
});

