import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';

export const ProtectedRoute = () => {
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }
  return <Outlet />;
};

import React from 'react';
import { Outlet, Link } from 'react-router-dom';
import { AppBar, Toolbar, Typography, Box, Button } from '@mui/material';

export default function Layout() {
  return (
    <Box sx={{width:'100%'}}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{flex:1}}>CollisionOS</Typography>
          <Button color="inherit" component={Link} to="/dashboard">Dashboard</Button>
          <Button color="inherit" component={Link} to="/production">Production</Button>
          <Button color="inherit" component={Link} to="/customers">Customers</Button>
          <Button color="inherit" component={Link} to="/parts">Parts</Button>
          <Button color="inherit" component={Link} to="/technician">Technician</Button>
          <Button color="inherit" component={Link} to="/quality-control">QC</Button>
          <Button color="inherit" component={Link} to="/reports">Reports</Button>
        </Toolbar>
      </AppBar>
      <Box sx={{p:2}}>
        <Outlet />
      </Box>
    </Box>
  );
}

