const { test, expect } = require('@playwright/test');

test.describe('Authentication Flow', () => {
  test.beforeEach(async ({ page }) => {
    // Navigate to the app
    await page.goto('/');
  });

  test('should display login page correctly', async ({ page }) => {
    // Check if we're on login page
    await expect(page).toHaveTitle(/CollisionOS/);

    // Check for login form elements
    await expect(page.locator('input[placeholder="admin"]')).toBeVisible();
    await expect(page.locator('input[type="password"]')).toBeVisible();
    await expect(page.locator('button:has-text("Login"), button[type="submit"]')).toBeVisible();

    // Check for CollisionOS branding/logo
    await expect(page.locator('text=CollisionOS')).toBeVisible();
  });

  test('should show validation errors for empty form', async ({ page }) => {
    // Try to submit empty form
    await page.click('button:has-text("Login"), button[type="submit"]');

    // Wait a moment for validation
    await page.waitForTimeout(1000);

    // Check for error messages (could be different selectors depending on implementation)
    const hasError = await page.locator('[role="alert"], .error, .MuiFormHelperText-error').count() > 0;

    if (hasError) {
      console.log('âœ… Validation errors shown for empty form');
    } else {
      console.log('âš ï¸ No validation errors found - this might be expected behavior');
    }
  });

  test('should login with valid credentials', async ({ page }) => {
    // Fill login form
    await page.fill('input[placeholder="admin"]', 'admin');
    await page.fill('input[type="password"]', 'admin123');

    // Submit form
    await page.click('button:has-text("Login"), button[type="submit"]');

    // Wait for navigation or dashboard elements
    await page.waitForTimeout(3000);

    // Check if redirected to dashboard or main app
    const url = page.url();
    const isDashboardPage = url.includes('/dashboard');

    if (isDashboardPage) {
      // Check for app bar and dashboard elements
      await expect(page.locator('[role="toolbar"], .MuiToolbar-root')).toBeVisible();
      await expect(page.locator('text=CollisionOS')).toBeVisible();

      // Check for navigation buttons in the toolbar
      const hasNavButtons = await page.locator('button:has-text("Dashboard"), button:has-text("Production"), button:has-text("Parts")').count() > 0;
      expect(hasNavButtons).toBeTruthy();
    } else {
      // Fallback check for any navigation elements
      const hasNavigation = await page.locator('nav, [role="navigation"], [role="toolbar"], .MuiToolbar-root').count() > 0;
      expect(hasNavigation).toBeTruthy();
    }

    console.log(`âœ… Login successful - Current URL: ${url}`);
  });

  test('should handle invalid credentials gracefully', async ({ page }) => {
    // Fill with invalid credentials
    await page.fill('input[placeholder="admin"]', 'invalid');
    await page.fill('input[type="password"]', 'wrongpass');

    // Submit form
    await page.click('button:has-text("Login"), button[type="submit"]');

    // Wait for response
    await page.waitForTimeout(2000);

    // Should still be on login page or show error
    const currentUrl = page.url();
    const hasErrorMessage = await page.locator('[role="alert"], .error').count() > 0;

    // Either should stay on login page or show error
    const isStillOnLogin = currentUrl.includes('/login') || !currentUrl.includes('/dashboard');

    expect(isStillOnLogin || hasErrorMessage).toBeTruthy();
    console.log('âœ… Invalid credentials handled appropriately');
  });

  test('should be responsive on mobile viewport', async ({ page }) => {
    // Set mobile viewport
    await page.setViewportSize({ width: 375, height: 667 });

    // Check that login elements are still visible and accessible
    await expect(page.locator('input[placeholder="admin"]')).toBeVisible();
    await expect(page.locator('input[type="password"]')).toBeVisible();
    await expect(page.locator('button:has-text("Login"), button[type="submit"]')).toBeVisible();

    // Test form interaction on mobile
    await page.fill('input[placeholder="admin"]', 'admin');
    await page.fill('input[type="password"]', 'admin123');

    console.log('âœ… Login form is responsive and functional on mobile');
  });

  test('should take screenshot of login page', async ({ page }) => {
    // Take screenshot for visual testing
    await page.screenshot({ 
      path: 'test-results/login-page-screenshot.png',
      fullPage: true 
    });

