const { test, expect } = require('@playwright/test');

test.describe('Dashboard Functionality', () => {
  test.beforeEach(async ({ page }) => {
    // Login first
    await page.goto('/');

    // Check if login is needed
    const needsLogin = await page.locator('input[type="password"]').count() > 0;

    if (needsLogin) {
      await page.fill('input[type="email"], input[placeholder*="admin"], input[name="username"]', 'admin');
      await page.fill('input[type="password"]', 'admin123');
      await page.click('button:has-text("Login"), button[type="submit"]');
      await page.waitForTimeout(3000);
    }

    // Navigate to dashboard
    const currentUrl = page.url();
    if (!currentUrl.includes('/dashboard')) {
      // Try to navigate to dashboard via URL
      await page.goto('/dashboard');
      await page.waitForTimeout(2000);

      // Or try clicking dashboard navigation
      const dashboardLink = await page.locator('a:has-text("Dashboard"), button:has-text("Dashboard")').count();
      if (dashboardLink > 0) {
        await page.click('a:has-text("Dashboard"), button:has-text("Dashboard")');
        await page.waitForTimeout(2000);
      }
    }
  });

  test('should display dashboard title and welcome message', async ({ page }) => {
    // Check for dashboard title
    await expect(page.locator('h1, h2, h3, h4').filter({ hasText: /dashboard/i })).toBeVisible();

    // Check for welcome message or user greeting
    const welcomeMessage = await page.locator('text*="Welcome"').count() > 0;
    const userGreeting = await page.locator('text*="Hello"').count() > 0;

    expect(welcomeMessage || userGreeting).toBeTruthy();
    console.log('âœ… Dashboard title and greeting displayed');
  });

  test('should display KPI cards with data', async ({ page }) => {
    // Look for KPI/metric cards
    const kpiCards = await page.locator('[role="region"], .MuiCard-root, .card').count();
    expect(kpiCards).toBeGreaterThan(0);

    // Check for specific KPIs mentioned in the implementation
    const revenueKPI = await page.locator('text*="Revenue"').count() > 0;
    const jobsKPI = await page.locator('text*="Jobs"').count() > 0;
    const efficiencyKPI = await page.locator('text*="Efficiency"').count() > 0;
    const satisfactionKPI = await page.locator('text*="Satisfaction"').count() > 0;

    console.log(`ğŸ“Š KPI Cards Found:
    - Revenue: ${revenueKPI}
    - Jobs: ${jobsKPI}
    - Efficiency: ${efficiencyKPI}
    - Satisfaction: ${satisfactionKPI}
    - Total Cards: ${kpiCards}`);

    expect(revenueKPI || jobsKPI).toBeTruthy();
  });

  test('should display charts and visualizations', async ({ page }) => {
    // Wait for charts to load
    await page.waitForTimeout(3000);

    // Look for chart containers (Chart.js creates canvas elements)
    const charts = await page.locator('canvas').count();
    const chartContainers = await page.locator('[class*="chart"], [class*="Chart"]').count();

    console.log(`ğŸ“ˆ Charts found: ${charts} canvas elements, ${chartContainers} chart containers`);

    // Check for specific chart titles from implementation
    const revenueTrend = await page.locator('text*="Revenue Trend"').count() > 0;
    const jobDistribution = await page.locator('text*="Job Status Distribution", text*="Job Distribution"').count() > 0;

    expect(charts > 0 || chartContainers > 0).toBeTruthy();
    console.log(`âœ… Charts displayed - Revenue Trend: ${revenueTrend}, Job Distribution: ${jobDistribution}`);
  });

  test('should display production status widgets', async ({ page }) => {
    // Look for production status indicators
    const statusWidgets = await page.locator('text*="Estimate", text*="In Progress", text*="Quality Check"').count();
    const progressBars = await page.locator('.MuiLinearProgress-root, [role="progressbar"]').count();

    console.log(`ğŸ­ Production widgets: ${statusWidgets}, Progress bars: ${progressBars}`);
    expect(statusWidgets).toBeGreaterThan(0);
  });

  test('should have working timeframe selector', async ({ page }) => {
    // Look for timeframe buttons (Today, Week, Month, Quarter)
    const timeframeButtons = await page.locator('button:has-text("Today"), button:has-text("Week"), button:has-text("Month")').count();

    if (timeframeButtons > 0) {
      console.log(`ğŸ“… Timeframe selector found with ${timeframeButtons} options`);

      // Try clicking different timeframes
      const monthButton = await page.locator('button:has-text("Month")').count();
      if (monthButton > 0) {
        await page.click('button:has-text("Month")');
        await page.waitForTimeout(1000);
        console.log('âœ… Month timeframe selection works');
      }
    } else {
      console.log('âš ï¸ Timeframe selector not found');
    }
  });

  test('should display alerts and notifications', async ({ page }) => {
    // Look for alerts panel
    const alertsPanel = await page.locator('text*="Alerts"').count() > 0;
    const notifications = await page.locator('[role="alert"], .MuiAlert-root').count();

    console.log(`ğŸš¨ Alerts panel: ${alertsPanel}, Active alerts: ${notifications}`);

    if (alertsPanel) {
      console.log('âœ… Alerts panel displayed');
    }
  });

  test('should display quick actions', async ({ page }) => {
    // Look for quick action buttons
    const quickActionsSection = await page.locator('text*="Quick Actions"').count() > 0;
    const actionButtons = await page.locator('button:has-text("New Job"), button:has-text("Create"), button:has-text("Add")').count();

    console.log(`âš¡ Quick actions section: ${quickActionsSection}, Action buttons: ${actionButtons}`);
    expect(quickActionsSection || actionButtons > 0).toBeTruthy();
  });

  test('should display recent jobs list', async ({ page }) => {
    // Look for recent jobs section
    const recentJobsSection = await page.locator('text*="Recent Jobs"').count() > 0;
