import React, { useState, useEffect, useRef } from 'react';
import { Button, Box, TextField, Typography, Alert, CircularProgress, Paper } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { authService } from '../../services/authService';

export default function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const usernameFieldRef = useRef(null);

  useEffect(() => {
    // Focus username field after component mounts
    const timer = setTimeout(() => {
      if (usernameFieldRef.current) {
        usernameFieldRef.current.focus();
      }
    }, 100);

    return () => {
      clearTimeout(timer);
    };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setError('');
      setLoading(true);

      if (!username || !password) {
        setError('Please enter both username and password');
        return;
      }

      const response = await authService.login(username, password);

      if (response.success && response.token && response.user) {
        localStorage.setItem('token', response.token);
        login(response.user, response.token);
        navigate('/dashboard');
      } else {
        setError('Invalid username or password');
      }
    } catch (err) {
      if (err.response?.data?.error) {
        setError(err.response.data.error);
      } else if (err.message === 'Network Error') {
        setError('Cannot connect to server. Please check if the server is running.');
      } else {
        setError('Login failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <Box sx={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: { xs: 2, sm: 3, md: 4, lg: 5 },
      position: 'relative',
      width: '100vw',
      '&::before': {
        content: '""',
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'radial-gradient(ellipse at center, rgba(255,255,255,0.1) 0%, transparent 70%)',
        pointerEvents: 'none'
      }
    }}>
      <Paper sx={{
        width: '100%',
        maxWidth: { xs: 420, sm: 500, md: 600, lg: 700 },
        padding: { xs: 4, sm: 5, md: 6, lg: 7 },
        borderRadius: 4,
        boxShadow: '0 20px 40px rgba(0,0,0,0.15)',
        background: 'rgba(255, 255, 255, 0.95)',
        backdropFilter: 'blur(10px)',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        position: 'relative',
        zIndex: 1
      }}>
        <Box sx={{ textAlign: 'center', mb: { xs: 4, md: 5 } }}>
          <Box sx={{ 
            fontSize: { xs: '3rem', md: '4rem', lg: '4.5rem' }, 
            mb: 2,
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            ðŸ”§
          </Box>
          <Typography variant="h3" sx={{ 
            fontWeight: 'bold', 
            mb: 1,
            fontSize: { xs: '2rem', sm: '2.5rem', md: '3rem', lg: '3.5rem' },
            background: 'linear-gradient(135deg, #333 0%, #666 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text',
            lineHeight: 1.2
          }}>
            CollisionOS
          </Typography>
          <Typography variant="h5" sx={{ 
            color: 'rgba(0,0,0,0.7)',
            fontWeight: 400,
            fontSize: { xs: '1.2rem', sm: '1.4rem', md: '1.6rem', lg: '1.8rem' },
            lineHeight: 1.4
          }}>
            Auto Body Shop Management
          </Typography>
        </Box>

        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        <Box component="form" onSubmit={handleSubmit} sx={{ width: '100%' }}>
          <TextField 
            ref={usernameFieldRef}
            label="Username" 
            fullWidth
            margin="normal"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="admin"
            variant="outlined"
            autoComplete="username"
            disabled={loading}
            sx={{ 
              mb: 2.5,
              position: 'relative',
              zIndex: 100,
              pointerEvents: 'auto',
              '& .MuiOutlinedInput-root': {
                borderRadius: 2,
                background: 'rgba(255, 255, 255, 0.95)',
                fontSize: { xs: '1rem', md: '1.1rem', lg: '1.2rem' },
                padding: { xs: '0', md: '4px 0' },
                position: 'relative',
                zIndex: 100,
                pointerEvents: 'auto',
                '&:hover .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#667eea',
                },
                '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#667eea',
                  borderWidth: 2,
                },
              },
              '& .MuiInputBase-input': {
                color: 'rgba(0, 0, 0, 0.9)',
                fontSize: { xs: '1rem', md: '1.1rem', lg: '1.2rem' },
                padding: { xs: '16px 14px', md: '18px 16px', lg: '20px 18px' },
                position: 'relative',
                zIndex: 100,
                pointerEvents: 'auto',
                '&::placeholder': {
                  color: 'rgba(0, 0, 0, 0.5)',
                  opacity: 1,
                },
              },
              '& .MuiInputLabel-root': {
                fontSize: { xs: '1rem', md: '1.1rem', lg: '1.2rem' },
                color: 'rgba(0, 0, 0, 0.7)',
                position: 'relative',
                zIndex: 100,
                pointerEvents: 'auto',
                '&.Mui-focused': {
                  color: '#667eea',
                },
              },
            }}
          />

          <TextField 
            label="Password" 
            type="password" 
            fullWidth
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="admin123"
            variant="outlined"
            autoComplete="current-password"
            disabled={loading}
            sx={{ 
              mb: 4,
              '& .MuiOutlinedInput-root': {
                borderRadius: 2,
                background: 'rgba(255, 255, 255, 0.8)',
                fontSize: { xs: '1rem', md: '1.1rem', lg: '1.2rem' },
                padding: { xs: '0', md: '4px 0' },
                '&:hover .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#667eea',
                },
                '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#667eea',
                  borderWidth: 2,
                },
              },
              '& .MuiInputLabel-root': {
                fontSize: { xs: '1rem', md: '1.1rem', lg: '1.2rem' },
                '&.Mui-focused': {
                  color: '#667eea',
                },
              },
            }}
          />

          <Button 
            type="submit"
            variant="contained" 
            disabled={loading}
            size="large"
            fullWidth
            sx={{ 
              py: { xs: 1.8, md: 2.2, lg: 2.5 },
              fontSize: { xs: '1.1rem', md: '1.2rem', lg: '1.3rem' },
              fontWeight: 600,
              borderRadius: 3,
              background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              boxShadow: '0 8px 20px rgba(102, 126, 234, 0.4)',
