const { test, expect } = require('@playwright/test');

test.describe('Smoke Tests', () => {
  test('should load the application without errors', async ({ page }) => {
    // Navigate to the app
    await page.goto('/');

    // Check that the page loads without console errors
    const errors = [];
    page.on('pageerror', error => errors.push(error.message));

    // Wait for the page to load
    await page.waitForTimeout(2000);

    // Check basic elements
    const hasTitle = await page.title();
    const hasContent = await page.locator('body').count() > 0;
    const hasLoginForm = await page.locator('input[type="password"]').count() > 0;

    console.log(`🚀 Application Status:
    - Page Title: ${hasTitle}
    - Has Content: ${hasContent}
    - Has Login Form: ${hasLoginForm}
    - Console Errors: ${errors.length}`);

    // Basic assertions
    expect(hasTitle).toBeTruthy();
    expect(hasContent).toBeTruthy();

    // Log any errors for debugging
    if (errors.length > 0) {
      console.log('Console Errors:', errors);
    }

    console.log('✅ Application loads successfully');
  });

  test('should display correct login page elements', async ({ page }) => {
    await page.goto('/');

    // Check for essential login elements
    await expect(page.locator('text=CollisionOS')).toBeVisible();
    await expect(page.locator('input[placeholder="admin"]')).toBeVisible();
    await expect(page.locator('input[type="password"]')).toBeVisible();
    await expect(page.locator('button:has-text("Login")')).toBeVisible();

    console.log('✅ Login page elements are correct');
  });

  test('should handle basic form interaction', async ({ page }) => {
    await page.goto('/');

    // Test form fields
    await page.fill('input[placeholder="admin"]', 'test');
    await page.fill('input[type="password"]', 'test123');

    const usernameValue = await page.locator('input[placeholder="admin"]').inputValue();
    const passwordValue = await page.locator('input[type="password"]').inputValue();

    expect(usernameValue).toBe('test');
    expect(passwordValue).toBe('test123');

    console.log('✅ Form interaction works correctly');
  });

  test('should be responsive', async ({ page }) => {
    await page.goto('/');

    // Test different viewport sizes
    const viewports = [
      { width: 1920, height: 1080, name: 'Desktop' },
      { width: 768, height: 1024, name: 'Tablet' },
      { width: 375, height: 667, name: 'Mobile' }
    ];

    for (const viewport of viewports) {
      await page.setViewportSize(viewport);
      await page.waitForTimeout(500);

      const hasLoginForm = await page.locator('input[type="password"]').isVisible();
      const hasTitle = await page.locator('text=CollisionOS').isVisible();

      console.log(`📱 ${viewport.name} (${viewport.width}x${viewport.height}): Form=${hasLoginForm}, Title=${hasTitle}`);

      expect(hasLoginForm).toBeTruthy();
      expect(hasTitle).toBeTruthy();
    }

    console.log('✅ Responsive design works correctly');
  });
});

