const { test, expect } = require('@playwright/test');

test.describe('UI Analysis and Screenshots', () => {
  test('capture current UI state and analyze', async ({ page }) => {
    // Navigate to the app
    await page.goto('/');

    // Wait for the page to load
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(2000);

    // Take screenshot of login page
    await page.screenshot({ 
      path: 'test-results/current-login-page.png', 
      fullPage: true 
    });

    console.log('📸 Login page screenshot saved');

    // Try to login with test credentials
    await page.fill('input[placeholder="admin"]', 'admin');
    await page.fill('input[type="password"]', 'admin123');
    await page.click('button:has-text("Login")');

    // Wait for potential redirect or dashboard load
    await page.waitForTimeout(3000);

    // Check if we're on dashboard or still on login
    const currentUrl = page.url();
    console.log(`Current URL: ${currentUrl}`);

    // Take screenshot of whatever page we're on
    await page.screenshot({ 
      path: 'test-results/current-main-page.png', 
      fullPage: true 
    });

    console.log('📸 Main page screenshot saved');

    // Analyze page structure
    const title = await page.title();
    const bodyClasses = await page.locator('body').getAttribute('class');
    const hasNavigation = await page.locator('nav').count() > 0;
    const hasHeader = await page.locator('header').count() > 0;
    const hasSidebar = await page.locator('[role="navigation"], .sidebar, .drawer').count() > 0;

    console.log(`
🔍 UI Analysis:
- Page Title: ${title}
- Body Classes: ${bodyClasses || 'none'}
- Has Navigation: ${hasNavigation}
- Has Header: ${hasHeader}
- Has Sidebar: ${hasSidebar}
- Current URL: ${currentUrl}
    `);

    // Check for Material-UI components
    const muiCards = await page.locator('.MuiCard-root').count();
    const muiButtons = await page.locator('.MuiButton-root').count();
    const muiTextFields = await page.locator('.MuiTextField-root').count();

    console.log(`
🎨 Material-UI Components:
- Cards: ${muiCards}
- Buttons: ${muiButtons}
- Text Fields: ${muiTextFields}
    `);

    expect(title).toBe('CollisionOS');
  });

  test('test theme and color analysis', async ({ page }) => {
    await page.goto('/');
    await page.waitForLoadState('networkidle');

    // Get computed styles of key elements
    const bodyStyles = await page.evaluate(() => {
      const body = document.body;
      const styles = window.getComputedStyle(body);
      return {
        backgroundColor: styles.backgroundColor,
        color: styles.color,
        fontFamily: styles.fontFamily
      };
    });

    console.log(`
🎨 Current Theme Analysis:
- Background Color: ${bodyStyles.backgroundColor}
- Text Color: ${bodyStyles.color}
- Font Family: ${bodyStyles.fontFamily}
    `);
  });
});

