import React, { useState } from 'react';
import { Button, Box, TextField, Typography, Alert, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { authService } from '../../services/authService';
import ModernBackground from '../../components/Common/ModernBackground';
import ModernCard from '../../components/Common/ModernCard';

export default function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    try {
      setError('');
      setLoading(true);

      if (!username || !password) {
        setError('Please enter both username and password');
        return;
      }

      const response = await authService.login(username, password);

      if (response.success && response.token && response.user) {
        // Store token
        localStorage.setItem('token', response.token);

        // Update auth context
        login(response.user, response.token);

        console.log('Login successful, redirecting to dashboard...');
        navigate('/dashboard');
      } else {
        setError('Invalid username or password');
      }
    } catch (err) {
      console.error('Login error:', err);
      if (err.response?.data?.error) {
        setError(err.response.data.error);
      } else if (err.message === 'Network Error') {
        setError('Cannot connect to server. Please check if the server is running.');
      } else {
        setError('Login failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

  return (
    <ModernBackground variant="auth">
      <Box sx={{
        height: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        p: 2
      }}>
        <ModernCard 
          variant="glass" 
          sx={{ 
            maxWidth: 420, 
            width: '100%',
            p: 2
          }}
        >
          <Box sx={{ p: 4, display: 'flex', flexDirection: 'column', gap: 3 }}>
            <Box sx={{ textAlign: 'center', mb: 2 }}>
              <Typography 
                variant="h3" 
                sx={{ 
                  mb: 1,
                  fontWeight: 700,
                  background: 'linear-gradient(135deg, #3b82f6 0%, #9333ea 100%)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  backgroundClip: 'text'
                }}
              >
                🚗 CollisionOS
              </Typography>
              <Typography variant="h6" sx={{ color: 'text.secondary', fontWeight: 500 }}>
                Auto Body Shop Management
              </Typography>
            </Box>

            {error && (
              <Alert 
                severity="error" 
                sx={{ 
                  borderRadius: 2,
                  backdropFilter: 'blur(10px)'
                }}
              >
                {error}
              </Alert>
            )}

            <TextField 
              label="Username" 
              fullWidth
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="admin"
              variant="outlined"
            />

            <TextField 
              label="Password" 
              type="password" 
              fullWidth
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="admin123"
              variant="outlined"
            />

            <Button 
              variant="contained" 
              onClick={handleLogin}
              disabled={loading}
              size="large"
              fullWidth
              sx={{ 
                py: 1.5,
                fontSize: '1rem',
                fontWeight: 600,
                mt: 1
              }}
              startIcon={loading ? <CircularProgress size={20} /> : null}
            >
              {loading ? 'Signing In...' : 'Login'}
            </Button>

            <ModernCard 
              variant="glass" 
              sx={{ 
                p: 2,
                mt: 2,
                background: (theme) => theme.palette.mode === 'dark' 
                  ? 'rgba(255, 255, 255, 0.05)' 
                  : 'rgba(0, 0, 0, 0.02)'
              }}
            >
              <Typography variant="caption" display="block" sx={{ fontWeight: 600, mb: 1 }}>
                Demo Accounts:
              </Typography>
              <Typography variant="caption" display="block" sx={{ opacity: 0.8 }}>
                admin / admin123 (Owner)
              </Typography>
              <Typography variant="caption" display="block" sx={{ opacity: 0.8 }}>
                manager / manager123 (Manager)
              </Typography>
              <Typography variant="caption" display="block" sx={{ opacity: 0.8 }}>
                estimator / estimator123 (Estimator)
              </Typography>
            </ModernCard>
          </Box>
        </ModernCard>
      </Box>
    </ModernBackground>
  );
}

import React from 'react';
import { Box, useTheme } from '@mui/material';
import { keyframes } from '@emotion/react';

// Animated gradient keyframes
const gradientShift = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`;

const floatingElements = keyframes`
  0% {
    transform: translateY(0px) rotate(0deg);
    opacity: 0.7;
  }
  50% {
    transform: translateY(-20px) rotate(180deg);
    opacity: 1;
  }
  100% {
    transform: translateY(0px) rotate(360deg);
    opacity: 0.7;
  }
`;

const pulseGlow = keyframes`
  0% {
    opacity: 0.4;
    transform: scale(1);
  }
  50% {
    opacity: 0.8;
    transform: scale(1.1);
  }
  100% {
    opacity: 0.4;
    transform: scale(1);
  }
`;

const ModernBackground = ({ children, variant = 'default' }) => {
  const theme = useTheme();
  const isDark = theme.palette.mode === 'dark';

  // Dynamic background variants
  const backgroundVariants = {
    default: {
      background: isDark
        ? `
          linear-gradient(-45deg, 
            rgba(10, 14, 19, 0.95), 
            rgba(26, 31, 46, 0.95), 
            rgba(16, 20, 31, 0.95), 
            rgba(30, 41, 59, 0.95)
          )
        `
        : `
          linear-gradient(-45deg, 
            rgba(248, 250, 252, 0.95), 
            rgba(241, 245, 249, 0.95), 
            rgba(226, 232, 240, 0.95), 
            rgba(203, 213, 225, 0.95)
          )
        `,
      backgroundSize: '400% 400%',
      animation: `${gradientShift} 15s ease infinite`,
    },

    dashboard: {
      background: isDark
        ? `
          radial-gradient(ellipse at top left, rgba(59, 130, 246, 0.1) 0%, transparent 50%),
          radial-gradient(ellipse at top right, rgba(147, 51, 234, 0.1) 0%, transparent 50%),
          radial-gradient(ellipse at bottom left, rgba(16, 185, 129, 0.1) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(10, 14, 19, 0.98) 0%, 
            rgba(26, 31, 46, 0.95) 50%, 
            rgba(16, 20, 31, 0.98) 100%
          )
        `
        : `
          radial-gradient(ellipse at top left, rgba(59, 130, 246, 0.08) 0%, transparent 50%),
          radial-gradient(ellipse at top right, rgba(147, 51, 234, 0.08) 0%, transparent 50%),
          radial-gradient(ellipse at bottom left, rgba(16, 185, 129, 0.08) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(248, 250, 252, 0.98) 0%, 
            rgba(241, 245, 249, 0.95) 50%, 
            rgba(226, 232, 240, 0.98) 100%
          )
        `,
    },

    auth: {
      background: isDark
        ? `
          radial-gradient(circle at 20% 80%, rgba(59, 130, 246, 0.3) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(147, 51, 234, 0.3) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(10, 14, 19, 0.9) 0%, 
            rgba(26, 31, 46, 0.8) 100%
          )
        `
        : `
          radial-gradient(circle at 20% 80%, rgba(59, 130, 246, 0.15) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(147, 51, 234, 0.15) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(248, 250, 252, 0.9) 0%, 
            rgba(226, 232, 240, 0.8) 100%
          )
        `,
      backgroundSize: '400% 400%',
      animation: `${gradientShift} 20s ease infinite`,
    }
  };

  return (
    <Box
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: -2,
        overflow: 'hidden',
        ...backgroundVariants[variant],

        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundImage: isDark
            ? `
              radial-gradient(circle at 25% 25%, rgba(59, 130, 246, 0.1) 0%, transparent 25%),
              radial-gradient(circle at 75% 25%, rgba(147, 51, 234, 0.1) 0%, transparent 25%),
              radial-gradient(circle at 25% 75%, rgba(16, 185, 129, 0.1) 0%, transparent 25%),
              radial-gradient(circle at 75% 75%, rgba(245, 101, 101, 0.1) 0%, transparent 25%)
            `
            : `
              radial-gradient(circle at 25% 25%, rgba(59, 130, 246, 0.05) 0%, transparent 25%),
              radial-gradient(circle at 75% 25%, rgba(147, 51, 234, 0.05) 0%, transparent 25%),
              radial-gradient(circle at 25% 75%, rgba(16, 185, 129, 0.05) 0%, transparent 25%),
              radial-gradient(circle at 75% 75%, rgba(245, 101, 101, 0.05) 0%, transparent 25%)
            `,
          animation: `${pulseGlow} 8s ease-in-out infinite`,
        },

        '&::after': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundImage: `
            url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='${
              isDark ? '%234f46e5' : '%236366f1'
            }' fill-opacity='${isDark ? '0.05' : '0.03'}'%3E%3Ccircle cx='30' cy='30' r='1'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")
          `,
          opacity: isDark ? 0.3 : 0.2,
        }
      }}
    >
      {/* Floating geometric elements */}
      <Box
        sx={{
          position: 'absolute',
          top: '10%',
          left: '10%',
          width: 100,
          height: 100,
          borderRadius: '20px',
          background: isDark 
            ? 'linear-gradient(135deg, rgba(59, 130, 246, 0.1) 0%, rgba(147, 51, 234, 0.1) 100%)'
            : 'linear-gradient(135deg, rgba(59, 130, 246, 0.05) 0%, rgba(147, 51, 234, 0.05) 100%)',
          backdropFilter: 'blur(10px)',
          border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)'}`,
          animation: `${floatingElements} 20s ease-in-out infinite`,
        }}
      />

      <Box
        sx={{
          position: 'absolute',
          top: '60%',
          right: '15%',
          width: 80,
          height: 80,
          borderRadius: '50%',
          background: isDark 
            ? 'linear-gradient(135deg, rgba(16, 185, 129, 0.1) 0%, rgba(5, 150, 105, 0.1) 100%)'
            : 'linear-gradient(135deg, rgba(16, 185, 129, 0.05) 0%, rgba(5, 150, 105, 0.05) 100%)',
          backdropFilter: 'blur(10px)',
          border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)'}`,
          animation: `${floatingElements} 25s ease-in-out infinite reverse`,
        }}
      />

      <Box
        sx={{
          position: 'absolute',
          bottom: '20%',
          left: '20%',
          width: 60,
          height: 60,
          borderRadius: '15px',
          background: isDark 
            ? 'linear-gradient(135deg, rgba(245, 101, 101, 0.1) 0%, rgba(239, 68, 68, 0.1) 100%)'
            : 'linear-gradient(135deg, rgba(245, 101, 101, 0.05) 0%, rgba(239, 68, 68, 0.05) 100%)',
          backdropFilter: 'blur(10px)',
          border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)'}`,
          animation: `${floatingElements} 18s ease-in-out infinite`,
          animationDelay: '5s',
        }}
      />

      {children}
    </Box>
  );
};

export default ModernBackground;

