// Create modern theme
export const createModernTheme = (mode = 'light') => {
  const isDark = mode === 'dark';

  return createTheme({
    palette: {
      mode,
      primary: {
        main: isDark ? colors.primary[400] : colors.primary[600],
        light: colors.primary[300],
        dark: colors.primary[700],
        contrastText: '#ffffff',
      },
      secondary: {
        main: isDark ? colors.secondary[400] : colors.secondary[600],
        light: colors.secondary[300],
        dark: colors.secondary[700],
        contrastText: '#ffffff',
      },
      success: {
        main: isDark ? colors.success[400] : colors.success[600],
        light: colors.success[300],
        dark: colors.success[700],
      },
      warning: {
        main: isDark ? colors.warning[400] : colors.warning[600],
        light: colors.warning[300],
