};

export default ModernBackground;

import React, { useState } from 'react';
import { Button, Box, TextField, Typography, Alert, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { authService } from '../../services/authService';
import ModernBackground from '../../components/Common/ModernBackground';
import ModernCard from '../../components/Common/ModernCard';

export default function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    try {
      setError('');
      setLoading(true);

      if (!username || !password) {
        setError('Please enter both username and password');
        return;
      }

      const response = await authService.login(username, password);

      if (response.success && response.token && response.user) {
        // Store token
        localStorage.setItem('token', response.token);

        // Update auth context
        login(response.user, response.token);

        console.log('Login successful, redirecting to dashboard...');
        navigate('/dashboard');
      } else {
        setError('Invalid username or password');
      }
    } catch (err) {
      console.error('Login error:', err);
      if (err.response?.data?.error) {
        setError(err.response.data.error);
      } else if (err.message === 'Network Error') {
        setError('Cannot connect to server. Please check if the server is running.');
      } else {
        setError('Login failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

  return (
    <ModernBackground variant="auth">
      <Box sx={{
        height: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        p: 2
      }}>
        <ModernCard 
          variant="glass" 
          sx={{ 
            maxWidth: 420, 
            width: '100%',
            p: 2
          }}
        >
          <Box sx={{ p: 4, display: 'flex', flexDirection: 'column', gap: 3 }}>
            <Box sx={{ textAlign: 'center', mb: 2 }}>
              <Typography 
                variant="h3" 
                sx={{ 
                  mb: 1,
                  fontWeight: 700,
                  background: 'linear-gradient(135deg, #3b82f6 0%, #9333ea 100%)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  backgroundClip: 'text'
                }}
              >
                🚗 CollisionOS
              </Typography>
              <Typography variant="h6" sx={{ color: 'text.secondary', fontWeight: 500 }}>
                Auto Body Shop Management
              </Typography>
            </Box>

            {error && (
              <Alert 
                severity="error" 
                sx={{ 
                  borderRadius: 2,
                  backdropFilter: 'blur(10px)'
                }}
              >
                {error}
              </Alert>
            )}

            <TextField 
              label="Username" 
              fullWidth
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="admin"
              variant="outlined"
            />

            <TextField 
              label="Password" 
              type="password" 
              fullWidth
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="admin123"
              variant="outlined"
            />

            <Button 
              variant="contained" 
              onClick={handleLogin}
              disabled={loading}
              size="large"
              fullWidth
              sx={{ 
                py: 1.5,
                fontSize: '1rem',
                fontWeight: 600,
                mt: 1
              }}
              startIcon={loading ? <CircularProgress size={20} /> : null}
            >
              {loading ? 'Signing In...' : 'Login'}
            </Button>

            <ModernCard 
              variant="glass" 
              sx={{ 
                p: 2,
                mt: 2,
                background: (theme) => theme.palette.mode === 'dark' 
                  ? 'rgba(255, 255, 255, 0.05)' 
                  : 'rgba(0, 0, 0, 0.02)'
              }}
            >
              <Typography variant="caption" display="block" sx={{ fontWeight: 600, mb: 1 }}>
                Demo Accounts:
              </Typography>
              <Typography variant="caption" display="block" sx={{ opacity: 0.8 }}>
                admin / admin123 (Owner)
              </Typography>
              <Typography variant="caption" display="block" sx={{ opacity: 0.8 }}>
                manager / manager123 (Manager)
              </Typography>
              <Typography variant="caption" display="block" sx={{ opacity: 0.8 }}>
                estimator / estimator123 (Estimator)
              </Typography>
            </ModernCard>
          </Box>
        </ModernCard>
      </Box>
    </ModernBackground>
  );
}

