toolUseResult.originalFile:
/* Global styles for CollisionOS */

/* Only apply Electron-specific fixes when in Electron */
@media screen and (-webkit-app-region: no-drag) {
  /* Electron-specific fixes */
  body {
    -webkit-app-region: no-drag;
    -webkit-user-select: auto;
    user-select: auto;
  }
}

/* Basic interactive element styles */
button, a, input, select, textarea, [role="button"], [tabindex] {
  cursor: pointer;
}

input, textarea {
  cursor: text;
}

/* Material-UI specific styles */
.MuiButton-root,
.MuiIconButton-root,
.MuiMenuItem-root,
.MuiListItem-root,
.MuiCard-root,
.MuiChip-root {
  cursor: pointer;
}

/* Fix for dialog and modal interactions */
.MuiDialog-root {
  pointer-events: auto;
}

.MuiDialog-container {
  pointer-events: none;
}

.MuiDialog-paper {
  pointer-events: auto;
}

/* Remove any blocking overlays */
.MuiBackdrop-root {
  pointer-events: none;
}

/* Ensure proper z-index for interactive elements */
.MuiButton-root,
.MuiIconButton-root,
.MuiCard-root {
  position: relative;
  z-index: 1;
}

/* Hover effects for better UX */
.MuiCard-root:hover {
  transform: translateY(-2px);
  transition: all 0.2s ease-in-out;
}

.MuiButton-root:hover {
  transform: translateY(-1px);
  transition: all 0.2s ease-in-out;
}

