/* Global styles for CollisionOS - Automotive Business Management Platform */

@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap');

/* Global Typography and Base Styles */
* {
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Helvetica Neue', Arial, sans-serif;
  font-feature-settings: 'cv02', 'cv03', 'cv04', 'cv11';
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Improved focus styles for accessibility */
*:focus-visible {
  outline: 2px solid #1e40af;
  outline-offset: 2px;
  border-radius: 4px;
}

/* Only apply Electron-specific fixes when in Electron */
@media screen and (-webkit-app-region: no-drag) {
  /* Electron-specific fixes */
  body {
    -webkit-app-region: no-drag;
    -webkit-user-select: auto;
    user-select: auto;
  }
}

/* Basic interactive element styles */
button, a, input, select, textarea, [role="button"], [tabindex] {
  cursor: pointer;
}

input, textarea {
  cursor: text;
}

/* Material-UI specific styles */
.MuiButton-root,
.MuiIconButton-root,
.MuiMenuItem-root,
.MuiListItem-root,
.MuiCard-root,
.MuiChip-root {
  cursor: pointer;
}

/* Fix for dialog and modal interactions */
.MuiDialog-root {
  pointer-events: auto;
}

.MuiDialog-container {
  pointer-events: none;
}

.MuiDialog-paper {
  pointer-events: auto;
}

/* Remove any blocking overlays - Allow clicks on backdrop when appropriate */
.MuiBackdrop-root {
  pointer-events: auto;
}

/* Ensure proper z-index for interactive elements */
.MuiButton-root,
.MuiIconButton-root,
.MuiCard-root {
  position: relative;
  z-index: 1;
}

/* Enhanced hover effects for better UX */
.MuiCard-root {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
  position: relative;
  z-index: 1;
}

.MuiCard-root:hover {
  transform: translateY(-2px);
  box-shadow: 0 20px 25px -5px rgba(30, 64, 175, 0.15), 0 10px 10px -5px rgba(30, 64, 175, 0.08);
}

.MuiButton-root {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  text-transform: none;
  font-weight: 600;
}

.MuiButton-root:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 15px -3px rgba(30, 64, 175, 0.2), 0 4px 6px -2px rgba(30, 64, 175, 0.1);
}

/* Improved spacing and readability */
.MuiTypography-h1, .MuiTypography-h2, .MuiTypography-h3 {
  line-height: 1.2;
  letter-spacing: -0.025em;
}

.MuiTypography-body1, .MuiTypography-body2 {
  line-height: 1.6;
}

/* Professional spacing for automotive business context */
.dashboard-container {
  padding: 24px;
  gap: 24px;
}

.stats-grid {
  gap: 20px;
}

/* Accessibility improvements */
.MuiButton-root:focus,
.MuiIconButton-root:focus,
.MuiTextField-root:focus-within {
  box-shadow: 0 0 0 3px rgba(30, 64, 175, 0.12);
}

import React from 'react';
import { Box, useTheme } from '@mui/material';
import { keyframes } from '@emotion/react';

// Animated gradient keyframes
const gradientShift = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`;

const floatingElements = keyframes`
  0% {
    transform: translateY(0px) rotate(0deg);
    opacity: 0.7;
  }
  50% {
    transform: translateY(-20px) rotate(180deg);
    opacity: 1;
  }
  100% {
    transform: translateY(0px) rotate(360deg);
    opacity: 0.7;
  }
`;

const pulseGlow = keyframes`
  0% {
    opacity: 0.4;
    transform: scale(1);
  }
  50% {
    opacity: 0.8;
    transform: scale(1.1);
  }
  100% {
    opacity: 0.4;
    transform: scale(1);
  }
`;

const ModernBackground = ({ children, variant = 'default' }) => {
  const theme = useTheme();
  const isDark = theme.palette.mode === 'dark';

  // Dynamic background variants
  const backgroundVariants = {
    default: {
      background: isDark
        ? `
          linear-gradient(-45deg, 
            rgba(10, 14, 19, 0.95), 
            rgba(26, 31, 46, 0.95), 
            rgba(16, 20, 31, 0.95), 
            rgba(30, 41, 59, 0.95)
          )
        `
        : `
          linear-gradient(-45deg, 
            rgba(248, 250, 252, 0.95), 
            rgba(241, 245, 249, 0.95), 
            rgba(226, 232, 240, 0.95), 
            rgba(203, 213, 225, 0.95)
          )
        `,
      backgroundSize: '400% 400%',
      animation: `${gradientShift} 15s ease infinite`,
    },

    dashboard: {
      background: isDark
        ? `
          radial-gradient(ellipse at top left, rgba(59, 130, 246, 0.1) 0%, transparent 50%),
          radial-gradient(ellipse at top right, rgba(147, 51, 234, 0.1) 0%, transparent 50%),
          radial-gradient(ellipse at bottom left, rgba(16, 185, 129, 0.1) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(10, 14, 19, 0.98) 0%, 
            rgba(26, 31, 46, 0.95) 50%, 
            rgba(16, 20, 31, 0.98) 100%
          )
        `
        : `
          radial-gradient(ellipse at top left, rgba(59, 130, 246, 0.08) 0%, transparent 50%),
          radial-gradient(ellipse at top right, rgba(147, 51, 234, 0.08) 0%, transparent 50%),
          radial-gradient(ellipse at bottom left, rgba(16, 185, 129, 0.08) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(248, 250, 252, 0.98) 0%, 
            rgba(241, 245, 249, 0.95) 50%, 
            rgba(226, 232, 240, 0.98) 100%
          )
        `,
    },

    auth: {
      background: isDark
        ? `
          radial-gradient(circle at 20% 80%, rgba(30, 64, 175, 0.25) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(16, 185, 129, 0.25) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(15, 23, 42, 0.95) 0%, 
            rgba(30, 41, 59, 0.9) 100%
          )
        `
        : `
          radial-gradient(circle at 20% 80%, rgba(30, 64, 175, 0.12) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(16, 185, 129, 0.12) 0%, transparent 50%),
          linear-gradient(135deg, 
            rgba(248, 250, 252, 0.95) 0%, 
            rgba(226, 232, 240, 0.9) 100%
          )
        `,
      backgroundSize: '400% 400%',
      animation: `${gradientShift} 20s ease infinite`,
    }
  };

  return (
    <Box
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: -2,
        overflow: 'hidden',
        ...backgroundVariants[variant],

        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundImage: isDark
            ? `
              radial-gradient(circle at 25% 25%, rgba(59, 130, 246, 0.1) 0%, transparent 25%),
              radial-gradient(circle at 75% 25%, rgba(147, 51, 234, 0.1) 0%, transparent 25%),
              radial-gradient(circle at 25% 75%, rgba(16, 185, 129, 0.1) 0%, transparent 25%),
              radial-gradient(circle at 75% 75%, rgba(245, 101, 101, 0.1) 0%, transparent 25%)
            `
            : `
              radial-gradient(circle at 25% 25%, rgba(59, 130, 246, 0.05) 0%, transparent 25%),
              radial-gradient(circle at 75% 25%, rgba(147, 51, 234, 0.05) 0%, transparent 25%),
              radial-gradient(circle at 25% 75%, rgba(16, 185, 129, 0.05) 0%, transparent 25%),
              radial-gradient(circle at 75% 75%, rgba(245, 101, 101, 0.05) 0%, transparent 25%)
            `,
          animation: `${pulseGlow} 8s ease-in-out infinite`,
        },

        '&::after': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundImage: `
            url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='${
              isDark ? '%234f46e5' : '%236366f1'
            }' fill-opacity='${isDark ? '0.05' : '0.03'}'%3E%3Ccircle cx='30' cy='30' r='1'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")
          `,
          opacity: isDark ? 0.3 : 0.2,
        }
      }}
    >
      {/* Floating geometric elements */}
      <Box
        sx={{
          position: 'absolute',
          top: '10%',
          left: '10%',
          width: 100,
          height: 100,
          borderRadius: '20px',
          background: isDark 
            ? 'linear-gradient(135deg, rgba(30, 64, 175, 0.15) 0%, rgba(16, 185, 129, 0.15) 100%)'
            : 'linear-gradient(135deg, rgba(30, 64, 175, 0.08) 0%, rgba(16, 185, 129, 0.08) 100%)',
          backdropFilter: 'blur(10px)',
          border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)'}`,
          animation: `${floatingElements} 20s ease-in-out infinite`,
        }}
      />

      <Box
        sx={{
          position: 'absolute',
          top: '60%',
          right: '15%',
          width: 80,
          height: 80,
          borderRadius: '50%',
          background: isDark 
            ? 'linear-gradient(135deg, rgba(16, 185, 129, 0.1) 0%, rgba(5, 150, 105, 0.1) 100%)'
            : 'linear-gradient(135deg, rgba(16, 185, 129, 0.05) 0%, rgba(5, 150, 105, 0.05) 100%)',
          backdropFilter: 'blur(10px)',
          border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)'}`,
          animation: `${floatingElements} 25s ease-in-out infinite reverse`,
        }}
      />

      <Box
        sx={{
          position: 'absolute',
          bottom: '20%',
          left: '20%',
          width: 60,
          height: 60,
          borderRadius: '15px',
          background: isDark 
            ? 'linear-gradient(135deg, rgba(245, 101, 101, 0.1) 0%, rgba(239, 68, 68, 0.1) 100%)'
            : 'linear-gradient(135deg, rgba(245, 101, 101, 0.05) 0%, rgba(239, 68, 68, 0.05) 100%)',
          backdropFilter: 'blur(10px)',
          border: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)'}`,
          animation: `${floatingElements} 18s ease-in-out infinite`,
          animationDelay: '5s',
        }}
      />

      {children}
    </Box>
  );
};

export default ModernBackground;

