import React, { useState, useEffect } from 'react';
import {
  Box,
  Grid,
  Card,
  CardContent,
  Typography,
  IconButton,
  Chip,
  LinearProgress,
  Avatar,
  Button,
  Tooltip,
  useTheme,
  useMediaQuery
} from '@mui/material';
import {
  TrendingUp,
  TrendingDown,
  Assignment,
  Person,
  DirectionsCar,
  Build,
  AttachMoney,
  Refresh,
  CheckCircle,
  Timeline,
  Assessment
} from '@mui/icons-material';

// Components
import { KPIChart } from '../../components/Dashboard/KPIChart';
import { ProductionBoardPreview } from '../../components/Dashboard/ProductionBoardPreview';
import { RecentJobs } from '../../components/Dashboard/RecentJobs';
import { AlertsPanel } from '../../components/Dashboard/AlertsPanel';
import { QuickActions } from '../../components/Dashboard/QuickActions';
import { WeatherWidget } from '../../components/Dashboard/WeatherWidget';
import { CalendarWidget } from '../../components/Dashboard/CalendarWidget';

// Hooks
import { useAuth } from '../../hooks/useAuth';

const Dashboard = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const { user } = useAuth();

  const [selectedTimeframe, setSelectedTimeframe] = useState('today');
  const [isLoading, setIsLoading] = useState(false);

  // Mock data
  const dashboardData = {
    kpis: { 
      revenue: 123456, 
      jobsInProgress: 12, 
      avgCycleTime: 6.2, 
      customerSatisfaction: 95 
    },
    productionStats: { 
      total: 24, 
      estimate: 3, 
      inProgress: 12, 
      qualityCheck: 2, 
      readyPickup: 1 
    },
    efficiencyStats: { 
      laborEfficiency: 88, 
      partsEfficiency: 92, 
      bayUtilization: 73 
    }
  };

  const recentJobs = [
    { id: 1, jobNumber: 'JOB-001', customerName: 'John Smith', status: 'estimate' },
    { id: 2, jobNumber: 'JOB-002', customerName: 'Jane Doe', status: 'in_progress' },
    { id: 3, jobNumber: 'JOB-003', customerName: 'Bob Johnson', status: 'ready_pickup' }
  ];

  const alerts = [
    { id: 1, title: 'Parts Overdue', message: '2 parts overdue', type: 'warning' },
    { id: 2, title: 'Job Overdue', message: 'One job overdue', type: 'error' }
  ];

  const handleRefresh = () => {
    console.log('Refresh clicked');
    alert('Refresh button clicked!');
    setIsLoading(true);
    setTimeout(() => setIsLoading(false), 1000);
  };

  const handleTimeframeChange = (timeframe) => {
    console.log('Timeframe changed to:', timeframe);
    alert(`Timeframe changed to: ${timeframe}`);
    setSelectedTimeframe(timeframe);
  };

  const handleViewFullBoard = () => {
    console.log('View Full Board clicked');
    alert('View Full Board clicked!');
  };

  const handleKPIWidgetClick = (title) => {
    console.log(`${title} KPI widget clicked`);
    alert(`${title} KPI widget clicked!`);
  };

  const handleStatusWidgetClick = (title) => {
    console.log(`${title} status widget clicked`);
