import React, { useState, useEffect, useCallback } from 'react';
import {
  Box,
  Grid,
  Card,
  CardContent,
  Typography,
  IconButton,
  Button,
  Chip,
  Avatar,
  Tooltip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Badge,
  Paper,
  useTheme,
  useMediaQuery,
  Fab,
  SpeedDial,
  SpeedDialAction,
  SpeedDialIcon
} from '@mui/material';
import {
  Add,
  FilterList,
  Search,
  ViewList,
  ViewModule,
  Refresh,
  Settings,
  Assignment,
  Person,
  DirectionsCar,
  Build,
  CheckCircle,
  Warning,
  Error,
  Schedule,
  AttachMoney,
  MoreVert,
  Edit,
  Delete,
  Visibility,
  Archive,
  Print,
  Share,
  Download,
  Upload,
  CameraAlt,
  PhotoCamera,
  QrCode,
  Scanner,
  Timeline,
  Assessment,
  PieChart,
  BarChart,
  ShowChart,
  FilterAlt,
  Sort,
  Group,
  ViewColumn,
  Fullscreen,
  FullscreenExit,
  ShoppingCart,
  LocalShipping,
  Brush,
  Tune,
  CleaningServices,
  FormatPaint
} from '@mui/icons-material';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { motion, AnimatePresence } from 'framer-motion';
import { useQuery, useMutation, useQueryClient } from 'react-query';

// Components
import { JobCard } from '../../components/Production/JobCard';
import { JobDetailDialog } from '../../components/Production/JobDetailDialog';
import { FilterPanel } from '../../components/Production/FilterPanel';
import { SearchBar } from '../../components/Common/SearchBar';
import { LoadingSpinner } from '../../components/Common/LoadingSpinner';
// Removed EmptyState (component not present)

// Services
import { jobService } from '../../services/jobService';
import { userService } from '../../services/userService';
import { bayService } from '../../services/bayService';

// Hooks
import { useAuth } from '../../hooks/useAuth';
import { useSocket } from '../../hooks/useSocket';
import { useDebounce } from '../../hooks/useDebounce';

// Utils
import { getStatusColor, getPriorityColor, getStatusIcon } from '../../utils/statusUtils';
import { formatCurrency, formatDate, formatTime } from '../../utils/formatters';

const ProductionBoard = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const { user } = useAuth();
  const { socket, isConnected } = useSocket();
  const queryClient = useQueryClient();

  // State
  const [selectedJob, setSelectedJob] = useState(null);
  const [isDetailDialogOpen, setIsDetailDialogOpen] = useState(false);
  const [isFilterPanelOpen, setIsFilterPanelOpen] = useState(false);
  const [viewMode, setViewMode] = useState('kanban'); // 'kanban' or 'list'
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    status: [],
    priority: [],
    assignedTo: [],
    bay: [],
    jobType: [],
    dateRange: null
  });
  const [sortBy, setSortBy] = useState('priority');
  const [isFullscreen, setIsFullscreen] = useState(false);

  // Debounced search
  const debouncedSearchTerm = useDebounce(searchTerm, 300);

  // Fetch jobs data
  const { data: jobs, isLoading, error, refetch } = useQuery(
    ['jobs', 'production', debouncedSearchTerm, filters, sortBy],
    () => jobService.getJobs({ search: debouncedSearchTerm, filters, sortBy }),
    {
      refetchInterval: 30000, // Refresh every 30 seconds
      staleTime: 10000,
    }
  );

  // Fetch users for assignment
  const { data: users } = useQuery(
    ['users', 'technicians'],
    () => userService.getTechnicians(),
    {
      staleTime: 5 * 60 * 1000, // 5 minutes
    }
  );

  // Fetch bays
  const { data: bays } = useQuery(
    ['bays'],
    () => bayService.getBays(),
    {
      staleTime: 5 * 60 * 1000,
    }
  );

  // Mutations
  const updateJobMutation = useMutation(
    (data) => jobService.updateJob(data.id, data),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['jobs']);
        queryClient.invalidateQueries(['dashboard']);
      },
    }
  );

  const moveJobMutation = useMutation(
    (data) => jobService.moveJob(data.jobId, data.newStatus, data.assignedTo, data.bayId),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['jobs']);
        queryClient.invalidateQueries(['dashboard']);
      },
    }
  );

  // Real-time updates
  useEffect(() => {
    if (!socket || !isConnected) return;

    const handleJobUpdate = (data) => {
      queryClient.invalidateQueries(['jobs']);
      queryClient.invalidateQueries(['dashboard']);
    };

    socket.on('job_update', handleJobUpdate);
    socket.on('production_update', handleJobUpdate);

    return () => {
      socket.off('job_update', handleJobUpdate);
      socket.off('production_update', handleJobUpdate);
    };
  }, [socket, isConnected, queryClient]);

  // Production board columns
  const columns = [
    {
      id: 'estimate',
      title: 'Estimate',
      color: '#FFA500',
      icon: <Assessment />,
      limit: 10
    },
    {
      id: 'intake',
      title: 'Intake',
      color: '#FF6B6B',
      icon: <Assignment />,
      limit: 5
    },
    {
      id: 'blueprint',
      title: 'Blueprint',
      color: '#4ECDC4',
      icon: <Timeline />,
      limit: 8
    },
    {
      id: 'parts_ordering',
      title: 'Parts Ordering',
      color: '#45B7D1',
      icon: <ShoppingCart />,
      limit: 12
    },
    {
      id: 'parts_receiving',
      title: 'Parts Receiving',
      color: '#96CEB4',
      icon: <LocalShipping />,
      limit: 8
    },
    {
      id: 'body_structure',
      title: 'Body/Structure',
      color: '#FFEAA7',
      icon: <Build />,
      limit: 6
    },
    {
      id: 'paint_prep',
      title: 'Paint Prep',
      color: '#DDA0DD',
      icon: <Brush />,
      limit: 4
    },
    {
      id: 'paint_booth',
      title: 'Paint Booth',
      color: '#98D8C8',
      icon: <FormatPaint />,
      limit: 2
    },
    {
      id: 'reassembly',
      title: 'Reassembly',
      color: '#F7DC6F',
      icon: <Settings />,
      limit: 6
    },
    {
      id: 'quality_control',
      title: 'Quality Control',
      color: '#BB8FCE',
      icon: <CheckCircle />,
      limit: 4
    },
    {
      id: 'calibration',
      title: 'Calibration',
      color: '#85C1E9',
      icon: <Tune />,
      limit: 3
    },
    {
      id: 'detail',
      title: 'Detail',
      color: '#F8C471',
      icon: <CleaningServices />,
      limit: 4
    },
    {
      id: 'ready_pickup',
      title: 'Ready for Pickup',
      color: '#82E0AA',
      icon: <DirectionsCar />,
      limit: 8
    },
    {
      id: 'delivered',
      title: 'Delivered',
      color: '#2ECC71',
      icon: <CheckCircle />,
      limit: 20
    }
  ];

  // Group jobs by status
  const groupedJobs = jobs?.reduce((acc, job) => {
    const status = job.status;
    if (!acc[status]) {
      acc[status] = [];
    }
    acc[status].push(job);
    return acc;
  }, {}) || {};

  // Handle drag and drop
  const handleDragEnd = useCallback((result) => {
    if (!result.destination) return;

    const { source, destination, draggableId } = result;
    const jobId = draggableId;
    const newStatus = destination.droppableId;

    // Find the job
    const job = jobs?.find(j => j.id === jobId);
    if (!job) return;

    // Check if move is valid
    const validTransitions = job.canMoveToNextStatus();
    if (!validTransitions.includes(newStatus) && newStatus !== job.status) {
      // Show error message
      return;
    }

    // Update job status
    moveJobMutation.mutate({
      jobId,
      newStatus,
      assignedTo: job.assignedTo,
      bayId: job.bayId
    });
  }, [jobs, moveJobMutation]);

  // Handle job selection
  const handleJobSelect = (job) => {
    setSelectedJob(job);
    setIsDetailDialogOpen(true);
  };

  // Handle job update
  const handleJobUpdate = (jobId, updates) => {
    updateJobMutation.mutate({ id: jobId, ...updates });
  };

  // Handle filters change
  const handleFiltersChange = (newFilters) => {
    setFilters(newFilters);
  };

  // Handle search
  const handleSearch = (term) => {
    setSearchTerm(term);
  };

  // Handle refresh
  const handleRefresh = () => {
    refetch();
  };

  // Handle fullscreen toggle
  const handleFullscreenToggle = () => {
    setIsFullscreen(!isFullscreen);
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  if (error) {
    return (
      <Box sx={{ p: 3 }}>
        <Typography color="error">
          Error loading production board: {error.message}
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ 
      p: isFullscreen ? 0 : 3, 
      pb: isFullscreen ? 0 : 10,
      height: isFullscreen ? '100vh' : 'auto',
      overflow: isFullscreen ? 'hidden' : 'auto'
    }}>
      {/* Header */}
      {!isFullscreen && (
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Box>
            <Typography variant="h4" component="h1" gutterBottom>
              Production Board
            </Typography>
            <Typography variant="body1" color="text.secondary">
              Manage and track job progress through the repair workflow
            </Typography>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <Tooltip title="Refresh">
              <IconButton onClick={handleRefresh} disabled={isLoading}>
                <Refresh />
              </IconButton>
            </Tooltip>
            <Tooltip title="Filters">
              <IconButton 
                onClick={() => setIsFilterPanelOpen(!isFilterPanelOpen)}
                color={isFilterPanelOpen ? 'primary' : 'default'}
              >
                <FilterList />
              </IconButton>
            </Tooltip>
            <Tooltip title="View Mode">
              <IconButton onClick={() => setViewMode(viewMode === 'kanban' ? 'list' : 'kanban')}>
                {viewMode === 'kanban' ? <ViewList /> : <ViewModule />}
              </IconButton>
            </Tooltip>
            <Tooltip title={isFullscreen ? 'Exit Fullscreen' : 'Fullscreen'}>
              <IconButton onClick={handleFullscreenToggle}>
                {isFullscreen ? <FullscreenExit /> : <Fullscreen />}
              </IconButton>
            </Tooltip>
          </Box>
        </Box>
      )}

      {/* Search and Filters */}
      {!isFullscreen && (
        <Box sx={{ mb: 3 }}>
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12} md={6}>
              <SearchBar
                value={searchTerm}
                onChange={handleSearch}
                placeholder="Search jobs by number, customer, or vehicle..."
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <Box sx={{ display: 'flex', gap: 1, justifyContent: 'flex-end' }}>
                <FormControl size="small" sx={{ minWidth: 120 }}>
                  <InputLabel>Sort By</InputLabel>
                  <Select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    label="Sort By"
                  >
                    <MenuItem value="priority">Priority</MenuItem>
                    <MenuItem value="date">Date</MenuItem>
                    <MenuItem value="customer">Customer</MenuItem>
                    <MenuItem value="vehicle">Vehicle</MenuItem>
                  </Select>
                </FormControl>
                <Button
                  variant="outlined"
                  startIcon={<Add />}
                  onClick={() => {/* Navigate to new job */}}
                >
                  New Job
                </Button>
              </Box>
            </Grid>
          </Grid>
        </Box>
      )}

      {/* Filter Panel */}
      {isFilterPanelOpen && !isFullscreen && (
        <Box sx={{ mb: 3 }}>
          <FilterPanel
            filters={filters}
            onFiltersChange={handleFiltersChange}
            users={users}
            bays={bays}
          />
        </Box>
      )}

      {/* Production Board */}
      <DragDropContext onDragEnd={handleDragEnd}>
        <Box sx={{ 
          display: 'flex', 
          gap: 2, 
          overflowX: 'auto',
          pb: 2,
          minHeight: isFullscreen ? 'calc(100vh - 100px)' : '600px'
        }}>
          {columns.map((column) => {
            const columnJobs = groupedJobs[column.id] || [];
            const isOverLimit = columnJobs.length > column.limit;

            return (
              <Droppable key={column.id} droppableId={column.id}>
                {(provided, snapshot) => (
                  <Paper
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    sx={{
                      minWidth: 300,
                      maxWidth: 300,
                      height: 'fit-content',
                      maxHeight: isFullscreen ? 'calc(100vh - 120px)' : '600px',
                      overflow: 'hidden',
                      display: 'flex',
                      flexDirection: 'column',
                      border: snapshot.isDraggingOver ? `2px solid ${column.color}` : '1px solid',
                      borderColor: snapshot.isDraggingOver ? column.color : 'divider',
                      backgroundColor: snapshot.isDraggingOver ? `${column.color}10` : 'background.paper',
                    }}
                  >
                    {/* Column Header */}
                    <Box
                      sx={{
                        p: 2,
                        borderBottom: '1px solid',
                        borderColor: 'divider',
                        backgroundColor: `${column.color}20`,
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'space-between',
                      }}
                    >
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <Avatar
                          sx={{
                            bgcolor: column.color,
                            width: 32,
                            height: 32,
                          }}
                        >
                          {column.icon}
                        </Avatar>
                        <Box>
                          <Typography variant="subtitle1" fontWeight="bold">
                            {column.title}
                          </Typography>
                          <Typography variant="caption" color="text.secondary">
                            {columnJobs.length} / {column.limit}
                          </Typography>
                        </Box>
                      </Box>
                      {isOverLimit && (
                        <Tooltip title="Column is over capacity">
                          <Warning sx={{ color: 'warning.main', fontSize: 20 }} />
                        </Tooltip>
                      )}
                    </Box>

                    {/* Column Content */}
                    <Box
                      sx={{
                        flex: 1,
                        overflowY: 'auto',
                        p: 1,
                        minHeight: 200,
                      }}
                    >
                      <AnimatePresence>
                        {columnJobs.map((job, index) => (
                          <Draggable key={job.id} draggableId={job.id} index={index}>
                            {(provided, snapshot) => (
                              <motion.div
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                exit={{ opacity: 0, y: -20 }}
                                transition={{ duration: 0.2 }}
                                style={{
                                  ...provided.draggableProps.style,
                                  transform: snapshot.isDragging
                                    ? provided.draggableProps.style?.transform
                                    : 'none',
                                }}
                              >
                                <Box sx={{ mb: 1 }}>
                                  <JobCard
                                    job={job}
                                    onClick={() => handleJobSelect(job)}
                                    isDragging={snapshot.isDragging}
                                    onUpdate={handleJobUpdate}
                                    users={users}
                                    bays={bays}
                                  />
                                </Box>
                              </motion.div>
                            )}
                          </Draggable>
                        ))}
                      </AnimatePresence>
                      {provided.placeholder}
                    </Box>
                  </Paper>
                )}
              </Droppable>
            );
          })}
        </Box>
      </DragDropContext>

      {/* Job Detail Dialog */}
      <JobDetailDialog
        open={isDetailDialogOpen}
        job={selectedJob}
        onClose={() => {
          setIsDetailDialogOpen(false);
          setSelectedJob(null);
        }}
        onUpdate={handleJobUpdate}
        users={users}
        bays={bays}
      />

      {/* Speed Dial for Mobile */}
      {isMobile && (
        <SpeedDial
          ariaLabel="Production board actions"
          sx={{ position: 'fixed', bottom: 16, right: 16 }}
          icon={<SpeedDialIcon />}
        >
          <SpeedDialAction
            icon={<Add />}
            tooltipTitle="New Job"
            onClick={() => {/* Navigate to new job */}}
          />
          <SpeedDialAction
            icon={<FilterList />}
            tooltipTitle="Filters"
            onClick={() => setIsFilterPanelOpen(!isFilterPanelOpen)}
          />
          <SpeedDialAction
            icon={<Refresh />}
            tooltipTitle="Refresh"
            onClick={handleRefresh}
          />
          <SpeedDialAction
            icon={<Fullscreen />}
            tooltipTitle="Fullscreen"
            onClick={handleFullscreenToggle}
          />
        </SpeedDial>
      )}
    </Box>
  );
};

