          </Typography>
          <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
            <Button
              variant="contained"
              startIcon={<ShoppingCart />}
              onClick={() => {}}
            >
              Create Purchase Order
            </Button>
            <Button
              variant="outlined"
              startIcon={<LocalShipping />}
              onClick={() => {}}
            >
              Receive Shipment
            </Button>
            <Button
              variant="outlined"
              startIcon={<Inventory />}
              onClick={() => {}}
            >
              Stock Check
            </Button>
            <Button
              variant="outlined"
              startIcon={<Assessment />}
              onClick={() => {}}
            >
              Parts Report
            </Button>
          </Box>
        </CardContent>
      </Card>

      {/* Parts Management System */}
      <PartsManagementSystem />
    </Box>
  );
};

export default PartsManagement;

