    color: '#1976d2',
    checkpoints: [
      'Panel gap uniformity',
      'Surface finish quality',
      'Alignment accuracy',
      'Door fit and function',
      'Hood/trunk alignment',
      'Trim attachment'
    ]
  },
  paint: {
    title: 'Paint & Finish',
    icon: Palette,
    color: '#7b1fa2',
    checkpoints: [
      'Color match verification',
      'Texture consistency',
      'Coverage completeness',
      'Defect inspection',
