// Components
import { KPIChart } from '../../components/Dashboard/KPIChart';
import { ProductionBoardPreview } from '../../components/Dashboard/ProductionBoardPreview';
import { RecentJobs } from '../../components/Dashboard/RecentJobs';
import { AlertsPanel } from '../../components/Dashboard/AlertsPanel';
import { QuickActions } from '../../components/Dashboard/QuickActions';
import { WeatherWidget } from '../../components/Dashboard/WeatherWidget';
import { CalendarWidget } from '../../components/Dashboard/CalendarWidget';

// Hooks
import { useAuth } from '../../hooks/useAuth';

const Dashboard = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const { user } = useAuth();

  const [selectedTimeframe, setSelectedTimeframe] = useState('today');
  const [isLoading, setIsLoading] = useState(false);

  // Mock data
  const dashboardData = {
    kpis: { 
      revenue: 123456, 
      jobsInProgress: 12, 
      avgCycleTime: 6.2, 
      customerSatisfaction: 95 
    },
    productionStats: { 
      total: 24, 
      estimate: 3, 
      inProgress: 12, 
      qualityCheck: 2, 
      readyPickup: 1 
    },
    efficiencyStats: { 
      laborEfficiency: 88, 
      partsEfficiency: 92, 
      bayUtilization: 73 
    }
  };

  const recentJobs = [
    { id: 1, jobNumber: 'JOB-001', customerName: 'John Smith', status: 'estimate' },
    { id: 2, jobNumber: 'JOB-002', customerName: 'Jane Doe', status: 'in_progress' },
    { id: 3, jobNumber: 'JOB-003', customerName: 'Bob Johnson', status: 'ready_pickup' }
  ];

  const alerts = [
    { id: 1, title: 'Parts Overdue', message: '2 parts overdue', type: 'warning' },
    { id: 2, title: 'Job Overdue', message: 'One job overdue', type: 'error' }
  ];

  const handleRefresh = () => {
    console.log('Refresh clicked');
    alert('Refresh button clicked!');
    setIsLoading(true);
    setTimeout(() => setIsLoading(false), 1000);
  };

  const handleTimeframeChange = (timeframe) => {
    console.log('Timeframe changed to:', timeframe);
    alert(`Timeframe changed to: ${timeframe}`);
    setSelectedTimeframe(timeframe);
  };

  const handleViewFullBoard = () => {
    console.log('View Full Board clicked');
    alert('View Full Board clicked!');
  };

  const handleKPIWidgetClick = (title) => {
    console.log(`${title} KPI widget clicked`);
    alert(`${title} KPI widget clicked!`);
  };

  const handleStatusWidgetClick = (title) => {
    console.log(`${title} status widget clicked`);
    alert(`${title} status widget clicked!`);
  };

  const handleChartClick = (type) => {
    console.log(`${type} chart clicked`);
    alert(`${type} chart clicked!`);
  };

  const KPIWidget = ({ title, value, change, icon, color, format = 'number' }) => (
    <Card 
      sx={{ 
        height: '100%', 
        position: 'relative', 
        overflow: 'visible',
        cursor: 'pointer',
        '&:hover': {
          boxShadow: 4,
          transform: 'translateY(-2px)',
          transition: 'all 0.2s ease-in-out'
        }
      }}
      onClick={() => handleKPIWidgetClick(title)}
    >
      <CardContent>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
          <Box>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              {title}
            </Typography>
            <Typography variant="h4" component="div" sx={{ fontWeight: 'bold' }}>
              {format === 'currency' ? `$${value.toLocaleString()}` : value}
            </Typography>
            {change !== undefined && (
              <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                {change >= 0 ? (
                  <TrendingUp sx={{ color: 'success.main', fontSize: 16, mr: 0.5 }} />
                ) : (
                  <TrendingDown sx={{ color: 'error.main', fontSize: 16, mr: 0.5 }} />
                )}
                <Typography
                  variant="body2"
                  color={change >= 0 ? 'success.main' : 'error.main'}
                >
                  {Math.abs(change)}% from last period
                </Typography>
              </Box>
            )}
          </Box>
          <Avatar
            sx={{
              bgcolor: color,
              width: 56,
              height: 56,
              position: 'absolute',
              top: -20,
              right: 16,
            }}
          >
            {icon}
          </Avatar>
        </Box>
      </CardContent>
    </Card>
  );

  const StatusWidget = ({ title, count, total, color, icon }) => (
    <Card 
      sx={{ 
        height: '100%',
        cursor: 'pointer',
        '&:hover': {
          boxShadow: 4,
          transform: 'translateY(-2px)',
          transition: 'all 0.2s ease-in-out'
        }
      }}
      onClick={() => handleStatusWidgetClick(title)}
    >
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Avatar sx={{ bgcolor: color, mr: 2 }}>
            {icon}
          </Avatar>
          <Typography variant="h6">{title}</Typography>
        </Box>
        <Typography variant="h3" component="div" sx={{ fontWeight: 'bold', mb: 1 }}>
          {count}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          of {total} total
        </Typography>
        <LinearProgress
          variant="determinate"
          value={(count / total) * 100}
          sx={{ mt: 2, height: 8, borderRadius: 4 }}
        />
      </CardContent>
    </Card>
  );

  return (
    <Box sx={{ p: 3, pb: 10 }}>
      {/* Header */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Box>
          <Typography variant="h4" component="h1" gutterBottom>
            Dashboard
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Welcome back, {user?.firstName || 'User'}! Here's what's happening today.
          </Typography>
        </Box>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Tooltip title="Refresh">
            <IconButton onClick={handleRefresh} disabled={isLoading}>
              <Refresh />
            </IconButton>
          </Tooltip>
          <Chip
            icon={<CheckCircle />}
            label="Connected"
            color="success"
            size="small"
            onClick={() => alert('Connection status clicked!')}
            sx={{ cursor: 'pointer' }}
          />
        </Box>
      </Box>

      {/* Timeframe Selector */}
      <Box sx={{ mb: 3 }}>
        <Box sx={{ display: 'flex', gap: 1 }}>
          {['today', 'week', 'month', 'quarter'].map((timeframe) => (
            <Button
              key={timeframe}
              variant={selectedTimeframe === timeframe ? 'contained' : 'outlined'}
              size="small"
              onClick={() => handleTimeframeChange(timeframe)}
            >
              {timeframe.charAt(0).toUpperCase() + timeframe.slice(1)}
            </Button>
          ))}
        </Box>
      </Box>

      {/* KPI Cards */}
      <Grid container spacing={3} sx={{ mb: 3 }}>
        <Grid item xs={12} sm={6} md={3}>
          <KPIWidget
            title="Revenue"
            value={dashboardData.kpis.revenue}
            change={5.2}
            icon={<AttachMoney />}
            color={theme.palette.primary.main}
            format="currency"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <KPIWidget
            title="Jobs in Progress"
            value={dashboardData.kpis.jobsInProgress}
            change={-2.1}
            icon={<Assignment />}
            color={theme.palette.secondary.main}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <KPIWidget
            title="Average Cycle Time"
            value={dashboardData.kpis.avgCycleTime}
            change={-1.5}
            icon={<Timeline />}
            color={theme.palette.success.main}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <KPIWidget
            title="Customer Satisfaction"
            value={dashboardData.kpis.customerSatisfaction}
            change={2.3}
            icon={<Person />}
            color={theme.palette.warning.main}
            format="percentage"
          />
        </Grid>
      </Grid>

      {/* Main Content Grid */}
      <Grid container spacing={3}>
        {/* Left Column */}
        <Grid item xs={12} lg={8}>
          {/* Production Status */}
          <Grid container spacing={3} sx={{ mb: 3 }}>
            <Grid item xs={12} sm={6} md={3}>
              <StatusWidget
                title="Estimate"
                count={dashboardData.productionStats.estimate}
                total={dashboardData.productionStats.total}
                color="#FFA500"
                icon={<Assessment />}
              />
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <StatusWidget
                title="In Progress"
                count={dashboardData.productionStats.inProgress}
                total={dashboardData.productionStats.total}
                color="#1976d2"
                icon={<Build />}
              />
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <StatusWidget
                title="Quality Check"
                count={dashboardData.productionStats.qualityCheck}
                total={dashboardData.productionStats.total}
                color="#9c27b0"
                icon={<CheckCircle />}
              />
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <StatusWidget
                title="Ready for Pickup"
                count={dashboardData.productionStats.readyPickup}
                total={dashboardData.productionStats.total}
                color="#2e7d32"
                icon={<DirectionsCar />}
              />
            </Grid>
          </Grid>

          {/* Charts Row */}
          <Grid container spacing={3} sx={{ mb: 3 }}>
            <Grid item xs={12} md={6}>
              <Card 
                sx={{ 
                  cursor: 'pointer',
                  '&:hover': {
                    boxShadow: 4,
                    transform: 'translateY(-2px)',
                    transition: 'all 0.2s ease-in-out'
                  }
                }}
                onClick={() => handleChartClick('Revenue Trend')}
              >
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Revenue Trend
                  </Typography>
                  <KPIChart
                    data={[
                      { label: 'Jan', value: 45000 },
                      { label: 'Feb', value: 52000 },
                      { label: 'Mar', value: 48000 },
                      { label: 'Apr', value: 61000 },
                      { label: 'May', value: 58000 },
                      { label: 'Jun', value: 67000 }
                    ]}
                    type="line"
                    height={200}
                    title="Monthly Revenue"
                    currency={true}
                    animated={true}
                    gradient={true}
                  />
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} md={6}>
              <Card 
                sx={{ 
                  cursor: 'pointer',
                  '&:hover': {
                    boxShadow: 4,
                    transform: 'translateY(-2px)',
                    transition: 'all 0.2s ease-in-out'
                  }
                }}
                onClick={() => handleChartClick('Job Status Distribution')}
              >
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Job Status Distribution
                  </Typography>
                  <KPIChart
                    data={[
                      { label: 'Estimate', value: 3 },
                      { label: 'In Progress', value: 12 },
                      { label: 'Quality Check', value: 2 },
                      { label: 'Ready', value: 1 }
                    ]}
                    type="pie"
                    height={200}
                  />
                </CardContent>
              </Card>
            </Grid>
          </Grid>

          {/* Production Board Preview */}
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                <Typography variant="h6">Production Board</Typography>
                <Button 
                  size="small" 
                  variant="outlined"
                  onClick={handleViewFullBoard}
                >
                  View Full Board
                </Button>
              </Box>
              <ProductionBoardPreview jobs={recentJobs} />
            </CardContent>
          </Card>
        </Grid>

        {/* Right Column */}
        <Grid item xs={12} lg={4}>
          {/* Quick Actions */}
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Quick Actions
              </Typography>
              <QuickActions />
            </CardContent>
          </Card>

          {/* Alerts */}
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Alerts
              </Typography>
              <AlertsPanel alerts={alerts} />
            </CardContent>
          </Card>

          {/* Recent Jobs */}
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Recent Jobs
              </Typography>
              <RecentJobs jobs={recentJobs} />
            </CardContent>
          </Card>

          {/* Efficiency Metrics */}
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Efficiency Metrics
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Typography variant="body2">Labor Efficiency</Typography>
                  <Typography variant="body2">{dashboardData.efficiencyStats.laborEfficiency}%</Typography>
                </Box>
                <LinearProgress
                  variant="determinate"
                  value={dashboardData.efficiencyStats.laborEfficiency}
                  sx={{ height: 8, borderRadius: 4 }}
                />
              </Box>
              <Box sx={{ mb: 2 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Typography variant="body2">Parts Efficiency</Typography>
                  <Typography variant="body2">{dashboardData.efficiencyStats.partsEfficiency}%</Typography>
                </Box>
                <LinearProgress
                  variant="determinate"
                  value={dashboardData.efficiencyStats.partsEfficiency}
                  sx={{ height: 8, borderRadius: 4 }}
                />
              </Box>
              <Box>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Typography variant="body2">Bay Utilization</Typography>
                  <Typography variant="body2">{dashboardData.efficiencyStats.bayUtilization}%</Typography>
                </Box>
                <LinearProgress
                  variant="determinate"
                  value={dashboardData.efficiencyStats.bayUtilization}
                  sx={{ height: 8, borderRadius: 4 }}
                />
              </Box>
            </CardContent>
          </Card>

          {/* Weather Widget */}
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <WeatherWidget />
            </CardContent>
          </Card>

          {/* Calendar Widget */}
          <Card>
            <CardContent>
              <CalendarWidget />
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;

