import React, { useState, useEffect } from 'react';
import {
  Box,
  Grid,
  Card,
  CardContent,
  Typography,
  IconButton,
  Chip,
  LinearProgress,
  Avatar,
  Button,
  Tooltip,
  useTheme,
  useMediaQuery
} from '@mui/material';
import {
  TrendingUp,
  TrendingDown,
  Assignment,
  Person,
  DirectionsCar,
  Build,
  AttachMoney,
  Refresh,
  CheckCircle,
  Timeline,
  Assessment
} from '@mui/icons-material';

// Components
import { KPIChart } from '../../components/Dashboard/KPIChart';
import { ProductionBoardPreview } from '../../components/Dashboard/ProductionBoardPreview';
import { RecentJobs } from '../../components/Dashboard/RecentJobs';
import { AlertsPanel } from '../../components/Dashboard/AlertsPanel';
import { QuickActions } from '../../components/Dashboard/QuickActions';
import { WeatherWidget } from '../../components/Dashboard/WeatherWidget';
import { CalendarWidget } from '../../components/Dashboard/CalendarWidget';

// Hooks
import { useAuth } from '../../hooks/useAuth';

const Dashboard = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const { user } = useAuth();

  const [selectedTimeframe, setSelectedTimeframe] = useState('today');
  const [isLoading, setIsLoading] = useState(false);

  // Mock data
  const dashboardData = {
    kpis: { 
      revenue: 123456, 
      jobsInProgress: 12, 
      avgCycleTime: 6.2, 
      customerSatisfaction: 95 
    },
    productionStats: { 
      total: 24, 
      estimate: 3, 
      inProgress: 12, 
      qualityCheck: 2, 
      readyPickup: 1 
    },
    efficiencyStats: { 
      laborEfficiency: 88, 
      partsEfficiency: 92, 
      bayUtilization: 73 
    }
  };

  const recentJobs = [
    { id: 1, jobNumber: 'JOB-001', customerName: 'John Smith', status: 'estimate' },
    { id: 2, jobNumber: 'JOB-002', customerName: 'Jane Doe', status: 'in_progress' },
    { id: 3, jobNumber: 'JOB-003', customerName: 'Bob Johnson', status: 'ready_pickup' }
  ];

  const alerts = [
    { id: 1, title: 'Parts Overdue', message: '2 parts overdue', type: 'warning' },
    { id: 2, title: 'Job Overdue', message: 'One job overdue', type: 'error' }
  ];

  const handleRefresh = () => {
    console.log('Refresh clicked');
    alert('Refresh button clicked!');
    setIsLoading(true);
    setTimeout(() => setIsLoading(false), 1000);
  };

  const handleTimeframeChange = (timeframe) => {
    console.log('Timeframe changed to:', timeframe);
    alert(`Timeframe changed to: ${timeframe}`);
    setSelectedTimeframe(timeframe);
  };

  const handleViewFullBoard = () => {
    console.log('View Full Board clicked');
    alert('View Full Board clicked!');
  };

  const handleKPIWidgetClick = (title) => {
    console.log(`${title} KPI widget clicked`);
    alert(`${title} KPI widget clicked!`);
  };

  const handleStatusWidgetClick = (title) => {
    console.log(`${title} status widget clicked`);
    alert(`${title} status widget clicked!`);
  };

  const handleChartClick = (type) => {
    console.log(`${type} chart clicked`);
    alert(`${type} chart clicked!`);
  };

  const KPIWidget = ({ title, value, change, icon, color, format = 'number' }) => (
    <Card 
      sx={{ 
        height: '100%', 
        position: 'relative', 
        overflow: 'visible',
        cursor: 'pointer',
        '&:hover': {
          boxShadow: 4,
          transform: 'translateY(-2px)',
          transition: 'all 0.2s ease-in-out'
        }
      }}
      onClick={() => handleKPIWidgetClick(title)}
    >
      <CardContent>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
          <Box>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              {title}
            </Typography>
            <Typography variant="h4" component="div" sx={{ fontWeight: 'bold' }}>
              {format === 'currency' ? `$${value.toLocaleString()}` : value}
            </Typography>
            {change !== undefined && (
              <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                {change >= 0 ? (
                  <TrendingUp sx={{ color: 'success.main', fontSize: 16, mr: 0.5 }} />
                ) : (
                  <TrendingDown sx={{ color: 'error.main', fontSize: 16, mr: 0.5 }} />
                )}
                <Typography
                  variant="body2"
                  color={change >= 0 ? 'success.main' : 'error.main'}
                >
                  {Math.abs(change)}% from last period
                </Typography>
              </Box>
            )}
          </Box>
          <Avatar
            sx={{
              bgcolor: color,
              width: 56,
              height: 56,
              position: 'absolute',
              top: -20,
              right: 16,
            }}
          >
            {icon}
          </Avatar>
        </Box>
      </CardContent>
    </Card>
  );

  const StatusWidget = ({ title, count, total, color, icon }) => (
    <Card 
      sx={{ 
        height: '100%',
        cursor: 'pointer',
        '&:hover': {
          boxShadow: 4,
          transform: 'translateY(-2px)',
          transition: 'all 0.2s ease-in-out'
        }
      }}
      onClick={() => handleStatusWidgetClick(title)}
    >
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Avatar sx={{ bgcolor: color, mr: 2 }}>
            {icon}
          </Avatar>
          <Typography variant="h6">{title}</Typography>
        </Box>
        <Typography variant="h3" component="div" sx={{ fontWeight: 'bold', mb: 1 }}>
          {count}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          of {total} total
        </Typography>
        <LinearProgress
          variant="determinate"
          value={(count / total) * 100}
          sx={{ mt: 2, height: 8, borderRadius: 4 }}
        />
      </CardContent>
    </Card>
  );

  return (
    <Box sx={{ p: 3, pb: 10 }}>
      {/* Header */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Box>
          <Typography variant="h4" component="h1" gutterBottom>
            Dashboard
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Welcome back, {user?.firstName || 'User'}! Here's what's happening today.
