const { chromium } = require('playwright');

async function testLogin() {
  console.log('ğŸš— Testing CollisionOS Login Page...\n');

  const browser = await chromium.launch({ 
    headless: false, // Show the browser so you can see what's happening
    slowMo: 1000 // Slow down actions so you can see them
  });

  try {
    const page = await browser.newPage();

    // Test 1: Check if React app loads
    console.log('ğŸ“± Testing React app at http://localhost:3000...');
    await page.goto('http://localhost:3000');

    // Wait for the page to load
    await page.waitForTimeout(3000);

    // Check if login form elements are present
    const usernameField = await page.locator('input[placeholder="admin"]').count();
    const passwordField = await page.locator('input[type="password"]').count();
    const loginButton = await page.locator('button:has-text("Login")').count();

    console.log(`âœ… Username field found: ${usernameField > 0}`);
    console.log(`âœ… Password field found: ${passwordField > 0}`);
    console.log(`âœ… Login button found: ${loginButton > 0}`);

    if (usernameField === 0 || passwordField === 0 || loginButton === 0) {
      console.log('âŒ Login form elements not found!');
      return;
    }

    // Test 2: Check if elements are clickable
    console.log('\nğŸ–±ï¸ Testing if elements are clickable...');

    // Try to click the username field
    try {
      await page.click('input[placeholder="admin"]');
      console.log('âœ… Username field is clickable');
    } catch (error) {
      console.log('âŒ Username field is NOT clickable:', error.message);
    }

    // Try to click the password field
    try {
      await page.click('input[type="password"]');
      console.log('âœ… Password field is clickable');
    } catch (error) {
      console.log('âŒ Password field is NOT clickable:', error.message);
    }

    // Test 3: Try to enter credentials
    console.log('\nâŒ¨ï¸ Testing credential entry...');

    try {
      await page.fill('input[placeholder="admin"]', 'admin');
      console.log('âœ… Username entered successfully');
    } catch (error) {
      console.log('âŒ Could not enter username:', error.message);
    }

    try {
      await page.fill('input[type="password"]', 'admin123');
      console.log('âœ… Password entered successfully');
    } catch (error) {
      console.log('âŒ Could not enter password:', error.message);
    }

    // Test 4: Try to click login button
    console.log('\nğŸ”˜ Testing login button...');

    try {
      await page.click('button:has-text("Login")');
      console.log('âœ… Login button clicked successfully');

      // Wait a moment to see if login works
      await page.waitForTimeout(2000);

      // Check if we're redirected to dashboard
      const currentUrl = page.url();
      console.log(`ğŸ“ Current URL: ${currentUrl}`);

      if (currentUrl.includes('/dashboard') || currentUrl.includes('/app')) {
        console.log('âœ… Login successful! Redirected to dashboard');
      } else {
        console.log('âš ï¸ Login may have worked but no redirect detected');
      }

    } catch (error) {
      console.log('âŒ Could not click login button:', error.message);
    }

    // Test 5: Check for any error messages
    console.log('\nğŸ” Checking for error messages...');

    const errorElements = await page.locator('[role="alert"], .MuiAlert-root, .error').count();
    if (errorElements > 0) {
      const errorText = await page.locator('[role="alert"], .MuiAlert-root, .error').first().textContent();
      console.log(`âš ï¸ Error message found: ${errorText}`);
    } else {
      console.log('âœ… No error messages found');
    }

    // Test 6: Take a screenshot for debugging
    console.log('\nğŸ“¸ Taking screenshot for debugging...');
    await page.screenshot({ path: 'login-test-screenshot.png', fullPage: true });
    console.log('âœ… Screenshot saved as login-test-screenshot.png');

    // Keep browser open for manual inspection
    console.log('\nğŸ” Browser will stay open for 10 seconds for manual inspection...');
    await page.waitForTimeout(10000);

  } catch (error) {
    console.log('âŒ Test failed:', error.message);
  } finally {
    await browser.close();
    console.log('\nğŸ Test completed!');
  }
}

// Run the test
testLogin().catch(console.error);

