const { chromium } = require('playwright');

async function testCustomerManagement() {
  console.log('ğŸš— Testing CollisionOS Customer Management System...\n');

  const browser = await chromium.launch({ 
    headless: false, // Show the browser so you can see what's happening
    slowMo: 500 // Slow down actions so you can see them
  });

  try {
    const page = await browser.newPage();

    // Test 1: Navigate to application
    console.log('ğŸ“± Loading CollisionOS application...');
    await page.goto('http://localhost:3000');
    await page.waitForTimeout(2000);

    // Test 2: Login with admin credentials
    console.log('ğŸ” Logging in as admin...');

    // Check if we're on login page or already logged in
    const loginForm = await page.locator('input[type="email"], input[placeholder*="admin"], input[name="username"]').count();

    if (loginForm > 0) {
      console.log('ğŸ“ Login form detected, entering credentials...');

      // Find and fill email/username field
      const emailField = await page.locator('input[type="email"], input[placeholder*="admin"], input[name="username"]').first();
      await emailField.fill('admin');

      // Find and fill password field
      const passwordField = await page.locator('input[type="password"]');
      await passwordField.fill('admin123');

      // Click login button
      const loginButton = await page.locator('button:has-text("Login"), button[type="submit"]');
      await loginButton.click();

      // Wait for login to complete
      await page.waitForTimeout(3000);
      console.log('âœ… Login attempt completed');
    } else {
      console.log('â„¹ï¸ Already logged in or login form not found');
    }

    // Test 3: Navigate to Customer Management
    console.log('\nğŸ§‘â€ğŸ’¼ Navigating to Customer Management...');

    // Try different ways to get to customers page
    const currentUrl = page.url();

    if (!currentUrl.includes('/customers')) {
      // Try direct navigation
      await page.goto('http://localhost:3000/customers');
      await page.waitForTimeout(2000);

      // Check if navigation menu exists and click customers
      const navMenu = await page.locator('nav, [role="navigation"]').count();
      if (navMenu > 0) {
        const customersLink = await page.locator('a:has-text("Customer"), button:has-text("Customer")').count();
        if (customersLink > 0) {
          await page.locator('a:has-text("Customer"), button:has-text("Customer")').first().click();
          await page.waitForTimeout(1000);
        }
      }
    }

    console.log(`ğŸ“ Current URL: ${page.url()}`);

    // Test 4: Check Customer List Page
    console.log('\nğŸ“‹ Testing Customer List Page...');

    // Look for customer management elements
    const pageTitle = await page.locator('h1, h2, h3, h4').filter({ hasText: /customer/i }).count();
    const customerTable = await page.locator('table, [role="table"]').count();
    const addButton = await page.locator('button:has-text("Add Customer"), button:has-text("Add")').count();

    console.log(`âœ… Customer page title found: ${pageTitle > 0}`);
    console.log(`âœ… Customer table found: ${customerTable > 0}`);
    console.log(`âœ… Add Customer button found: ${addButton > 0}`);

    // Test 5: Check for sample customers
    console.log('\nğŸ‘¥ Checking for sample customers...');

    const customerRows = await page.locator('tr, [role="row"]').count();
    const customerNames = await page.locator('td, [role="cell"]').filter({ hasText: /Alice|Bob|Carol|Johnson|Smith|Davis/i }).count();

    console.log(`ğŸ“Š Total rows found: ${customerRows}`);
    console.log(`ğŸ‘¤ Customer names found: ${customerNames}`);

    if (customerNames > 0) {
      console.log('âœ… Sample customers are visible');
    } else {
      console.log('âš ï¸ No sample customers found - checking if data loaded...');
    }

    // Test 6: Test Search Functionality
    console.log('\nğŸ” Testing search functionality...');

    const searchField = await page.locator('input[placeholder*="search" i], input[type="search"]').count();

    if (searchField > 0) {
      console.log('ğŸ“ Search field found, testing search...');
      await page.locator('input[placeholder*="search" i], input[type="search"]').first().fill('Alice');
      await page.waitForTimeout(1000);

      const searchResults = await page.locator('td, [role="cell"]').filter({ hasText: /Alice/i }).count();
      console.log(`ğŸ” Search results for "Alice": ${searchResults} matches`);

      // Clear search
      await page.locator('input[placeholder*="search" i], input[type="search"]').first().fill('');
      await page.waitForTimeout(500);
    } else {
      console.log('âš ï¸ Search field not found');
    }

    // Test 7: Test Add Customer Dialog
    console.log('\nâ• Testing Add Customer functionality...');

    if (addButton > 0) {
      await page.locator('button:has-text("Add Customer"), button:has-text("Add")').first().click();
      await page.waitForTimeout(1000);

      // Check if dialog/form opened
      const dialog = await page.locator('[role="dialog"], .MuiDialog-root').count();
      const form = await page.locator('form').count();
      const firstNameField = await page.locator('input[name="firstName"], input[label*="First Name" i]').count();

      console.log(`ğŸ“ Dialog opened: ${dialog > 0}`);
      console.log(`ğŸ“‹ Form found: ${form > 0}`);
      console.log(`ğŸ‘¤ First name field found: ${firstNameField > 0}`);

      if (dialog > 0 || form > 0) {
        console.log('âœ… Add Customer dialog opened successfully');

        // Test form fields
        if (firstNameField > 0) {
          console.log('ğŸ“ Testing form input...');
          await page.locator('input[name="firstName"], input[label*="First Name" i]').first().fill('Test User');
          await page.waitForTimeout(500);
          console.log('âœ… Form input working');
        }

        // Close dialog
        const cancelButton = await page.locator('button:has-text("Cancel"), button:has-text("Close")').count();
        if (cancelButton > 0) {
          await page.locator('button:has-text("Cancel"), button:has-text("Close")').first().click();
          await page.waitForTimeout(500);
          console.log('âœ… Dialog closed successfully');
        }
      }
    }

    // Test 8: Test Customer Detail View
    console.log('\nğŸ‘ï¸ Testing customer detail view...');

    const viewButton = await page.locator('button[title*="View"], button:has([data-testid="VisibilityIcon"]), td button').count();

    if (viewButton > 0) {
      await page.locator('button[title*="View"], button:has([data-testid="VisibilityIcon"]), td button').first().click();
      await page.waitForTimeout(1000);

      const detailDialog = await page.locator('[role="dialog"], .MuiDialog-root').count();
      if (detailDialog > 0) {
        console.log('âœ… Customer detail dialog opened');

        // Check tabs
        const tabs = await page.locator('[role="tab"], .MuiTab-root').count();
        console.log(`ğŸ“‘ Tabs found: ${tabs}`);

        // Close detail dialog
        const closeButton = await page.locator('button:has-text("Close"), button[aria-label*="close" i]').count();
        if (closeButton > 0) {
          await page.locator('button:has-text("Close"), button[aria-label*="close" i]').first().click();
          await page.waitForTimeout(500);
        }
      }
    } else {
      console.log('âš ï¸ View button not found');
    }

    // Test 9: Check filters
    console.log('\nğŸ›ï¸ Testing filters...');

    const filterDropdowns = await page.locator('select, [role="combobox"]').count();
    console.log(`ğŸšï¸ Filter dropdowns found: ${filterDropdowns}`);

    if (filterDropdowns > 0) {
      console.log('âœ… Filters are available');
    }

    // Test 10: Take final screenshot
    console.log('\nğŸ“¸ Taking final screenshot...');
    await page.screenshot({ path: 'customer-management-test.png', fullPage: true });
    console.log('âœ… Screenshot saved as customer-management-test.png');

    // Test Summary
    console.log('\nğŸ“Š TEST SUMMARY:');
    console.log('================');
    console.log(`âœ… Application loaded: âœ“`);
    console.log(`âœ… Login functionality: âœ“`);
    console.log(`âœ… Customer page accessible: ${pageTitle > 0 ? 'âœ“' : 'âœ—'}`);
    console.log(`âœ… Customer list displayed: ${customerTable > 0 ? 'âœ“' : 'âœ—'}`);
    console.log(`âœ… Add customer functionality: ${addButton > 0 ? 'âœ“' : 'âœ—'}`);
    console.log(`âœ… Search functionality: ${searchField > 0 ? 'âœ“' : 'âœ—'}`);
    console.log(`âœ… Customer data visible: ${customerNames > 0 ? 'âœ“' : 'âœ—'}`);
    console.log(`âœ… Filters available: ${filterDropdowns > 0 ? 'âœ“' : 'âœ—'}`);

    // Keep browser open for manual inspection
    console.log('\nğŸ” Browser will stay open for 15 seconds for manual inspection...');
    await page.waitForTimeout(15000);

  } catch (error) {
    console.log('âŒ Test failed:', error.message);
    console.log('ğŸ” Error details:', error);
  } finally {
    await browser.close();
    console.log('\nğŸ Customer Management test completed!');
  }
}

// Run the test
testCustomerManagement().catch(console.error);

