const { chromium } = require('playwright');

async function quickTest() {
  console.log('ğŸš— Quick Test: Checking if login page loads...\n');

  const browser = await chromium.launch({ headless: false });

  try {
    const page = await browser.newPage();

    // Navigate to the login page
    console.log('ğŸ“± Loading http://localhost:3000...');
    await page.goto('http://localhost:3000');

    // Wait for page to load
    await page.waitForTimeout(3000);

    // Check page title
    const title = await page.title();
    console.log(`ğŸ“„ Page title: ${title}`);

    // Check if we can find the login form
    const loginForm = await page.locator('form, .login-container, [data-testid="login-form"]').count();
    console.log(`ğŸ“ Login form found: ${loginForm > 0}`);

    // Check for username field
    const usernameField = await page.locator('input[type="text"], input[name="username"], input[placeholder*="admin"]').count();
    console.log(`ğŸ‘¤ Username field found: ${usernameField > 0}`);

    // Check for password field
    const passwordField = await page.locator('input[type="password"], input[name="password"]').count();
    console.log(`ğŸ”’ Password field found: ${passwordField > 0}`);

    // Check for login button
    const loginButton = await page.locator('button:has-text("Login"), button[type="submit"]').count();
    console.log(`ğŸ”˜ Login button found: ${loginButton > 0}`);

    // Try to take a screenshot
    try {
      await page.screenshot({ path: 'quick-test-screenshot.png' });
      console.log('ğŸ“¸ Screenshot saved as quick-test-screenshot.png');
    } catch (error) {
      console.log('âŒ Could not take screenshot:', error.message);
    }

    // Check for any error messages or console errors
    const errors = [];
    page.on('console', msg => {
      if (msg.type() === 'error') {
        errors.push(msg.text());
      }
    });

    await page.waitForTimeout(2000);

    if (errors.length > 0) {
      console.log('âš ï¸ Console errors found:');
      errors.forEach(error => console.log(`   - ${error}`));
    } else {
      console.log('âœ… No console errors found');
    }

    // Summary
