    await page.setViewportSize({ width: 375, height: 667 });
    await page.waitForTimeout(1000);

    // Look for speed dial (floating action button)
    const speedDial = await page.locator('[role="button"]').filter({ hasText: /fab/i }).count();
    const floatingButton = await page.locator('button').filter({ has: page.locator('[data-testid="SpeedDialIcon"]') }).count();

    console.log(`ğŸ“± Mobile speed dial found: ${speedDial > 0 || floatingButton > 0}`);
  });

  test('should handle drag and drop simulation', async ({ page }) => {
    // Wait for board to fully load
    await page.waitForTimeout(3000);

    // Find first job card
    const firstJobCard = page.locator('.MuiCard-root').first();
    const cardExists = await firstJobCard.count() > 0;

    if (cardExists) {
      // Get the card's bounding box
      const cardBox = await firstJobCard.boundingBox();

      if (cardBox) {
        // Simulate drag start
        await page.mouse.move(cardBox.x + cardBox.width/2, cardBox.y + cardBox.height/2);
        await page.mouse.down();

        // Move to another column (simulate drag)
        await page.mouse.move(cardBox.x + 300, cardBox.y);
        await page.waitForTimeout(1000);

        // Drop
        await page.mouse.up();
        await page.waitForTimeout(1000);

        console.log('âœ… Drag and drop simulation completed');
      }
    }
  });

  test('should take production board screenshots', async ({ page }) => {
    // Wait for full load
    await page.waitForTimeout(3000);

    // Take desktop screenshot
    await page.screenshot({ 
      path: 'test-results/production-board-desktop.png',
      fullPage: true 
    });

    // Take mobile screenshot
    await page.setViewportSize({ width: 375, height: 667 });
    await page.waitForTimeout(1000);

    await page.screenshot({ 
      path: 'test-results/production-board-mobile.png',
      fullPage: true 
    });

    console.log('ğŸ“¸ Production board screenshots saved');
  });

  test('should measure production board performance', async ({ page }) => {
    const startTime = Date.now();

    await page.goto('/production');
    await page.waitForLoadState('networkidle');

    // Wait for kanban columns to load
    await page.waitForSelector('text="Estimate"', { timeout: 10000 }).catch(() => {
      console.log('âš ï¸ Kanban columns not found within timeout');
    });

    const loadTime = Date.now() - startTime;

    console.log(`âš¡ Production Board Performance: ${loadTime}ms`);
    expect(loadTime).toBeLessThan(8000); // Should load within 8 seconds
  });
});

