    const cardExists = await firstJobCard.count() > 0;

    if (cardExists) {
      // Get the card's bounding box
      const cardBox = await firstJobCard.boundingBox();

      if (cardBox) {
        // Simulate drag start
        await page.mouse.move(cardBox.x + cardBox.width/2, cardBox.y + cardBox.height/2);
        await page.mouse.down();

        // Move to another column (simulate drag)
        await page.mouse.move(cardBox.x + 300, cardBox.y);
