  test('should test session persistence', async ({ page }) => {
    // Login and navigate to dashboard
    await page.goto('/dashboard');
    await page.waitForTimeout(2000);

    // Close and reopen browser context (simulates app restart)
    const context = page.context();
    const newPage = await context.newPage();

    // Navigate to protected route
    await newPage.goto('/production');
    await newPage.waitForTimeout(3000);

    // Should either be logged in or redirect to login
    const currentUrl = newPage.url();
    const isLoggedIn = !currentUrl.includes('/login') && await newPage.locator('h1, h2, h3, h4').count() > 0;
    const redirectedToLogin = currentUrl.includes('/login');

    console.log(`ğŸ”’ Session persistence: URL=${currentUrl}, Logged in=${isLoggedIn}, Redirected=${redirectedToLogin}`);

    await newPage.close();
  });

  test('should test data loading states', async ({ page }) => {
    // Navigate to data-heavy pages and check for loading indicators
    const dataPages = [
      { path: '/dashboard', name: 'Dashboard' },
      { path: '/production', name: 'Production Board' },
      { path: '/parts', name: 'Parts Management' }
    ];

    for (const pageInfo of dataPages) {
      console.log(`â³ Testing loading states for ${pageInfo.name}...`);

      // Navigate and immediately check for loading indicators
      await page.goto(pageInfo.path);

      // Check for loading indicators within first 500ms
      await page.waitForTimeout(500);
      const hasLoadingIndicator = await page.locator('.MuiCircularProgress-root, [role="progressbar"], text*="Loading"').count() > 0;

      // Wait for content to load
      await page.waitForTimeout(3000);
      const hasContent = await page.locator('h1, h2, h3, h4').count() > 0;

      console.log(`   âœ… ${pageInfo.name}: Loading indicator=${hasLoadingIndicator}, Final content=${hasContent}`);
    }
  });

  test('should test form validation across the system', async ({ page }) => {
    // Test various forms throughout the application
    const formTests = [];

    // Test customer form (if accessible)
    await page.goto('/customers');
    await page.waitForTimeout(2000);

    const addCustomerButton = await page.locator('button:has-text("Add Customer"), button:has-text("Add")').count();
    if (addCustomerButton > 0) {
      await page.click('button:has-text("Add Customer"), button:has-text("Add")');
      await page.waitForTimeout(1000);

      const dialog = await page.locator('[role="dialog"]').count();
      if (dialog > 0) {
        // Try to submit empty form
        const submitButton = await page.locator('button:has-text("Save"), button:has-text("Submit"), button[type="submit"]').count();
        if (submitButton > 0) {
          await page.click('button:has-text("Save"), button:has-text("Submit"), button[type="submit"]');
          await page.waitForTimeout(1000);

          const validationErrors = await page.locator('.MuiFormHelperText-error, [role="alert"]').count();
          formTests.push({ form: 'Customer', hasValidation: validationErrors > 0 });

          // Close dialog
          const closeButton = await page.locator('button:has-text("Cancel"), button:has-text("Close")').count();
          if (closeButton > 0) {
            await page.click('button:has-text("Cancel"), button:has-text("Close")');
            await page.waitForTimeout(500);
          }
        }
      }
    }

    console.log('ğŸ“ Form validation tests:', formTests);
  });

  test('should test system performance across pages', async ({ page }) => {
    const performanceResults = [];
    const pages = [
      { path: '/dashboard', name: 'Dashboard' },
      { path: '/production', name: 'Production' },
      { path: '/parts', name: 'Parts' }
    ];

    for (const pageInfo of pages) {
      const startTime = Date.now();

      await page.goto(pageInfo.path);
      await page.waitForLoadState('networkidle');

      const loadTime = Date.now() - startTime;

      // Check for content to be fully rendered
      await page.waitForSelector('h1, h2, h3, h4', { timeout: 5000 }).catch(() => {});

      const fullRenderTime = Date.now() - startTime;

      performanceResults.push({
        page: pageInfo.name,
        loadTime,
        fullRenderTime
      });

      console.log(`âš¡ ${pageInfo.name}: Load=${loadTime}ms, Render=${fullRenderTime}ms`);
    }

    // Check that no page takes too long
    const slowPages = performanceResults.filter(p => p.fullRenderTime > 10000);
    expect(slowPages.length).toBe(0);

    console.log('âœ… Performance test completed - all pages load within acceptable time');
  });

  test('should create comprehensive system screenshots', async ({ page }) => {
    const pages = [
      { path: '/dashboard', name: 'dashboard-system' },
      { path: '/production', name: 'production-system' },
      { path: '/parts', name: 'parts-system' },
      { path: '/technician', name: 'technician-system' },
      { path: '/quality-control', name: 'qc-system' }
    ];

    // Desktop screenshots
    for (const pageInfo of pages) {
      try {
        await page.goto(pageInfo.path);
        await page.waitForTimeout(3000);

        await page.screenshot({ 
          path: `test-results/${pageInfo.name}-desktop.png`,
          fullPage: true 
        });

        console.log(`ğŸ“¸ Desktop screenshot saved: ${pageInfo.name}`);
      } catch (error) {
        console.log(`âŒ Failed to screenshot ${pageInfo.name}: ${error.message}`);
      }
    }

    // Mobile screenshots
    await page.setViewportSize({ width: 375, height: 667 });

    for (const pageInfo of pages) {
      try {
        await page.goto(pageInfo.path);
        await page.waitForTimeout(2000);

        await page.screenshot({ 
          path: `test-results/${pageInfo.name}-mobile.png`,
          fullPage: true 
        });

        console.log(`ğŸ“± Mobile screenshot saved: ${pageInfo.name}`);
      } catch (error) {
        console.log(`âŒ Failed to screenshot mobile ${pageInfo.name}: ${error.message}`);
      }
    }
  });

  test('should generate test report summary', async ({ page }) => {
    // Final system validation
    const systemHealth = {
      pages: [],
      overallHealth: 'Good'
    };

    const testPages = ['/dashboard', '/production', '/customers', '/parts', '/technician', '/quality-control'];

    for (const pagePath of testPages) {
      try {
        await page.goto(pagePath);
        await page.waitForTimeout(2000);

        const hasTitle = await page.locator('h1, h2, h3, h4').count() > 0;
        const hasErrors = await page.locator('[role="alert"]:has-text("error" i)').count() > 0;
        const hasContent = await page.locator('.MuiCard-root, table, canvas').count() > 0;

        systemHealth.pages.push({
          path: pagePath,
          title: hasTitle,
          errors: hasErrors,
          content: hasContent,
          status: hasTitle && !hasErrors && hasContent ? 'Healthy' : 'Needs Attention'
        });

      } catch (error) {
        systemHealth.pages.push({
          path: pagePath,
          status: 'Error',
          error: error.message
        });
      }
    }

    // Generate report
    const healthyPages = systemHealth.pages.filter(p => p.status === 'Healthy').length;
    const totalPages = systemHealth.pages.length;

    console.log('\nğŸ¥ SYSTEM HEALTH REPORT');
    console.log('========================');
    console.log(`ğŸ“Š Overall Score: ${healthyPages}/${totalPages} pages healthy`);
    console.log(`ğŸ“ˆ System Health: ${(healthyPages/totalPages * 100).toFixed(1)}%`);

    systemHealth.pages.forEach(page => {
      const status = page.status === 'Healthy' ? 'âœ…' : page.status === 'Error' ? 'âŒ' : 'âš ï¸';
      console.log(`${status} ${page.path}: ${page.status}`);
    });

    expect(healthyPages).toBeGreaterThan(totalPages * 0.7); // At least 70% should be healthy
  });
});

