const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

console.log('ğŸš— CollisionOS Desktop App Testing Suite');
console.log('=========================================\n');

// Ensure test results directory exists
const testResultsDir = path.join(__dirname, 'test-results');
if (!fs.existsSync(testResultsDir)) {
  fs.mkdirSync(testResultsDir, { recursive: true });
  console.log('ğŸ“ Created test-results directory\n');
}

// Test configuration
const testSuites = [
  {
    name: 'Authentication Tests',
    file: 'auth.spec.js',
    description: 'Login functionality and security'
  },
  {
    name: 'Dashboard Tests', 
    file: 'dashboard.spec.js',
    description: 'Dashboard UI and analytics'
  },
  {
    name: 'Production Board Tests',
    file: 'production-board.spec.js', 
    description: 'Kanban board and job management'
  },
  {
    name: 'Parts Management Tests',
    file: 'parts-management.spec.js',
    description: 'Parts inventory and suppliers'
  },
  {
    name: 'Comprehensive System Tests',
    file: 'comprehensive-system.spec.js',
    description: 'Full system integration and performance'
  }
];

// Check if servers are running
function checkServerStatus() {
  console.log('ğŸ” Checking server status...');

  try {
    // Check React dev server (port 3000)
    execSync('curl -s http://localhost:3000 > /dev/null', { stdio: 'ignore' });
    console.log('âœ… React app is running on port 3000');
  } catch (error) {
    console.log('âŒ React app is not running on port 3000');
    console.log('   Please run: npm run client');
    return false;
  }

  try {
    // Check Node.js server (port 3001) 
    execSync('curl -s http://localhost:3001 > /dev/null', { stdio: 'ignore' });
    console.log('âœ… Node.js server is running on port 3001');
  } catch (error) {
    console.log('âš ï¸ Node.js server may not be running on port 3001');
    console.log('   This is optional, but run: npm run server');
  }

  return true;
}

// Run individual test suite
function runTestSuite(suite) {
  console.log(`\nğŸ§ª Running ${suite.name}...`);
  console.log(`ğŸ“ ${suite.description}`);

  try {
    const command = `npx playwright test e2e/${suite.file} --reporter=line`;
    execSync(command, { stdio: 'inherit' });
    console.log(`âœ… ${suite.name} completed successfully`);
    return true;
  } catch (error) {
    console.log(`âŒ ${suite.name} failed`);
    return false;
  }
}

// Run all tests
function runAllTests() {
  console.log('\nğŸš€ Starting comprehensive test suite...\n');

  // Check server status first
  if (!checkServerStatus()) {
    console.log('\nâŒ Servers not running. Please start the application first.');
    process.exit(1);
  }

  const results = [];
  let totalPassed = 0;

  // Run each test suite
  for (const suite of testSuites) {
    const passed = runTestSuite(suite);
    results.push({ suite: suite.name, passed });
    if (passed) totalPassed++;
  }

  // Generate summary
  console.log('\nğŸ“Š TEST SUITE SUMMARY');
  console.log('====================');

  results.forEach(result => {
    const status = result.passed ? 'âœ… PASSED' : 'âŒ FAILED';
    console.log(`${status} ${result.suite}`);
  });

  console.log(`\nğŸ† Overall Result: ${totalPassed}/${testSuites.length} test suites passed`);

  if (totalPassed === testSuites.length) {
    console.log('ğŸ‰ All tests passed! CollisionOS is working correctly.');
  } else {
    console.log('âš ï¸ Some tests failed. Check the output above for details.');
  }

  // Generate HTML report
  try {
    console.log('\nğŸ“„ Generating HTML test report...');
    execSync('npx playwright show-report', { stdio: 'inherit' });
  } catch (error) {
    console.log('âš ï¸ Could not generate HTML report');
  }
}

// Quick smoke test
function runSmokeTest() {
  console.log('\nğŸ’¨ Running smoke test...');

  if (!checkServerStatus()) {
    console.log('âŒ Smoke test failed - servers not running');
    return;
  }

  try {
    const command = 'npx playwright test e2e/auth.spec.js -g "should display login page correctly" --reporter=line';
    execSync(command, { stdio: 'inherit' });
    console.log('âœ… Smoke test passed! Basic functionality is working.');
  } catch (error) {
    console.log('âŒ Smoke test failed! Check basic functionality.');
  }
}

// Parse command line arguments
const args = process.argv.slice(2);
const command = args[0];

switch (command) {
  case 'smoke':
    runSmokeTest();
    break;
  case 'auth':
    runTestSuite(testSuites[0]);
    break;
  case 'dashboard':
    runTestSuite(testSuites[1]);
    break;
  case 'production':
    runTestSuite(testSuites[2]);
    break;
  case 'parts':
    runTestSuite(testSuites[3]);
    break;
  case 'system':
    runTestSuite(testSuites[4]);
    break;
  case 'all':
  default:
    runAllTests();
    break;
}

console.log('\nğŸ Testing completed!');
console.log('ğŸ“¸ Screenshots and videos saved in test-results/');
console.log('ğŸ“„ For detailed HTML report, check: playwright-report/');

