import React, { useState } from 'react';
import { Button, Box, TextField, Typography, Alert } from '@mui/material';
import { useAuth } from '../../hooks/useAuth';

export default function Login() {
  const { login } = useAuth();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      setError('');

      // Simple credential check
      const validCredentials = [
        { username: 'admin', password: 'admin123', role: 'owner', firstName: 'Admin', lastName: 'User' },
        { username: 'manager', password: 'manager123', role: 'manager', firstName: 'Mike', lastName: 'Johnson' },
        { username: 'estimator', password: 'estimator123', role: 'estimator', firstName: 'Sarah', lastName: 'Wilson' },
        { username: 'tech1', password: 'tech123', role: 'technician', firstName: 'John', lastName: 'Smith' },
        { username: 'parts', password: 'parts123', role: 'parts_manager', firstName: 'Lisa', lastName: 'Chen' }
      ];

      const user = validCredentials.find(
        cred => cred.username === username && cred.password === password
      );

      if (user) {
        login(user, 'dev-token');
      } else {
        setError('Invalid username or password');
      }
    } catch (err) {
      setError('Login failed. Please try again.');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

  return (
    <Box sx={{p:4, display:'flex', flexDirection:'column', gap:2, maxWidth:360, m:'80px auto'}}>
      <Typography variant="h4" sx={{ textAlign: 'center', mb: 2, color: 'primary.main' }}>
        ğŸš— CollisionOS
      </Typography>
      <Typography variant="h6" sx={{ textAlign: 'center', mb: 3 }}>
        Auto Body Shop Management

