const { chromium } = require('playwright');

async function simpleTest() {
  console.log('ğŸš— Simple CollisionOS Test');
  console.log('==========================\n');

  // Start the React app first
  const { exec } = require('child_process');

  console.log('ğŸš€ Starting React development server...');
  const reactProcess = exec('npm run client', { 
    cwd: 'C:\\Users\\farha\\OneDrive\\Desktop\\CollisionOS',
    detached: true,
    stdio: 'ignore'
  });

  // Wait for React app to start
  await new Promise(resolve => {
    const checkServer = setInterval(async () => {
      try {
        const response = await fetch('http://localhost:3000');
        if (response.ok) {
          console.log('âœ… React app is ready!');
          clearInterval(checkServer);
          resolve();
        }
      } catch (error) {
        console.log('â³ Waiting for React app to start...');
      }
    }, 2000);
  });

  const browser = await chromium.launch({ 
    headless: false,  // Show the browser
    slowMo: 1000     // Slow down for visibility
  });

  try {
    const page = await browser.newPage();

    console.log('\nğŸ” Testing Login Page...');
    await page.goto('http://localhost:3000');
    await page.waitForTimeout(3000);

    // Take screenshot of what we actually see
    await page.screenshot({ path: 'test-results/actual-login-page.png', fullPage: true });
    console.log('ğŸ“¸ Screenshot saved: actual-login-page.png');

    // Check page title
    const title = await page.title();
    console.log(`ğŸ“„ Page title: "${title}"`);

    // Check for CollisionOS branding
    const branding = await page.locator('text=CollisionOS').count();
