const { chromium } = require('playwright');
const { spawn } = require('child_process');
const fs = require('fs');
const path = require('path');

async function comprehensiveTest() {
  console.log('ğŸš— CollisionOS Comprehensive Desktop App Test');
  console.log('=============================================\n');

  // Ensure test results directory exists
  const testResultsDir = path.join(__dirname, 'test-results');
  if (!fs.existsSync(testResultsDir)) {
    fs.mkdirSync(testResultsDir, { recursive: true });
  }

  let reactProcess = null;
  let serverProcess = null;

  try {
    // Start the server
    console.log('ğŸš€ Starting Node.js server...');
    serverProcess = spawn('npm', ['run', 'server'], {
      cwd: process.cwd(),
      stdio: 'pipe',
      shell: true
    });

    // Start React app
    console.log('ğŸš€ Starting React client...');
    reactProcess = spawn('npm', ['run', 'client'], {
      cwd: process.cwd(),
      stdio: 'pipe',
      shell: true
    });

    // Wait for servers to start
    console.log('â³ Waiting for servers to start...');
    await new Promise(resolve => setTimeout(resolve, 15000));

    // Check if servers are running
    let serverReady = false;
    let reactReady = false;

    try {
      const response = await fetch('http://localhost:3000');
      reactReady = response.ok;
      console.log(`ğŸ“± React app status: ${reactReady ? 'âœ… Ready' : 'âŒ Not ready'}`);
    } catch (error) {
      console.log('âŒ React app not responding');
    }

    try {
      const response = await fetch('http://localhost:3001/health');
      serverReady = response.ok;
      console.log(`ğŸ–¥ï¸ Server status: ${serverReady ? 'âœ… Ready' : 'âŒ Not ready'}`);
    } catch (error) {
      console.log('âš ï¸ Server not responding (this is optional)');
    }

    if (!reactReady) {
      console.log('âŒ Cannot proceed without React app. Exiting...');
      return;
    }

    // Start browser testing
    console.log('\nğŸŒ Starting browser tests...');

    const browser = await chromium.launch({ 
      headless: false,
      slowMo: 500,
      args: ['--disable-web-security', '--disable-features=VizDisplayCompositor']
    });

    const page = await browser.newPage();

    // Set up console logging
    page.on('console', msg => {
      if (msg.type() === 'error') {
        console.log('ğŸ”´ Console Error:', msg.text());
      }
    });

    page.on('pageerror', error => {
      console.log('ğŸ”´ Page Error:', error.message);
    });

    // Test 1: Load the application
    console.log('\nğŸ“± Test 1: Loading application...');
    await page.goto('http://localhost:3000', { waitUntil: 'networkidle' });
    await page.waitForTimeout(5000); // Extra time for React to render

    await page.screenshot({ 
      path: 'test-results/01-app-loaded.png', 
      fullPage: true 
    });

    const title = await page.title();
    console.log(`   âœ… Page title: "${title}"`);

    // Test 2: Check for React root and loading
    console.log('\nğŸ” Test 2: Checking React application...');

    const reactRoot = await page.locator('#root').count();
    console.log(`   React root found: ${reactRoot > 0 ? 'âœ…' : 'âŒ'}`);

    // Wait for any loading spinners to disappear
    await page.waitForSelector('.loading, [role="progressbar"]', { state: 'detached', timeout: 5000 }).catch(() => {});

    // Check what's actually rendered
    const bodyContent = await page.locator('body').textContent();
    const hasContent = bodyContent.length > 100;
    console.log(`   Page has content: ${hasContent ? 'âœ…' : 'âŒ'} (${bodyContent.length} chars)`);

    // Test 3: Look for login elements with multiple strategies
    console.log('\nğŸ” Test 3: Login functionality...');

    // Strategy 1: Look for Material-UI TextField components
    const textFields = await page.locator('.MuiTextField-root').count();
    console.log(`   Material-UI TextFields: ${textFields}`);

    // Strategy 2: Look for any input elements
    const allInputs = await page.locator('input').count();
    console.log(`   Total input elements: ${allInputs}`);

    // Strategy 3: Look for form elements
    const forms = await page.locator('form').count();
    console.log(`   Form elements: ${forms}`);

    // Strategy 4: Look for buttons
    const buttons = await page.locator('button').count();
    console.log(`   Button elements: ${buttons}`);

    // Strategy 5: Look for specific text content
    const hasLoginText = await page.locator('text=Login').count() > 0;
    const hasCollisionText = await page.locator('text*=CollisionOS').count() > 0;
    console.log(`   Login text found: ${hasLoginText ? 'âœ…' : 'âŒ'}`);
    console.log(`   CollisionOS text found: ${hasCollisionText ? 'âœ…' : 'âŒ'}`);

    // If we have inputs, try to interact
    if (allInputs >= 2 && buttons > 0) {
      console.log('\n   ğŸ¯ Attempting login interaction...');

      try {
        // Fill first input (username)
        await page.locator('input').first().fill('admin');
        console.log('   âœ… Username filled');

        // Fill password input
        const passwordInput = page.locator('input[type="password"]');
        if (await passwordInput.count() > 0) {
          await passwordInput.fill('admin123');
          console.log('   âœ… Password filled');
        }

        // Click login button
        const loginButton = page.locator('button').first();
        await loginButton.click();
        console.log('   âœ… Login button clicked');

        // Wait for potential navigation
        await page.waitForTimeout(3000);

        const currentUrl = page.url();
        console.log(`   ğŸ“ URL after login: ${currentUrl}`);

        await page.screenshot({ 
          path: 'test-results/02-after-login.png', 
          fullPage: true 
        });

      } catch (error) {
        console.log(`   âŒ Login interaction failed: ${error.message}`);
      }
    }

    // Test 4: Navigation test
    console.log('\nğŸ§­ Test 4: Testing navigation...');

    const navElements = await page.locator('nav, [role="navigation"]').count();
    console.log(`   Navigation elements: ${navElements}`);

    // Look for navigation links
    const navLinks = await page.locator('a, button').filter({ hasText: /dashboard|production|customer|parts/i }).count();
    console.log(`   Navigation links found: ${navLinks}`);

    // Test 5: Check for major page sections
    console.log('\nğŸ“Š Test 5: Checking for main application features...');

    const cards = await page.locator('.MuiCard-root, [role="region"]').count();
    const tables = await page.locator('table, [role="table"]').count();
    const charts = await page.locator('canvas').count();

    console.log(`   Cards/Widgets: ${cards}`);
    console.log(`   Tables: ${tables}`);
    console.log(`   Charts: ${charts}`);

    // Test 6: Responsive design test
    console.log('\nğŸ“± Test 6: Testing responsive design...');

    await page.setViewportSize({ width: 375, height: 667 });
    await page.waitForTimeout(1000);

    await page.screenshot({ 
      path: 'test-results/03-mobile-view.png', 
      fullPage: true 
    });

    const mobileElements = await page.locator('button, input').count();
    console.log(`   Mobile elements still accessible: ${mobileElements > 0 ? 'âœ…' : 'âŒ'}`);

    // Reset to desktop
    await page.setViewportSize({ width: 1280, height: 720 });
    await page.waitForTimeout(1000);

    // Test 7: Performance test
    console.log('\nâš¡ Test 7: Basic performance test...');

    const startTime = Date.now();
    await page.goto('http://localhost:3000', { waitUntil: 'networkidle' });
    const loadTime = Date.now() - startTime;

    console.log(`   Page load time: ${loadTime}ms`);
    console.log(`   Performance: ${loadTime < 3000 ? 'âœ… Fast' : loadTime < 5000 ? 'âš ï¸ Acceptable' : 'âŒ Slow'}`);

    // Final comprehensive screenshot
    await page.screenshot({ 
      path: 'test-results/04-final-state.png', 
      fullPage: true 
    });

    // Test Summary
    console.log('\nğŸ“‹ TEST SUMMARY');
    console.log('================');
    console.log(`âœ… Application loads: ${title === 'CollisionOS'}`);
    console.log(`âœ… React renders: ${hasContent}`);
    console.log(`âœ… Has interactive elements: ${allInputs > 0 && buttons > 0}`);
    console.log(`âœ… Has CollisionOS branding: ${hasCollisionText}`);
    console.log(`âœ… Navigation present: ${navElements > 0}`);
    console.log(`âœ… UI components present: ${cards > 0 || tables > 0}`);
    console.log(`âœ… Mobile responsive: ${mobileElements > 0}`);
    console.log(`âœ… Performance acceptable: ${loadTime < 5000}`);

    const passedTests = [
      title === 'CollisionOS',
      hasContent,
      allInputs > 0 && buttons > 0,
      hasCollisionText,
      navElements > 0 || navLinks > 0,
      cards > 0 || tables > 0 || charts > 0,
      mobileElements > 0,
      loadTime < 5000
    ].filter(Boolean).length;

    console.log(`\nğŸ† Overall Score: ${passedTests}/8 tests passed (${(passedTests/8*100).toFixed(0)}%)`);

    if (passedTests >= 6) {
      console.log('ğŸ‰ CollisionOS desktop app is working well!');
    } else if (passedTests >= 4) {
      console.log('âš ï¸ CollisionOS has some issues but core functionality works');
    } else {
      console.log('âŒ CollisionOS has significant issues that need attention');
    }

    // Keep browser open for manual inspection
    console.log('\nğŸ” Browser will stay open for 45 seconds for manual inspection...');
    await page.waitForTimeout(45000);

    await browser.close();

  } catch (error) {
    console.log('âŒ Test failed with error:', error.message);
    console.log('Stack trace:', error.stack);
  } finally {
    // Clean up processes
    console.log('\nğŸ§¹ Cleaning up...');

    if (reactProcess) {
      reactProcess.kill('SIGTERM');
      console.log('âœ… React process terminated');
    }

    if (serverProcess) {
      serverProcess.kill('SIGTERM');
      console.log('âœ… Server process terminated');
    }

    console.log('\nğŸ Comprehensive test completed!');
    console.log('ğŸ“¸ Screenshots saved in test-results/');
    console.log('ğŸ” Check the screenshots to see the actual UI state');
  }
}

// Handle graceful shutdown
process.on('SIGINT', () => {
  console.log('\nâš ï¸ Test interrupted by user');
  process.exit(0);
});

comprehensiveTest().catch(console.error);

