const { chromium } = require('playwright');

async function debugApp() {
  console.log('ğŸ”§ CollisionOS Debug Session');
  console.log('============================\n');

  const browser = await chromium.launch({ 
    headless: false,
    slowMo: 1000,
    devtools: true  // Open dev tools
  });

  const page = await browser.newPage();

  // Collect all console messages
  const consoleMessages = [];
  const errors = [];

  page.on('console', msg => {
    const text = msg.text();
    console.log(`ğŸ”µ Console [${msg.type()}]:`, text);
    consoleMessages.push({ type: msg.type(), text });
  });

  page.on('pageerror', error => {
    console.log('ğŸ”´ Page Error:', error.message);
    errors.push(error.message);
  });

  page.on('requestfailed', request => {
    console.log('ğŸ”´ Failed Request:', request.url(), request.failure());
  });

  try {
    console.log('ğŸŒ Loading CollisionOS...');
    await page.goto('http://localhost:3000', { 
      waitUntil: 'networkidle',
      timeout: 30000 
    });

    // Wait for React to attempt rendering
    await page.waitForTimeout(5000);

    // Take diagnostic screenshot
    await page.screenshot({ 
      path: 'test-results/debug-screenshot.png', 
      fullPage: true 
    });

    console.log('\nğŸ“¸ Screenshot saved: debug-screenshot.png');

    // Get page HTML to see what's actually rendered
    const html = await page.content();
    require('fs').writeFileSync('test-results/debug-page.html', html);
    console.log('ğŸ’¾ Page HTML saved: debug-page.html');

    // Check React error boundary
    const errorBoundary = await page.locator('[role="alert"], .error-boundary').count();
    console.log(`ğŸ›¡ï¸ Error boundary present: ${errorBoundary > 0}`);

    // Check if React root has any content
    const rootContent = await page.locator('#root').innerHTML();
    console.log(`ğŸ“¦ Root content length: ${rootContent.length} chars`);

    if (rootContent.length < 100) {
      console.log('âš ï¸ Root has minimal content - React may not be rendering');
      console.log('Root content:', rootContent.substring(0, 200));
    }

    // Try to evaluate JavaScript in the page
    const jsInfo = await page.evaluate(() => {
      return {
        hasReact: typeof React !== 'undefined',
        hasMaterialUI: typeof window.MUI !== 'undefined',
        reactVersion: window.React ? window.React.version : 'Not found',
        errors: window.console ? 'Console available' : 'No console',
        location: window.location.href
      };
    });

    console.log('\nğŸ” JavaScript Environment:');
    console.log('  React available:', jsInfo.hasReact);
    console.log('  Material-UI available:', jsInfo.hasMaterialUI);
    console.log('  React version:', jsInfo.reactVersion);
    console.log('  Current location:', jsInfo.location);

    // Analyze error patterns
    console.log('\nğŸ“Š Error Analysis:');
    const buttonErrors = errors.filter(err => err.includes('Button') || err.includes('MuiButtonRoot'));
    const undefinedErrors = errors.filter(err => err.includes('Cannot read properties of undefined'));

    console.log(`  Button-related errors: ${buttonErrors.length}`);
    console.log(`  Undefined property errors: ${undefinedErrors.length}`);

    if (undefinedErrors.length > 0) {
      console.log('\nğŸ”´ Undefined Property Errors:');
      undefinedErrors.forEach((err, i) => {
        console.log(`  ${i + 1}. ${err}`);
      });
    }

    // Check for missing dependencies
    console.log('\nğŸ“¦ Dependency Check:');
    const networkRequests = [];

    page.on('response', response => {
      if (!response.ok()) {
        console.log(`âŒ Failed request: ${response.url()} (${response.status()})`);
      }
    });

    console.log('\nâ° Keeping browser open for manual inspection...');
    console.log('   Check the browser DevTools Console tab for detailed errors');
    console.log('   Press Ctrl+C to exit when done inspecting');

    // Keep browser open until user stops
    await new Promise(resolve => {
      process.on('SIGINT', () => {
        console.log('\nâœ… Debug session ended by user');
        resolve();
      });

      // Auto-close after 5 minutes if not interrupted
      setTimeout(() => {
        console.log('\nâ° Auto-closing after 5 minutes');
        resolve();
      }, 300000);
    });

  } catch (error) {
    console.log('âŒ Debug failed:', error.message);
  } finally {
    await browser.close();
  }
}

// Start debug session
debugApp().catch(console.error);

