     1→// ===============================
     2→// Dashboard 2025 Drop‑in Pack
     3→// Tech: React 18 + MUI v5 + Framer Motion + Recharts
     4→// Copy these files into your project. Minimal wiring notes are at the top
     5→// of each section. The code assumes a dark theme by default.
     6→// ===============================
     7→
     8→// -------------------------------------------------
     9→// 1) Install deps (run once)
    10→// -------------------------------------------------
    11→// npm i @mui/material @mui/icons-material @emotion/react @emotion/styled framer-motion recharts
    12→
    13→// -------------------------------------------------
    14→// 2) src/theme/modernTheme.ts
    15→// Wrap your app with <ThemeProvider theme={modernTheme}> + <CssBaseline/>
    16→// -------------------------------------------------
    17→import { createTheme } from '@mui/material/styles';
    18→
    19→declare module '@mui/material/styles' {
    20→  interface Theme {
    21→    custom: {
    22→      gradients: {
    23→        primary: string;
    24→        background: string;
    25→      };
    26→      glass: {
    27→        surface: string;
    28→        border: string;
    29→        shadow: string;
    30→      };
    31→    };
    32→  }
    33→  interface ThemeOptions {
    34→    custom?: {
    35→      gradients?: {
    36→        primary?: string;
    37→        background?: string;
    38→      };
    39→      glass?: {
    40→        surface?: string;
    41→        border?: string;
    42→        shadow?: string;
    43→      };
    44→    };
    45→  }
    46→}
    47→
    48→export const modernTheme = createTheme({
    49→  palette: {
