    63→      'system-ui',
    64→      '-apple-system',
    65→      'Segoe UI',
    66→      'Roboto',
    67→      'Helvetica Neue',
    68→      'Arial',
    69→      'Noto Sans',
    70→      'sans-serif',
    71→    ].join(','),
    72→    h1: { fontWeight: 800, letterSpacing: '-0.02em' },
    73→    h2: { fontWeight: 700, letterSpacing: '-0.02em' },
    74→    h3: { fontWeight: 700 },
    75→    subtitle1: { opacity: 0.9 }
    76→  },
    77→  shape: { borderRadius: 20 },
    78→  shadows: [
    79→    'none',
    80→    '0 8px 24px rgba(0,0,0,0.12)',
    81→    '0 12px 32px rgba(0,0,0,0.16)',
    82→    '0 16px 48px rgba(0,0,0,0.18)',
    83→    ...Array(21).fill('0 16px 48px rgba(0,0,0,0.18)')
    84→  ] as any,
    85→  components: {
    86→    MuiCssBaseline: {
    87→      styleOverrides: `
    88→        :root { color-scheme: dark; }
    89→        .glassmorphic-card { 
    90→          background: rgba(255,255,255,0.08);
    91→          backdrop-filter: saturate(180%) blur(20px);
    92→          -webkit-backdrop-filter: saturate(180%) blur(20px);
    93→          border: 1px solid rgba(255,255,255,0.18);
    94→          box-shadow: 0 8px 32px rgba(0,0,0,0.35);
    95→        }
    96→        .no-blur .glassmorphic-card { backdrop-filter: none; -webkit-backdrop-filter: none; }
    97→        .ambient-gradient {
    98→          background-image: radial-gradient( 800px 400px at 0% 0%, rgba(99,102,241,.25), transparent 45%),
    99→                            radial-gradient( 800px 400px at 100% 0%, rgba(139,92,246,.20), transparent 45%),
   100→                            radial-gradient( 1200px 600px at 50% 100%, rgba(236,72,153,.18), transparent 45%);
   101→        }
   102→      `,
   103→    },
   104→    MuiPaper: {
   105→      styleOverrides: {
   106→        root: {
   107→          background: 'rgba(30,41,59,0.50)',
   108→          border: '1px solid rgba(255,255,255,0.08)',
   109→          backdropFilter: 'saturate(160%) blur(18px)',
   110→        }
   111→      }
   112→    },
   113→    MuiButton: {
   114→      styleOverrides: {
   115→        root: { borderRadius: 14, textTransform: 'none', fontWeight: 600 }
   116→      }
   117→    }
   118→  },
   119→  custom: {
   120→    gradients: {
   121→      primary: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
   122→      background: 'linear-gradient(135deg, #0F172A 0%, #1E293B 100%)'
   123→    },
   124→    glass: {
   125→      surface: 'rgba(255,255,255,0.08)',
   126→      border: 'rgba(255,255,255,0.18)',
   127→      shadow: '0 8px 32px rgba(0,0,0,0.35)'
   128→    }
   129→  }
   130→});
   131→
   132→// -------------------------------------------------
   133→// 3) src/components/ModernBackground.tsx (optional)
   134→// Place <ModernBackground/> once at app root
   135→// -------------------------------------------------
   136→import React from 'react';
   137→
   138→export const ModernBackground: React.FC = () => (
   139→  <div className="fixed inset-0 -z-10 ambient-gradient" style={{ pointerEvents: 'none' }}>
   140→    <svg style={{ position: 'absolute', inset: 0, width: '100%', height: '100%', opacity: 0.15 }}>
   141→      <defs>
   142→        <pattern id="grid" width="40" height="40" patternUnits="userSpaceOnUse">
   143→          <path d="M 40 0 L 0 0 0 40" fill="none" stroke="rgba(255,255,255,0.25)" strokeWidth="1" />
   144→        </pattern>
   145→      </defs>
   146→      <rect width="100%" height="100%" fill="url(#grid)" />
   147→    </svg>
   148→  </div>
   149→);
   150→
   151→// -------------------------------------------------
   152→// 4) src/components/GlassCard.tsx
   153→// Generic glass card with Framer hover + focus states
   154→// -------------------------------------------------
   155→import { Card, CardProps } from '@mui/material';
   156→import { motion } from 'framer-motion';
   157→import React from 'react';
   158→
   159→const MotionCard = motion(Card);
   160→
   161→export const GlassCard: React.FC<CardProps & { asMotion?: boolean }> = ({ children, asMotion = true, sx, ...props }) => {
   162→  const baseStyles = {
   163→    p: 2.5,
   164→    borderRadius: 4,
   165→    background: 'rgba(255,255,255,0.08)',
   166→    backdropFilter: 'saturate(160%) blur(18px)',
   167→    border: '1px solid rgba(255,255,255,0.12)'
   168→  } as const;
   169→
   170→  if (!asMotion) {
   171→    return (
   172→      <Card className="glassmorphic-card" sx={{ ...baseStyles, ...sx }} {...props}>
   173→        {children}
   174→      </Card>
   175→    );
   176→  }
   177→
   178→  return (
   179→    <MotionCard
   180→      className="glassmorphic-card"
   181→      whileHover={{ scale: 1.02 }}
   182→      transition={{ type: 'spring', stiffness: 400, damping: 30 }}
   183→      sx={{ ...baseStyles, ...sx }}
   184→      {...props}
   185→    >
   186→      {children}
   187→    </MotionCard>
   188→  );
   189→};
   190→
   191→// -------------------------------------------------
   192→// 5) src/utils/AnimatedCounter.tsx
   193→// Smoothly animates KPI numbers (respects reduced motion)
   194→// -------------------------------------------------
   195→import React, { useEffect, useRef } from 'react';
   196→import { animate, useReducedMotion } from 'framer-motion';
   197→
   198→export const AnimatedCounter: React.FC<{ value: number; duration?: number; prefix?: string; suffix?: string }> = ({ value, duration = 1.0, prefix = '', suffix = '' }) => {
   199→  const nodeRef = useRef<HTMLSpanElement>(null);
   200→  const prefersReducedMotion = useReducedMotion();
   201→
   202→  useEffect(() => {
   203→    const node = nodeRef.current;
   204→    if (!node) return;
   205→    if (prefersReducedMotion) {
   206→      node.textContent = `${prefix}${Math.round(value).toLocaleString()}${suffix}`;
   207→      return;
   208→    }
   209→    const controls = animate(0, value, {
   210→      duration,
   211→      ease: 'easeOut',
   212→      onUpdate(v) {
   213→        node.textContent = `${prefix}${Math.round(v).toLocaleString()}${suffix}`;
   214→      }
   215→    });
   216→    return () => controls.stop();
   217→  }, [value, duration, prefix, suffix, prefersReducedMotion]);
   218→
   219→  return <span ref={nodeRef} />;
   220→};
   221→
   222→// -------------------------------------------------
   223→// 6) src/components/KpiCard.tsx
   224→// KPI card with sparkline + predictive indicator
   225→// -------------------------------------------------
   226→import React from 'react';
   227→import { Box, Chip, Stack, Typography } from '@mui/material';
   228→import { GlassCard } from './GlassCard';
   229→import { AnimatedCounter } from '../utils/AnimatedCounter';
   230→import { ResponsiveContainer, AreaChart, Area, Tooltip } from 'recharts';
   231→import TrendingUpIcon from '@mui/icons-material/TrendingUp';
   232→import TrendingDownIcon from '@mui/icons-material/TrendingDown';
   233→
   234→export type KPI = {
   235→  label: string;
   236→  value: number;
   237→  deltaPct?: number; // e.g., +12% WoW
   238→  forecastNote?: string; // e.g., "Expected +12% next week"
   239→  series?: { name: string; value: number }[]; // last 7 days
   240→};
   241→
   242→export const KpiCard: React.FC<{ kpi: KPI } & { height?: number }> = ({ kpi, height = 120 }) => {
   243→  const Up = kpi.deltaPct && kpi.deltaPct >= 0;
   244→  return (
   245→    <GlassCard sx={{ overflow: 'hidden' }}>
   246→      <Stack direction="row" justifyContent="space-between" alignItems="flex-start" spacing={2}>
   247→        <Box>
   248→          <Typography variant="subtitle2" sx={{ opacity: 0.8 }}>{kpi.label}</Typography>
   249→          <Typography variant="h4" sx={{ fontWeight: 800, mt: .5 }}>
   250→            <AnimatedCounter value={kpi.value} />
   251→          </Typography>
   252→          <Stack direction="row" spacing={1} alignItems="center" sx={{ mt: 1 }}>
   253→            <Chip size="small" color={Up ? 'success' : 'error'}
   254→              icon={Up ? <TrendingUpIcon/> : <TrendingDownIcon/>}
   255→              label={`${Up ? '+' : ''}${kpi.deltaPct ?? 0}%`} />
   256→            {kpi.forecastNote && (
   257→              <Typography variant="caption" sx={{ opacity: 0.75 }}>{kpi.forecastNote}</Typography>
   258→            )}
   259→          </Stack>
   260→        </Box>
   261→        <Box sx={{ width: '45%', height }}>
   262→          <ResponsiveContainer width="100%" height="100%">
   263→            <AreaChart data={kpi.series ?? []} margin={{ top: 10, right: 0, left: 0, bottom: 0 }}>
   264→              <Tooltip contentStyle={{ background: 'rgba(15,23,42,0.9)', border: '1px solid rgba(255,255,255,0.1)' }} />
   265→              <Area type="monotone" dataKey="value" strokeOpacity={0.9} fillOpacity={0.25} />
   266→            </AreaChart>
   267→          </ResponsiveContainer>
   268→        </Box>
   269→      </Stack>
   270→    </GlassCard>
   271→  );
   272→};
   273→
   274→// -------------------------------------------------
   275→// 7) src/components/BentoGrid.tsx
   276→// Flexible CSS grid with variable spans per breakpoint
   277→// -------------------------------------------------
   278→import React, { ReactNode } from 'react';
   279→import { Box } from '@mui/material';
   280→import { motion } from 'framer-motion';
   281→
   282→export const BentoGrid: React.FC<{ children: ReactNode; cols?: number; gap?: number }> = ({ children, cols = 12, gap = 2 }) => (
   283→  <Box component={motion.div}
   284→       initial={{ opacity: 0 }}
   285→       animate={{ opacity: 1 }}
   286→       transition={{ duration: 0.3 }}
   287→       sx={{
   288→         display: 'grid',
   289→         gridTemplateColumns: `repeat(${cols}, minmax(0, 1fr))`,
   290→         gap
   291→       }}>
   292→    {children}
   293→  </Box>
   294→);
   295→
   296→export const BentoItem: React.FC<{ children: ReactNode; span?: { xs?: number; sm?: number; md?: number; lg?: number; xl?: number } }> = ({ children, span }) => (
   297→  <Box
   298→    component={motion.div}
   299→    variants={{ hidden: { y: 20, opacity: 0 }, visible: { y: 0, opacity: 1, transition: { type: 'spring', stiffness: 120, damping: 16 } } }}
   300→    initial="hidden"
   301→    animate="visible"
   302→    sx={{
   303→      gridColumn: {
   304→        xs: `span ${span?.xs ?? 12}`,
   305→        sm: `span ${span?.sm ?? span?.xs ?? 12}`,
   306→        md: `span ${span?.md ?? span?.sm ?? 6}`,
   307→        lg: `span ${span?.lg ?? span?.md ?? 4}`,
   308→        xl: `span ${span?.xl ?? span?.lg ?? 3}`,
   309→      }
   310→    }}
   311→  >
   312→    {children}
   313→  </Box>
   314→);
   315→
   316→// -------------------------------------------------
   317→// 8) src/pages/Dashboard.tsx
   318→// Example dashboard page using bento + KPI cards
   319→// -------------------------------------------------
   320→import React from 'react';
   321→import { Box, Container, Typography } from '@mui/material';
   322→import { ModernBackground } from '../components/ModernBackground';
   323→import { BentoGrid, BentoItem } from '../components/BentoGrid';
   324→import { KpiCard, KPI } from '../components/KpiCard';
   325→import { GlassCard } from '../components/GlassCard';
   326→
   327→const sampleKpis: KPI[] = [
   328→  { label: 'Active Jobs', value: 42, deltaPct: 8, forecastNote: 'Expected +12% next week', series: [ {name:'d1', value: 32}, {name:'d2', value: 30}, {name:'d3', value: 35}, {name:'d4', value: 34}, {name:'d5', value: 36}, {name:'d6', value: 38}, {name:'d7', value: 42} ] },
   329→  { label: 'Parts On Order', value: 123, deltaPct: -5, forecastNote: 'Anomaly risk ↓', series: [ {name:'d1', value: 100},{name:'d2', value: 120},{name:'d3', value: 115},{name:'d4', value: 112},{name:'d5', value: 110},{name:'d6', value: 118},{name:'d7', value: 123} ] },
   330→  { label: 'Cycle Time (days)', value: 9, deltaPct: -3, forecastNote: 'Target 8 days', series: [ {name:'d1', value: 11},{name:'d2', value: 10},{name:'d3', value: 10},{name:'d4', value: 9},{name:'d5', value: 9},{name:'d6', value: 9},{name:'d7', value: 9} ] }
   331→];
   332→
   333→export const DashboardPage: React.FC = () => {
   334→  return (
   335→    <Box sx={{ minHeight: '100vh', backgroundImage: (t) => t.custom.gradients.background }}>
   336→      <ModernBackground/>
   337→      <Container maxWidth="xl" sx={{ py: 4 }}>
   338→        <Typography variant="h4" sx={{ fontWeight: 800, mb: 3 }}>CollisionOS — Executive Overview</Typography>
   339→
   340→        {/* KPI Row */}
   341→        <BentoGrid cols={12} gap={2}>
   342→          {sampleKpis.map((k, i) => (
   343→            <BentoItem key={i} span={{ xs: 12, md: 4 }}>
   344→              <KpiCard kpi={k} />
   345→            </BentoItem>
   346→          ))}
   347→
   348→          {/* Wide primary analytics card (3x2 on md+) */}
   349→          <BentoItem span={{ xs: 12, md: 8 }}>
   350→            <GlassCard sx={{ minHeight: 320 }}>
   351→              <Typography variant="h6" sx={{ mb: 1.5, fontWeight: 700 }}>Throughput — Last 30 Days</Typography>
   352→              <Box sx={{ opacity: 0.8 }}>Replace with your main chart (Recharts/visx). Add annotations, drill‑downs, and animated transitions.</Box>
   353→            </GlassCard>
   354→          </BentoItem>
   355→
   356→          {/* Quick actions (2x1) */}
   357→          <BentoItem span={{ xs: 12, md: 4 }}>
   358→            <GlassCard sx={{ minHeight: 320, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
   359→              <Typography>Quick Actions: New RO • Book Calibration • Order Parts</Typography>
   360→            </GlassCard>
   361→          </BentoItem>
   362→
   363→          {/* Alerts (1x3 vertical stack on md+) */}
   364→          <BentoItem span={{ xs: 12, md: 4 }}>
   365→            <GlassCard sx={{ minHeight: 160, mb: 2 }}>Live Alerts — Anomalies & SLAs</GlassCard>
   366→            <GlassCard sx={{ minHeight: 160, mb: 2 }}>Today’s Appointments</GlassCard>
   367→            <GlassCard sx={{ minHeight: 160 }}>Late Parts / Escalations</GlassCard>
   368→          </BentoItem>
   369→        </BentoGrid>
   370→      </Container>
   371→    </Box>
   372→  );
   373→};
   374→
   375→// -------------------------------------------------
   376→// 9) src/App.tsx (wiring example)
   377→// Wrap your router/pages with ThemeProvider + CssBaseline
   378→// -------------------------------------------------
   379→import React from 'react';
   380→import { ThemeProvider, CssBaseline } from '@mui/material';
   381→import { modernTheme } from './theme/modernTheme';
   382→import { DashboardPage } from './pages/Dashboard';
   383→
   384→export default function App() {
   385→  // Toggle the className "no-blur" on the root <div> if you implement a
   386→  // settings toggle for reduced motion or low‑power mode.
   387→  return (
   388→    <ThemeProvider theme={modernTheme}>
   389→      <CssBaseline />
   390→      <DashboardPage />
   391→    </ThemeProvider>
   392→  );
   393→}
   394→
   395→// -------------------------------------------------
   396→// 10) Notes & Options
   397→// -------------------------------------------------
   398→// • Draggable/resizable widgets: add @dnd-kit/core + @dnd-kit/sortable or react-grid-layout.
   399→// • Anomaly detection & predictions: surface signals from your API and map into KPI.forecastNote.
   400→// • Accessibility: 
   401→//   - Use prefers-reduced-motion via Framer’s useReducedMotion (already applied in number animation).
   402→//   - Ensure text contrast > 4.5:1 on glass surfaces; tweak alpha if needed.
   403→// • Performance: lazy‑load heavy charts, memoize large datasets, and virtualize long lists.
   404→// • Branding: update theme.custom.gradients and component overrides to match your brand.
   405→

