import React, { useState, useEffect, useRef } from 'react';
import { Button, Box, TextField, Typography, Alert, CircularProgress, Paper, Fade, Zoom, Divider, Chip } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { Lock, Visibility, VisibilityOff, Security, Speed, TrendingUp } from '@mui/icons-material';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();
  const { login } = useAuth();
  const usernameRef = useRef();

  useEffect(() => {
    // Add login-page class to body for CSS fixes
    document.body.classList.add('login-page');

    // Focus username field after component mounts
    const timer = setTimeout(() => {
      if (usernameRef.current) {
        usernameRef.current.focus();
      }
    }, 100);

    return () => {
      clearTimeout(timer);
      // Remove login-page class when component unmounts
      document.body.classList.remove('login-page');
    };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Please enter both username and password');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Simple authentication logic - in real app, this would call your API
      const validCredentials = [
        { user: 'admin', pass: 'admin123', role: 'owner' },
        { user: 'manager', pass: 'manager123', role: 'manager' },
        { user: 'estimator', pass: 'estimator123', role: 'estimator' }
      ];

      const credential = validCredentials.find(
        cred => cred.user === username && cred.pass === password
      );

      if (credential) {
        // Call the login function from AuthContext
        await login({ 
          username: credential.user, 
          role: credential.role,
          firstName: credential.user.charAt(0).toUpperCase() + credential.user.slice(1)
        }, 'dev-token');

        navigate('/dashboard');
      } else {
        throw new Error('Invalid credentials');
      }
    } catch (err) {
      setError(err.message || 'Login failed. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSubmit(e);
    }
  };

  return (
    <Box sx={{
      minHeight: '100vh',
