      if (usernameRef.current) {
        usernameRef.current.focus();
      }
    }, 100);

    return () => {
      clearTimeout(timer);
      // Remove login-page class when component unmounts
      document.body.classList.remove('login-page');
    };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Please enter both username and password');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Simple authentication logic - in real app, this would call your API
      const validCredentials = [
        { user: 'admin', pass: 'admin123', role: 'owner' },
        { user: 'manager', pass: 'manager123', role: 'manager' },
        { user: 'estimator', pass: 'estimator123', role: 'estimator' }
      ];

      const credential = validCredentials.find(
        cred => cred.user === username && cred.pass === password
      );

      if (credential) {
        // Call the login function from AuthContext
        await login({ 
          username: credential.user, 
          role: credential.role,
          firstName: credential.user.charAt(0).toUpperCase() + credential.user.slice(1)
        }, 'dev-token');

        navigate('/dashboard');
      } else {
        throw new Error('Invalid credentials');
      }
    } catch (err) {
      setError(err.message || 'Login failed. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSubmit(e);
    }
  };

