      animation: 'premiumGradientShift 25s ease-in-out infinite',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: { xs: 2, sm: 3, md: 4 },
      position: 'relative',
      width: '100%',
      maxWidth: '100vw',
      overflow: 'hidden',
      zIndex: 1,
      '&::before': {
        content: '""',
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: `
          radial-gradient(circle at 20% 80%, rgba(59, 130, 246, 0.15) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(139, 92, 246, 0.1) 0%, transparent 50%),
          radial-gradient(circle at 40% 40%, rgba(30, 64, 175, 0.08) 0%, transparent 50%)
        `,
        pointerEvents: 'none',
        zIndex: -1
      },
      '@keyframes premiumGradientShift': {
        '0%': { backgroundPosition: '0% 50%, 0% 0%, 0% 0%, 0% 0%' },
        '50%': { backgroundPosition: '100% 50%, 0% 0%, 0% 0%, 0% 0%' },
