/* Global styles for CollisionOS - Automotive Business Management Platform */

@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap');

/* Global Typography and Base Styles */
* {
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Helvetica Neue', Arial, sans-serif;
  font-feature-settings: 'cv02', 'cv03', 'cv04', 'cv11';
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Modern mobile viewport fixes using 2025 best practices */
html {
  /* Use modern viewport units for better mobile browser support */
  height: max(100%, 100svh);
  width: 100%;
  overflow-x: hidden;
}

body {
  margin: 0;
  padding: 0;
  /* Use small viewport height to account for mobile browser UI */
  height: max(100%, 100svh);
  width: 100%;
  overflow-x: hidden;
  position: relative;
}

/* Prevent scroll on login page for mobile - using modern viewport units */
body.login-page {
  overflow: hidden;
  height: max(100vh, 100svh);
  min-height: 100svh;
}

#root {
  height: 100%;
  width: 100%;
  overflow-x: hidden;
  /* Prevent root from intercepting clicks - let children handle them */
  pointer-events: none;
  position: relative;
}

/* Allow specific elements to receive clicks */
#root > * {
  pointer-events: auto;
}

/* Login page specific styling - modern viewport units */
.login-page #root {
  height: max(100vh, 100svh);
  min-height: 100svh;
  overflow: hidden;
  /* Let children handle their own clicks */
  pointer-events: none !important;
  position: relative;
}

/* Re-enable pointer events for direct children */
.login-page #root > * {
  pointer-events: auto !important;
}

/* Universal interactive element fixes - MUI v6 compatible */
/* Ensure all buttons are clickable */
.MuiButton-root, 
.MuiIconButton-root,
.MuiButtonBase-root {
  pointer-events: auto !important;
  position: relative !important;
  z-index: 10 !important;
}

/* Ensure all links are clickable */
a, 
a[role="button"],
.MuiLink-root {
  pointer-events: auto !important;
  position: relative !important;
  z-index: 10 !important;
}

/* Ensure all form elements are interactive */
input,
select,
textarea,
.MuiTextField-root,
.MuiFormControl-root,
.MuiInputBase-root {
  pointer-events: auto !important;
  position: relative !important;
  z-index: 10 !important;
}

/* Ensure navigation elements are clickable */
nav,
.MuiAppBar-root,
.MuiToolbar-root {
  pointer-events: auto !important;
  z-index: 1100 !important;
}

/* Ensure all interactive card elements are clickable */
.MuiCard-root,
.MuiCardActionArea-root {
  pointer-events: auto !important;
  position: relative !important;
  z-index: 5 !important;
}

/* Fix for floating action buttons */
.MuiFab-root {
  pointer-events: auto !important;
  z-index: 1050 !important;
}

/* Performance optimization for interactive elements */
.MuiButton-root,
.MuiCard-root {
  contain: layout style;
  isolation: isolate;
}

/* 2024 PROPER FIX - CSS Injection Order and Stacking Context */
/* Based on MUI v5/v6 best practices */

/* Fix stacking context issues */
.MuiBox-root {
  /* Remove problematic stacking contexts */
  isolation: auto !important;
}

/* Only disable pseudo-elements that create overlays */
.MuiBox-root::after,
.MuiBox-root::before {
  pointer-events: none !important;
}

/* Ensure interactive elements are always accessible */
button,
a,
input,
textarea,
select,
[role="button"],
[tabindex]:not([tabindex="-1"]) {
  pointer-events: auto !important;
  position: relative !important;
  z-index: 1 !important;
}

/* Fix for MUI component click interception */
.MuiButton-root,
.MuiIconButton-root,
.MuiButtonBase-root {
  pointer-events: auto !important;
  z-index: 10 !important;
  /* Force proper positioning to avoid click interception */
  position: relative !important;
  isolation: isolate !important;
}

/* Prevent overlay interference */
[class*="css-"]:empty {
  pointer-events: none !important;
}

/* Force all background and overlay elements to not intercept clicks */
.MuiBox-root[style*="position: fixed"],
.MuiBox-root[style*="position: absolute"],
[class*="css-"][style*="z-index: -"] {
  pointer-events: none !important;
}

/* Improved focus styles for accessibility */
*:focus-visible {
  outline: 2px solid #1e40af;
  outline-offset: 2px;
  border-radius: 4px;
}

/* Only apply Electron-specific fixes when in Electron */
@media screen and (-webkit-app-region: no-drag) {
  /* Electron-specific fixes */
  body {
    -webkit-app-region: no-drag;
    -webkit-user-select: auto;
    user-select: auto;
  }
}

/* Basic interactive element styles - Enhanced for better UX */
button, a, input, select, textarea, [role="button"], [tabindex] {
  cursor: pointer;
  pointer-events: auto;
  user-select: auto;
}

input, textarea {
  cursor: text;
  pointer-events: auto;
  user-select: text;
}

/* Ensure all clickable elements work */
[role="button"], 
.MuiButton-root, 
.MuiIconButton-root,
.MuiListItemButton-root,
.MuiMenuItem-root,
.MuiTab-root,
.MuiChip-root {
  cursor: pointer !important;
  pointer-events: auto !important;
  user-select: none;
}

/* Material-UI specific styles */
.MuiButton-root,
.MuiIconButton-root,
.MuiMenuItem-root,
.MuiListItem-root,
.MuiCard-root,
.MuiChip-root {
  cursor: pointer;
}

/* Fix for dialog and modal interactions */
.MuiDialog-root {
  pointer-events: auto;
}

.MuiDialog-container {
  pointer-events: none;
}

.MuiDialog-paper {
  pointer-events: auto;
}

/* Remove any blocking overlays - Allow clicks on backdrop when appropriate */
.MuiBackdrop-root {
  pointer-events: auto;
}

/* Ensure proper z-index for interactive elements */
.MuiButton-root,
.MuiIconButton-root,
.MuiCard-root {
  position: relative;
  z-index: 1;
}

/* Enhanced hover effects for better UX */
.MuiCard-root {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
  position: relative;
  z-index: 1;
}

.MuiCard-root:hover {
  transform: translateY(-2px);
  box-shadow: 0 20px 25px -5px rgba(30, 64, 175, 0.15), 0 10px 10px -5px rgba(30, 64, 175, 0.08);
}

.MuiButton-root {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  text-transform: none;
  font-weight: 600;
}

.MuiButton-root:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 15px -3px rgba(30, 64, 175, 0.2), 0 4px 6px -2px rgba(30, 64, 175, 0.1);
}

/* Improved spacing and readability */
.MuiTypography-h1, .MuiTypography-h2, .MuiTypography-h3 {
  line-height: 1.2;
  letter-spacing: -0.025em;
}

.MuiTypography-body1, .MuiTypography-body2 {
  line-height: 1.6;
}

/* Professional spacing for automotive business context */
.dashboard-container {
  padding: 24px;
  gap: 24px;
}

.stats-grid {
  gap: 20px;
}

/* Accessibility improvements */
.MuiButton-root:focus,
.MuiIconButton-root:focus,
.MuiTextField-root:focus-within {
  box-shadow: 0 0 0 3px rgba(30, 64, 175, 0.12);
}

