const { chromium } = require('playwright');
const fs = require('fs').promises;
const path = require('path');

async function testResponsiveLogin() {
    const browser = await chromium.launch({ headless: false });

    const viewports = [
        { name: 'mobile', width: 375, height: 667 },
        { name: 'tablet', width: 768, height: 1024 },
        { name: 'desktop-small', width: 1280, height: 720 },
        { name: 'desktop-large', width: 1920, height: 1080 },
        { name: 'desktop-xl', width: 2560, height: 1440 }
    ];

    const testResults = [];

    for (const viewport of viewports) {
        try {
            console.log(`Testing ${viewport.name} (${viewport.width}x${viewport.height})...`);

            const page = await browser.newPage();
            await page.setViewportSize({ width: viewport.width, height: viewport.height });

            // Navigate to localhost:3003 (assuming it's running)
            try {
                await page.goto('http://localhost:3003', { waitUntil: 'networkidle' });
            } catch (error) {
                console.log(`Could not connect to localhost:3003. Testing with file:// protocol...`);
                // If server isn't running, we'll check the code structure instead
                testResults.push({
                    viewport: viewport.name,
                    status: 'server_not_running',
                    message: 'Server not available for visual testing'
                });
                await page.close();
                continue;
            }

            // Wait for the login form to be visible
            await page.waitForSelector('form', { timeout: 10000 });

            // Take a screenshot for visual verification
            const screenshotPath = path.join(__dirname, `responsive-test-${viewport.name}.png`);
            await page.screenshot({ 
                path: screenshotPath, 
                fullPage: true 
            });

            // Check if elements are properly sized and positioned
            const loginContainer = await page.locator('form').first();
            const boundingBox = await loginContainer.boundingBox();

            // Verify the form is properly centered and not squeezed to one side
            const containerWidth = boundingBox.width;
            const containerLeft = boundingBox.x;
            const viewportCenter = viewport.width / 2;
            const containerCenter = containerLeft + (containerWidth / 2);
            const centerOffset = Math.abs(containerCenter - viewportCenter);

            // Check if text is readable (not too small)
            const titleElement = await page.locator('h3, h4, h5').first();
            const titleStyles = await titleElement.evaluate(el => {
                const styles = window.getComputedStyle(el);
                return {
                    fontSize: styles.fontSize,
                    lineHeight: styles.lineHeight
                };
            });

            const result = {
                viewport: viewport.name,
                status: 'success',
                containerWidth,
                centerOffset: Math.round(centerOffset),
                titleFontSize: titleStyles.fontSize,
                screenshot: screenshotPath,
                isCentered: centerOffset < 50, // Allow 50px tolerance
                isResponsive: containerWidth > 300 && containerWidth < viewport.width * 0.9
            };

            testResults.push(result);
            console.log(`âœ… ${viewport.name}: Container width ${containerWidth}px, centered (offset: ${result.centerOffset}px)`);

            await page.close();

        } catch (error) {
            console.error(`âŒ Error testing ${viewport.name}:`, error.message);
            testResults.push({
                viewport: viewport.name,
                status: 'error',
                error: error.message
            });
        }
    }

    await browser.close();

    // Generate summary report
    const report = {
        timestamp: new Date().toISOString(),
        testResults,
        summary: {
            total: viewports.length,
            successful: testResults.filter(r => r.status === 'success').length,
            centered: testResults.filter(r => r.isCentered).length,
            responsive: testResults.filter(r => r.isResponsive).length
        }
    };

    await fs.writeFile(
        path.join(__dirname, 'responsive-test-results.json'), 
        JSON.stringify(report, null, 2)
    );

    console.log('\nðŸ“Š Test Summary:');
    console.log(`Total viewports tested: ${report.summary.total}`);
    console.log(`Successful tests: ${report.summary.successful}`);
    console.log(`Properly centered: ${report.summary.centered}`);
    console.log(`Responsive design: ${report.summary.responsive}`);

    return report;
}

if (require.main === module) {
    testResponsiveLogin().catch(console.error);
}

module.exports = { testResponsiveLogin };

